      .attr("r", 4)
      .attr("fill", colorScale("theme"));

    miniMapContainer.append("text")
      .attr("x", legendX + 8)
      .attr("y", legendY + 4)
      .attr("font-size", "10px")
      .attr("fill", "#4A5568")
      .text("Themes");

    // Feature legend item
    miniMapContainer.append("circle")
      .attr("cx", legendX + 70)
      .attr("cy", legendY)
      .attr("r", 4)
      .attr("fill", colorScale("feature"));

    miniMapContainer.append("text")
      .attr("x", legendX + 78)
      .attr("y", legendY + 4)
      .attr("font-size", "10px")
      .attr("fill", "#4A5568")
