      for (const item of feedbackItems) {
        // Generate an ID if not present
        const feedbackId = item.id || `feedback-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`;

        // Create feedback node
        const feedbackNode = {
          id: feedbackId,
          name: item.text.length > 30 ? item.text.substring(0, 30) + '...' : item.text,
          fullText: item.text,
          type: "feedback",
          timestamp: item.timestamp || new Date().toISOString()
        };

        newGraph.nodes.push(feedbackNode);
        nodeMap.feedback[feedbackId] = feedbackNode;

        // Create or update persona node
        if (!nodeMap.personas[item.user_role]) {
          const personaId = `persona-${Date.now()}-${Object.keys(nodeMap.personas).length}`;
          const personaNode = {
            id: personaId,
            name: item.user_role,
            type: "persona",
