        if (batchAnalysisResult && batchAnalysisResult.themes) {
          for (const theme of batchAnalysisResult.themes) {
            const themeId = `theme-${Date.now()}-${Object.keys(nodeMap.themes).length}`;
            const themeNode = {
              id: themeId,
