        // Step 4: Process classifications to connect feedback to themes
        if (batchAnalysisResult && batchAnalysisResult.classifications) {
          for (const classification of batchAnalysisResult.classifications) {
            const feedbackItem = feedbackItems[classification.feedbackIndex];
