            const feedbackId = feedbackItem.id || `feedback-${Date.now()}-${classification.feedbackIndex}`;
            const feedbackNode = nodeMap.feedback[feedbackId];

            if (!feedbackNode) continue;

            // Add sentiment and priority to feedback node
            feedbackNode.sentiment = classification.sentiment;
            feedbackNode.priority = classification.priority;

            // Connect to primary theme
            if (classification.themes && classification.themes.length > 0) {
              const primaryTheme = classification.themes[0];
              const themeNode = nodeMap.themes[primaryTheme];

              if (themeNode) {
                // Increment theme's feedback count
                themeNode.feedbackCount += 1;

                // Create link between feedback and theme
                newGraph.links.push({
                  source: feedbackId,
