            const targetThemeNode = nodeMap.themes[connection.target];

            if (sourceThemeNode && targetThemeNode) {
              newGraph.links.push({
                source: sourceThemeNode.id,
                target: targetThemeNode.id,
                strength: connection.strength,
                reason: connection.reason
