  const analyzeMultipleFeedback = async (feedbackItems) => {
    try {
      // Create a system message that explains the task
      const systemMessage = `
        You are an AI assistant that specializes in analyzing user feedback for product teams.
        Your task is to analyze multiple feedback items, identify common themes, and classify each feedback.
        You should also identify potential connections between related themes.
      `;

      // Create a user message with the feedback items
      const userMessage = `
        Please analyze these ${feedbackItems.length} user feedback items:

        ${feedbackItems.map((item, i) => `${i+1}. "${item.text}" (from ${item.user_role})`).join('\n')}

        Identify 3-7 common themes across all feedback.
        Classify each feedback item into one or more themes.
        Suggest connections between related themes.

        Respond with a JSON object containing:
        {
          "themes": [
            {"name": "Theme Name", "description": "Brief description of theme"}
          ],
          "classifications": [
            {"feedbackIndex": 0, "themes": ["Primary Theme", "Secondary Theme"], "sentiment": "positive/negative/neutral", "priority": 1-10}
          ],
          "themeConnections": [
            {"source": "Theme A", "target": "Theme B", "strength": 0.1-1.0, "reason": "Why these themes are related"}
          ]
        }
      `;

      // Call the OpenAI API
      const response = await fetch('https://api.openai.com/v1/chat/completions', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${process.env.REACT_APP_OPENAI_API_KEY}`
        },
        body: JSON.stringify({
          model: "gpt-4-turbo",
          messages: [
            { role: "system", content: systemMessage },
            { role: "user", content: userMessage }
          ],
          temperature: 0.7,
          max_tokens: 2000
        })
      });

      if (!response.ok) {
        throw new Error(`OpenAI API error: ${response.status} ${response.statusText}`);
      }

      const data = await response.json();
      const content = data.choices[0].message.content;

      // Extract the JSON object from the response
      try {
        // Find JSON in the response (it might be wrapped in markdown code blocks)
        const jsonMatch = content.match(/```json\n([\s\S]*?)\n```/) || 
                          content.match(/```\n([\s\S]*?)\n```/) || 
                          content.match(/{[\s\S]*?}/);

        const jsonString = jsonMatch ? jsonMatch[1] || jsonMatch[0] : content;
        return JSON.parse(jsonString);
      } catch (parseError) {
        console.error("Error parsing OpenAI response:", parseError);
        console.log("Raw response:", content);
        throw new Error("Failed to parse the AI response");
      }
    } catch (error) {
      console.error("Error calling OpenAI API:", error);
      throw error;
    }
  };

