        setIsAIProcessing(false);
        setLoading(false);
        console.log("Import processing complete");
      }, 100);
    };

    // Start the processing
    processChunks().catch(err => {
      console.error("Error in processChunks:", err);
      setError("Error processing feedback. Please try again.");
      setIsAIProcessing(false);
      setLoading(false);
    });
  }, [dimensions]);

  // Handle node selection and display node details
  const handleNodeClick = useCallback((node) => {
    if (!node) return;

    // Find connected nodes
    const connectedLinks = graphData.links.filter(link => {
      const sourceId = typeof link.source === 'object' ? link.source.id : link.source;
      const targetId = typeof link.target === 'object' ? link.target.id : link.target;
      return sourceId === node.id || targetId === node.id;
    });

    const connectedNodeIds = connectedLinks.map(link => {
      const sourceId = typeof link.source === 'object' ? link.source.id : link.source;
      const targetId = typeof link.target === 'object' ? link.target.id : link.target;
      return sourceId === node.id ? targetId : sourceId;
    });

    const connectedNodes = graphData.nodes.filter(n => connectedNodeIds.includes(n.id));

    // Prepare insights
    let insights = {
      results: [],
      nodeType: node.type,
      nodeId: node.id
    };

    if (node.type === "theme") {
      // Theme view
      insights.theme = node.name;
      insights.feedbackCount = node.feedbackCount || 0;

      // Connected features
      const connectedFeatures = connectedNodes.filter(n => n.type === "feature");
      if (connectedFeatures.length > 0) {
        insights.connectedFeatures = connectedFeatures;
      }

      // Connected personas
      const connectedPersonas = connectedNodes.filter(n => n.type === "persona");
      if (connectedPersonas.length > 0) {
        insights.connectedPersonas = connectedPersonas;
      }

      // Find feedback related to this theme
      if (feedbackItems.length > 0) {
        // For themes, we need to find feedback that might be related
        // This is a simple string matching approach, but could be enhanced
        const relatedFeedback = feedbackItems.filter(item => 
          item.text.toLowerCase().includes(node.name.toLowerCase())
        );

        if (relatedFeedback.length > 0) {
          insights.relatedFeedback = relatedFeedback.map((item) => ({
            text: item.text,
            user_role: item.user_role,
            timestamp: item.timestamp
          }));
        }
      }
    } else if (node.type === "feature") {
      // Feature view
      insights.isFeatureView = true;
      insights.name = node.name;
      insights.priority = node.priority || "Medium";
      insights.description = node.description || "";

      // Connected themes
      const connectedThemes = connectedNodes.filter(n => n.type === "theme");
      if (connectedThemes.length > 0) {
        insights.connectedThemes = connectedThemes;
      }

      // Connected personas
      const connectedPersonas = connectedNodes.filter(n => n.type === "persona");
      if (connectedPersonas.length > 0) {
        insights.connectedPersonas = connectedPersonas;
      }
    } else if (node.type === "persona") {
      // Persona view
      insights.persona = node.name;
      insights.feedbackCount = node.feedbackCount || 0;

      // Connected themes
      const connectedThemes = connectedNodes.filter(n => n.type === "theme");
      if (connectedThemes.length > 0) {
        insights.connectedThemes = connectedThemes;
      }

      // Connected features
      const connectedFeatures = connectedNodes.filter(n => n.type === "feature");
      if (connectedFeatures.length > 0) {
        insights.connectedFeatures = connectedFeatures;
      }

      // Find feedback from this persona
      if (feedbackItems.length > 0) {
        const personaFeedback = feedbackItems.filter(item => 
          item.user_role === node.name
        );

        if (personaFeedback.length > 0) {
          insights.results = personaFeedback.map((item) => ({
            text: item.text,
            user_role: item.user_role,
            timestamp: item.timestamp || new Date().toISOString()
          }));
        }
      }
    } else if (node.type === "feedback") {
      // Feedback view
      insights.isFeedbackView = true;

      // Get the full text - check all possible properties where text might be stored
      insights.feedbackText = node.fullText || node.originalText || node.completeText || 
                             node.text || node.content || node.feedback || node.description || 
                             node.title || node.name || "";

      console.log("Initial feedback text:", insights.feedbackText);

      // If we have access to feedbackItems array, try to find a matching item with full text
      if (feedbackItems && feedbackItems.length > 0) {
        // Try to find a matching feedback item by id or text
        const matchingItem = feedbackItems.find(item => 
          (item.id === node.id) || 
          (item.text && node.text && item.text.includes(node.text)) ||
          (item.text && node.name && item.text.includes(node.name)) ||
          // Also try matching in reverse - node text might be a substring of item text
          (node.text && item.text && node.text.includes(item.text)) ||
          (node.name && item.text && node.name.includes(item.text))
        );

        if (matchingItem && matchingItem.text) {
          // Use the full text from the matching item if it's longer
          if (matchingItem.text.length > insights.feedbackText.length) {
            console.log("Found longer matching text in feedbackItems");
            insights.feedbackText = matchingItem.text;
          }

          // Also update user role and timestamp if available
          if (matchingItem.user_role) {
            insights.user_role = matchingItem.user_role;
          }
          if (matchingItem.timestamp) {
            insights.timestamp = matchingItem.timestamp;
          }
        }
      }

      console.log("Final feedback text length:", insights.feedbackText.length);

      // Always prefer the node's user_role if available
      insights.user_role = node.user_role || node.persona || node.role || insights.user_role || "Unknown User";
      insights.timestamp = node.timestamp || node.date || node.created_at || new Date().toISOString();

      // Get related themes if available
      if (node.themes) {
        insights.relatedThemes = node.themes;
      } else {
        // Try to find related themes from connections
        const relatedThemes = connectedNodes
          .filter(n => n.type === "theme")
          .map(n => n.name);

        if (relatedThemes.length > 0) {
          insights.relatedThemes = relatedThemes;
        }
      }

      // Check if node already has sentiment
      if (node.sentiment) {
        insights.sentiment = node.sentiment;
        setAIInsights(insights);
        setShowAIInsightsPanel(true);
      } else {
        // If no sentiment, analyze it with OpenAI
        setIsAIProcessing(true);

        // Get the text to analyze - ensure it's not empty
        const textToAnalyze = insights.feedbackText;

        if (textToAnalyze && textToAnalyze.length > 0) {
          console.log("Analyzing sentiment for text:", textToAnalyze);

          // Call OpenAI service to analyze sentiment
          analyzeSentiment(textToAnalyze)
            .then(result => {
              console.log("Sentiment analysis result:", result);

              // Update insights with sentiment
              insights.sentiment = result.sentiment;

              // Update the node with sentiment for future reference
              setGraphData(prevData => ({
                ...prevData,
                nodes: prevData.nodes.map(n => 
                  n.id === node.id ? { ...n, sentiment: result.sentiment } : n
                )
              }));

              // Update analytics data
              if (analyticsData) {
                const updatedCounts = { 
                  ...(analyticsData.sentimentDistribution || {}),
                  [result.sentiment]: ((analyticsData.sentimentDistribution || {})[result.sentiment] || 0) + 1
                };

                setAnalyticsData({
                  ...analyticsData,
                  sentimentDistribution: updatedCounts
                });
              }

              // Show insights panel
              setAIInsights(insights);
              setShowAIInsightsPanel(true);
              setIsAIProcessing(false);
            })
            .catch(error => {
              console.error("Error analyzing sentiment:", error);
              insights.sentiment = "Neutral"; // Fallback
              setAIInsights(insights);
              setShowAIInsightsPanel(true);
              setIsAIProcessing(false);
            });
        } else {
          // No text to analyze
          console.warn("No feedback text to analyze for sentiment");
          insights.sentiment = "Neutral";
          setAIInsights(insights);
          setShowAIInsightsPanel(true);
          setIsAIProcessing(false);
        }
      }

      return; // Exit early since we're handling async operations
    }

    // For non-feedback nodes, just set the insights directly
    setAIInsights(insights);
    setShowAIInsightsPanel(true);

    // Ensure mini-map is recreated if it should be visible
    if (showMiniMap) {
      setTimeout(() => {
        if (svgRef.current) {
          d3.select(svgRef.current).selectAll(".mini-map-container").remove();
          createMiniMap();
        }
      }, 0);
    }
  }, [graphData, feedbackItems, showMiniMap, createMiniMap, analyticsData]);

  // Zoom control functions
  const zoomIn = useCallback(() => {
    if (!svgRef.current || !zoomRef.current) return;

    const svg = d3.select(svgRef.current);
    const currentTransform = d3.zoomTransform(svg.node());

    svg.transition()
      .duration(300)
      .call(
        zoomRef.current.transform,
        d3.zoomIdentity
          .translate(currentTransform.x, currentTransform.y)
          .scale(currentTransform.k * 1.3)
      );
  }, []);

  const zoomOut = useCallback(() => {
    if (!svgRef.current || !zoomRef.current) return;

    const svg = d3.select(svgRef.current);
    const currentTransform = d3.zoomTransform(svg.node());

    svg.transition()
      .duration(300)
      .call(
        zoomRef.current.transform,
        d3.zoomIdentity
          .translate(currentTransform.x, currentTransform.y)
          .scale(currentTransform.k / 1.3)
      );
  }, []);

  const centerView = useCallback(() => {
    if (!svgRef.current || !zoomRef.current) return;

    const svg = d3.select(svgRef.current);

    // Calculate the center point
    const centerX = dimensions.width / 2;
    const centerY = dimensions.height / 2;

    // Create a transform that centers the view
    const transform = d3.zoomIdentity
      .translate(centerX, centerY)
      .scale(1)
      .translate(-centerX, -centerY);

    // Apply the transform with a smooth transition
    svg.transition()
      .duration(750)
      .call(zoomRef.current.transform, transform);
  }, [dimensions]);

  const fitToScreen = useCallback(() => {
    if (!svgRef.current || !zoomRef.current || !graphData.nodes.length) return;

    const svg = d3.select(svgRef.current);

    // Find the bounds of all nodes
    let minX = Infinity, minY = Infinity, maxX = -Infinity, maxY = -Infinity;

    graphData.nodes.forEach(node => {
      if (!node.x || !node.y) return;

      if (node.x < minX) minX = node.x;
      if (node.y < minY) minY = node.y;
      if (node.x > maxX) maxX = node.x;
      if (node.y > maxY) maxY = node.y;
    });

    // If we don't have valid bounds, center view instead
    if (!isFinite(minX) || !isFinite(minY) || !isFinite(maxX) || !isFinite(maxY)) {
      centerView();
      return;
    }

    // Add padding
    const padding = 50;
    minX -= padding;
    minY -= padding;
    maxX += padding;
    maxY += padding;

    // Calculate width and height of the content
    const contentWidth = maxX - minX;
    const contentHeight = maxY - minY;

    // Calculate scale to fit the content
    const scale = Math.min(
      dimensions.width / contentWidth,
      dimensions.height / contentHeight,
      2 // Maximum scale factor
    ) * 0.9; // 90% to add some margin

    // Calculate the center of the content
    const centerX = (minX + maxX) / 2;
    const centerY = (minY + maxY) / 2;

    // Create a transform that centers and scales the content
    const transform = d3.zoomIdentity
      .translate(dimensions.width / 2, dimensions.height / 2)
      .scale(scale)
      .translate(-centerX, -centerY);

    // Apply the transform with a smooth transition
    svg.transition()
      .duration(750)
      .call(zoomRef.current.transform, transform);
  }, [dimensions, graphData.nodes, centerView]);

  // Toggle mini-map visibility
  const toggleMiniMap = useCallback(() => {
    setShowMiniMap(!showMiniMap);
  }, [showMiniMap]);

  const handleDataImported = (importedData) => {
    if (importedData) {
      setGraphData({ 
        nodes: importedData.nodes || [], 
        links: importedData.links || [] 
      });
      setInsights(importedData.insights || []);
    }
  };

  // Add these functions back
  const exportData = useCallback(() => {
    // Prepare complete export with all AI data
    const completeExportData = {
      // Include the graph structure
      nodes: graphData.nodes.map(node => ({
        ...node,
        // If there are any D3-specific properties that shouldn't be exported, filter them out here
        fx: undefined,
        fy: undefined,
        index: undefined,
        vx: undefined,
        vy: undefined,
        x: undefined,
        y: undefined
      })),
      links: graphData.links.map(link => {
        // Ensure links use id strings rather than object references
        const sourceId = typeof link.source === 'object' ? link.source.id : link.source;
        const targetId = typeof link.target === 'object' ? link.target.id : link.target;

        return {
          source: sourceId,
          target: targetId,
          strength: link.strength || 0.5
        };
      }),

      // Include all AI-generated insights
      insights: insights || [],

      // Include analytics data with sentiment distribution
      analytics: {
        ...analyticsData,
        // Ensure sentiment distribution is included
        sentimentDistribution: analyticsData?.sentimentDistribution || {
          Positive: graphData.nodes.filter(n => n.type === 'feedback' && n.sentiment === 'Positive').length,
          Negative: graphData.nodes.filter(n => n.type === 'feedback' && n.sentiment === 'Negative').length,
          Neutral: graphData.nodes.filter(n => n.type === 'feedback' && n.sentiment === 'Neutral').length
        },
        exportDate: new Date().toISOString(),
        exportVersion: "1.0"
      },

      // Include raw feedback data if available
      feedbackItems: feedbackItems || []
    };

    // Convert to JSON string with nice formatting
    const dataStr = JSON.stringify(completeExportData, null, 2);
    const dataUri = 'data:application/json;charset=utf-8,'+ encodeURIComponent(dataStr);

    const exportFileDefaultName = 'feedback-insights-data.json';

    const linkElement = document.createElement('a');
    linkElement.setAttribute('href', dataUri);
    linkElement.setAttribute('download', exportFileDefaultName);
    linkElement.click();

    console.log("Exported data with AI insights and sentiment analysis");
  }, [graphData, insights, analyticsData, feedbackItems]);

  // Reset to initial data
  const resetVisualization = useCallback(() => {
    if (window.confirm("Are you sure you want to reset? This will remove all custom data.")) {
      setGraphData(initialData);
      setSelectedNode(null);
      setHighlightMode('none');
    }
  }, []);

  // Loading screen
  if (loading) {
    return (
      <div className="flex flex-col h-full items-center justify-center bg-gray-50">
        <div className="text-2xl text-blue-800 mb-4">Loading Insights Network...</div>
        <div className="w-16 h-16 border-4 border-blue-600 border-t-transparent rounded-full animate-spin"></div>
      </div>
    );
  }

  // Error screen
  if (error) {
    return (
      <div className="flex flex-col h-full items-center justify-center bg-gray-50">
        <div className="text-2xl text-red-800 mb-4">Error Loading Data</div>
        <div className="text-gray-600">{error}</div>
        <button 
          className="mt-4 px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
          onClick={() => window.location.reload()}
        >
          Retry
        </button>
      </div>
    );
  }

  return (
    <div className="flex flex-col h-full">
      {/* Header and controls */}
      <div className="bg-white p-4 shadow-md">
        <div className="flex justify-between items-center mb-4">
          <div>
            <h1 className="text-2xl font-bold text-blue-800">User Research Insights Network</h1>
            <p className="text-gray-600">Visualizing connections between user feedback, features, and personas</p>
          </div>

          {/* Search bar */}
          <div className="relative w-64">
            <input
              type="text"
              placeholder="Search nodes..."
              value={searchTerm}
              onChange={(e) => {
                setSearchTerm(e.target.value);
                if (e.target.value.length >= 2) {
                  const results = graphData.nodes.filter(node => 
                    node.name.toLowerCase().includes(e.target.value.toLowerCase())
                  );
                  setSearchResults(results);
                  setShowSearchResults(true);
                } else {
                  setShowSearchResults(false);
                  setSearchResults([]);
                }
              }}
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
            {showSearchResults && searchResults.length > 0 && (
              <div className="absolute top-full left-0 right-0 mt-1 bg-white border border-gray-300 rounded-md shadow-lg z-20 max-h-60 overflow-y-auto">
                {searchResults.map((result, index) => (
                  <div 
                    key={index}
                    className="px-3 py-2 hover:bg-gray-100 cursor-pointer"
                    onClick={() => {
                      setSelectedNode(result);
                      handleNodeClick(result);
                      setShowSearchResults(false);
                    }}
                  >
                    <div className="flex items-center">
                      <div 
                        className="w-3 h-3 rounded-full mr-2"
                        style={{ backgroundColor: colorScale(result.type) }}
                      ></div>
                      <span>{result.name}</span>
                      <span className="ml-2 text-xs text-gray-500">({result.type})</span>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>

        {/* Controls - Simplified */}
        <div className="flex flex-wrap gap-2 mb-4">
          <div className="flex items-center space-x-2">
            <span className="text-sm text-gray-600">Data:</span>
            <button 
              className="px-3 py-1 bg-blue-600 text-white rounded hover:bg-blue-700 text-sm"
              onClick={exportData}
            >
              Export
            </button>
            <label className="px-3 py-1 bg-blue-600 text-white rounded hover:bg-blue-700 text-sm cursor-pointer">
              Import
              <input 
                type="file" 
                accept=".json" 
                className="hidden" 
                onChange={importData}
              />
            </label>
            <button 
              className="px-3 py-1 bg-red-600 text-white rounded hover:bg-red-700 text-sm"
              onClick={resetVisualization}
            >
              Reset
            </button>
          </div>

          <div className="flex items-center space-x-2">
            <span className="text-sm text-gray-600">View:</span>
            <button 
              className="px-3 py-1 bg-purple-600 text-white rounded hover:bg-purple-700 text-sm"
              onClick={centerView}
            >
              Center View
            </button>
            <button 
              className="px-3 py-1 bg-purple-600 text-white rounded hover:bg-purple-700 text-sm"
              onClick={toggleMiniMap}
            >
              {showMiniMap ? 'Hide Mini-Map' : 'Show Mini-Map'}
            </button>
            <button 
              className="px-3 py-1 bg-blue-600 text-white rounded hover:bg-blue-700 text-sm"
              onClick={fitToScreen}
              title="Fit all nodes to screen"
            >
              Fit to Screen
            </button>
          </div>

          {/* Removed the "Add" section with buttons for Theme, Feature, and Feedback */}

          <div className="flex items-center space-x-2">
            <span className="text-sm text-gray-600">Highlight:</span>
            <button 
              className={`px-2 py-1 rounded text-xs ${highlightMode === 'none' ? 'bg-blue-600 text-white' : 'bg-gray-200 text-gray-700'}`}
              onClick={() => setHighlightMode('none')}
            >
              None
            </button>
            <button 
              className={`px-2 py-1 rounded text-xs ${highlightMode === 'themes' ? 'bg-blue-600 text-white' : 'bg-gray-200 text-gray-700'}`}
              onClick={() => setHighlightMode('themes')}
            >
              Themes
            </button>
            <button 
              className={`px-2 py-1 rounded text-xs ${highlightMode === 'feedback' ? 'bg-blue-600 text-white' : 'bg-gray-200 text-gray-700'}`}
              onClick={() => setHighlightMode('feedback')}
            >
              Feedback
            </button>
            <button 
              className={`px-2 py-1 rounded text-xs ${highlightMode === 'personas' ? 'bg-blue-600 text-white' : 'bg-gray-200 text-gray-700'}`}
              onClick={() => setHighlightMode('personas')}
            >
              Personas
            </button>
          </div>
        </div>

        {/* Removed the second row with Connect Nodes and Feedback Weight controls */}
      </div>

      {/* Main visualization container */}
      <div className="flex flex-1 relative">
        <div 
          ref={containerRef} 
          className="flex-1 bg-gray-50 p-4 transition-all duration-300"
          style={{
            marginRight: showAIInsightsPanel && !isPanelCollapsed ? `${panelWidth}px` : '0',
            minHeight: '500px'
          }}
        >
          <svg 
            ref={svgRef} 
            width="100%" 
            height="100%"
            className="border border-gray-200 rounded-lg bg-white shadow-inner w-full h-full"
            style={{ minHeight: '500px' }}
            onClick={() => setSelectedNode(null)}
          />

          {/* Add Legend */}
          <Legend 
            colorScale={colorScale} 
            showSentiment={true} 
            sentimentColors={{
              positive: colorScale("feedback-positive"),
              neutral: colorScale("feedback-neutral"),
              negative: colorScale("feedback-negative")
            }}
          />

          {/* Zoom controls */}
          <div className="absolute bottom-8 right-8 flex flex-col bg-white rounded-lg shadow p-2 space-y-2 z-10">
            <button 
              className="w-8 h-8 flex items-center justify-center bg-gray-100 hover:bg-gray-200 rounded-full"
              onClick={zoomIn}
            >
              <span className="text-xl">+</span>
            </button>
            <button 
              className="w-8 h-8 flex items-center justify-center bg-gray-100 hover:bg-gray-200 rounded-full"
              onClick={zoomOut}
            >
              <span className="text-xl">-</span>
            </button>
            <button 
              className="w-8 h-8 flex items-center justify-center bg-gray-100 hover:bg-gray-200 rounded-full"
              onClick={fitToScreen}
            >
              <span className="text-xl">⊡</span>
            </button>
          </div>
        </div>

        {/* AI Insights Panel */}
        {showAIInsightsPanel && aiInsights && (
          <div 
            className={`fixed right-0 top-0 bottom-0 bg-white shadow-lg z-40 transition-all duration-300 flex`}
            style={{ 
              width: isPanelCollapsed ? '50px' : `${panelWidth}px`,
              maxWidth: '40%'
            }}
          >
            {/* Collapse/Expand Button */}
            <div className="w-10 flex flex-col items-center pt-4 border-r border-gray-200 bg-gray-50 shrink-0">
              <button 
                onClick={() => setIsPanelCollapsed(!isPanelCollapsed)}
                className="text-gray-500 hover:text-gray-700"
              >
                {isPanelCollapsed ? (
                  <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 5l7 7-7 7M5 5l7 7-7 7" />
                  </svg>
                ) : (
                  <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M11 19l-7-7 7-7m8 14l-7-7 7-7" />
                  </svg>
                )}
              </button>
            </div>

            {/* Panel Content with improved scrolling */}
            {!isPanelCollapsed && (
              <div className="flex-1 flex flex-col overflow-hidden">
                {/* Fixed header section */}
                <div className="flex items-center justify-between p-4 border-b border-gray-200 bg-white z-30 shrink-0">
                  <h2 className="text-xl font-semibold text-gray-800">
                    AI Feedback Analysis
                  </h2>
                  <button 
                    onClick={() => setShowAIInsightsPanel(false)}
                    className="text-gray-500 hover:text-gray-700"
                  >
                    <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" viewBox="0 0 20 20" fill="currentColor">
                      <path fillRule="evenodd" d="M4.293 4.293a1 1 0 011.414 0L10 8.586l4.293-4.293a1 1 0 111.414 1.414L11.414 10l4.293 4.293a1 1 0 01-1.414 1.414L10 11.414l-4.293 4.293a1 1 0 01-1.414-1.414L8.586 10 4.293 5.707a1 1 0 010-1.414z" clipRule="evenodd" />
                    </svg>
                  </button>
                </div>

                {/* Scrollable content section */}
                <div className="flex-1 overflow-y-auto p-4 pb-20">
                  {/* Loading indicator */}
                  {isAIProcessing && (
                    <div className="flex justify-center items-center h-full">
                      <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-500"></div>
                    </div>
                  )}

                  {/* Error message */}
                  {aiError && (
                    <div className="bg-red-50 p-4 rounded-lg border border-red-200 mb-4">
                      <h3 className="text-lg font-semibold text-red-800 mb-2">Error</h3>
                      <p className="text-red-700">{aiError}</p>
                    </div>
                  )}

                  {/* AI Insights Content */}
                  {!isAIProcessing && !aiError && (
                    <div className="space-y-6">
                      {/* Sentiment Overview Section with proper spacing */}
                      {analyticsData && analyticsData.sentimentDistribution && (
                        <div className="mb-6 pb-6 border-b border-gray-200">
                          <h3 className="text-lg font-semibold text-gray-700 mb-4">Sentiment Overview</h3>
                          <div className="bg-gray-50 p-4 rounded-lg shadow-sm">
                            <div className="flex items-center justify-around mb-2">
                              <div className="text-center">
                                <div className="text-green-500 text-2xl font-bold">
                                  {analyticsData.sentimentDistribution?.Positive || 0}
                                </div>
                                <div className="text-xs text-gray-500">Positive</div>
                              </div>
                              <div className="text-center">
                                <div className="text-red-500 text-2xl font-bold">
                                  {analyticsData.sentimentDistribution?.Negative || 0}
                                </div>
                                <div className="text-xs text-gray-500">Negative</div>
                              </div>
                              <div className="text-center">
                                <div className="text-gray-500 text-2xl font-bold">
                                  {analyticsData.sentimentDistribution?.Neutral || 0}
                                </div>
                                <div className="text-xs text-gray-500">Neutral</div>
                              </div>
                            </div>
                          </div>
                        </div>
                      )}

                      {/* Feedback Node Details with proper spacing */}
                      {aiInsights.isFeedbackView && (
                        <div className="mb-6">
                          <div className="flex items-center mb-4">
                            <div className="w-4 h-4 rounded-full mr-2" style={{ backgroundColor: colorScale('feedback') }}></div>
                            <h3 className="text-lg font-semibold text-gray-700">Feedback Details</h3>
                          </div>

                          <div className="bg-gray-50 p-4 rounded-lg border border-gray-200 mb-4">
                            {/* Complete feedback text with better formatting */}
                            <div className="text-gray-800 mb-4 bg-white p-3 rounded border border-gray-200 max-h-80 overflow-y-auto">
                              <p className="whitespace-pre-wrap text-base leading-relaxed">"{aiInsights.feedbackText}"</p>

                              {/* Debug info for development */}
                              <div className="mt-3 pt-3 border-t border-gray-200 text-xs text-gray-500">
                                <div>Content length: {aiInsights.feedbackText?.length || 0} characters</div>
                              </div>
                            </div>

                            <div className="flex justify-between items-center">
                              <div className="flex items-center">
                                <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4 text-gray-500 mr-1" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z" />
                                </svg>
                                <span className="text-sm text-gray-600 font-medium">{aiInsights.user_role || 'Unknown User'}</span>
                              </div>

                              <div className="text-xs text-gray-500">
                                {aiInsights.timestamp ? new Date(aiInsights.timestamp).toLocaleString() : 'No timestamp available'}
                              </div>
                            </div>
                          </div>

                          {/* Sentiment Analysis - If available */}
                          {aiInsights.sentiment && (
                            <div className="mt-4 mb-6">
                              <div className="text-sm text-gray-500 mb-2">Sentiment Analysis</div>
                              <div className={`px-3 py-2 rounded-full inline-block font-medium text-sm ${
                                aiInsights.sentiment === 'Positive' ? 'bg-green-100 text-green-800' : 
                                aiInsights.sentiment === 'Negative' ? 'bg-red-100 text-red-800' : 
                                'bg-gray-100 text-gray-800'
                              }`}>
                                {aiInsights.sentiment}
                              </div>
                            </div>
                          )}

                          {/* Related Themes - If any */}
                          {aiInsights.relatedThemes && aiInsights.relatedThemes.length > 0 && (
                            <div className="mt-4 mb-6">
                              <div className="text-sm text-gray-500 mb-2">Related Themes</div>
                              <div className="flex flex-wrap gap-2">
                                {aiInsights.relatedThemes.map((theme, idx) => (
                                  <div key={idx} className="bg-blue-50 px-3 py-1 rounded-full text-sm">
                                    {theme}
                                  </div>
                                ))}
                              </div>
                            </div>
                          )}
                        </div>
                      )}

                      {/* Key Insights Section */}
                      {aiInsights.insights && aiInsights.insights.length > 0 && (
                        <div className="mb-8">
                          <h3 className="text-lg font-semibold text-gray-700 mb-3">Key Insights</h3>
                          <div className="space-y-4">
                            {aiInsights.insights.map(insight => (
                              <div key={insight.id || Math.random()} className="bg-blue-50 p-4 rounded-lg shadow-sm border-l-4 border-blue-500">
                                <h4 className="font-medium text-gray-800 mb-1">{insight.title}</h4>
                                <p className="text-gray-700 text-sm">{insight.description}</p>
                              </div>
                            ))}
                          </div>
                        </div>
                      )}
                    </div>
                  )}
                </div>
              </div>
            )}
          </div>
        )}
      </div>

      {/* Keep the AI Insights button */}
      {insights.length > 0 && !showAIInsightsPanel && (
        <button
          onClick={() => {
            setAIInsights({ results: [], insights: insights });
            setShowAIInsightsPanel(true);
          }}
          className="fixed bottom-8 left-8 bg-blue-600 text-white px-4 py-2 rounded-full shadow-lg hover:bg-blue-700"
        >
          View AI Insights ({insights.length})
        </button>
      )}
    </div>
  );
};

export default UserResearchNetwork;
