import React, { useState, useEffect, useRef } from 'react';
import * as d3 from 'd3';

const UserResearchNetwork = () => {
  const svgRef = useRef(null);
  const containerRef = useRef(null);
  const [dimensions, setDimensions] = useState({ width: 800, height: 600 });
  const [isSimulationRunning, setIsSimulationRunning] = useState(true);
  const [selectedNode, setSelectedNode] = useState(null);
  const [highlightMode, setHighlightMode] = useState('none');
  const [feedbackWeight, setFeedbackWeight] = useState(5);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [modalType, setModalType] = useState(''); // 'theme' or 'feature'
  const [newNodeName, setNewNodeName] = useState('');
  const [newNodeFeedbackCount, setNewNodeFeedbackCount] = useState(10);
  const [newNodePriority, setNewNodePriority] = useState('Medium');
  const [showConnectionModal, setShowConnectionModal] = useState(false);
  const [connectionSource, setConnectionSource] = useState(null);
  const [connectionTarget, setConnectionTarget] = useState('');
  const [connectionStrength, setConnectionStrength] = useState(0.5);
  const [availableTargets, setAvailableTargets] = useState([]);
  const [multipleConnections, setMultipleConnections] = useState([]);

  // Initial data structure
  const initialData = {
    nodes: [
      // Feedback themes (blue)
      { id: "theme1", name: "Slow Performance", type: "theme", feedbackCount: 37 },
      { id: "theme2", name: "Confusing Navigation", type: "theme", feedbackCount: 42 },
      { id: "theme3", name: "Missing Features", type: "theme", feedbackCount: 28 },
      { id: "theme4", name: "Sync Issues", type: "theme", feedbackCount: 15 },
      { id: "theme5", name: "Cluttered Interface", type: "theme", feedbackCount: 31 },
      { id: "theme6", name: "Mobile Experience", type: "theme", feedbackCount: 25 },
      { id: "theme7", name: "Data Visualization", type: "theme", feedbackCount: 19 },

      // Feature ideas (green)
      { id: "feature1", name: "Performance Optimization", type: "feature", priority: "High" },
      { id: "feature2", name: "Navigation Redesign", type: "feature", priority: "High" },
      { id: "feature3", name: "Bulk Actions", type: "feature", priority: "Medium" },
      { id: "feature4", name: "Offline Mode", type: "feature", priority: "Medium" },
      { id: "feature5", name: "Interface Simplification", type: "feature", priority: "High" },
      { id: "feature6", name: "Mobile App", type: "feature", priority: "High" },
      { id: "feature7", name: "Advanced Charts", type: "feature", priority: "Medium" },

      // User personas (orange)
      { id: "persona1", name: "Power User", type: "persona" },
      { id: "persona2", name: "Casual User", type: "persona" },
      { id: "persona3", name: "Admin", type: "persona" },
      { id: "persona4", name: "Mobile User", type: "persona" }
    ],
    links: [
      // Theme to Feature connections
      { source: "theme1", target: "feature1", strength: 0.8 },
      { source: "theme1", target: "feature4", strength: 0.4 },
      { source: "theme2", target: "feature2", strength: 0.9 },
      { source: "theme2", target: "feature5", strength: 0.5 },
      { source: "theme3", target: "feature3", strength: 0.6 },
      { source: "theme3", target: "feature7", strength: 0.5 },
      { source: "theme4", target: "feature4", strength: 0.8 },
      { source: "theme5", target: "feature5", strength: 0.9 },
      { source: "theme6", target: "feature6", strength: 0.9 },
      { source: "theme7", target: "feature7", strength: 0.8 },

      // Persona connections to themes
      { source: "persona1", target: "theme1", strength: 0.7 },
      { source: "persona1", target: "theme3", strength: 0.8 },
      { source: "persona1", target: "theme7", strength: 0.9 },
      { source: "persona2", target: "theme2", strength: 0.8 },
      { source: "persona2", target: "theme5", strength: 0.7 },
      { source: "persona3", target: "theme1", strength: 0.6 },
      { source: "persona3", target: "theme4", strength: 0.9 },
      { source: "persona4", target: "theme6", strength: 0.9 },

      // Persona connections to features
      { source: "persona1", target: "feature1", strength: 0.8 },
      { source: "persona1", target: "feature3", strength: 0.7 },
      { source: "persona1", target: "feature7", strength: 0.9 },
      { source: "persona2", target: "feature2", strength: 0.7 },
      { source: "persona2", target: "feature5", strength: 0.8 },
      { source: "persona3", target: "feature1", strength: 0.6 },
      { source: "persona3", target: "feature4", strength: 0.8 },
      { source: "persona4", target: "feature6", strength: 0.9 }
    ]
  };

  const [graphData, setGraphData] = useState(initialData);

  useEffect(() => {
    // Simulate loading data
    setLoading(true);
    setTimeout(() => {
      setLoading(false);
    }, 1000);
  }, []);

  const exportData = () => {
    const dataStr = JSON.stringify(graphData, null, 2);
    const dataUri = 'data:application/json;charset=utf-8,'+ encodeURIComponent(dataStr);

    const exportFileDefaultName = 'feedback-insights-data.json';

    const linkElement = document.createElement('a');
    linkElement.setAttribute('href', dataUri);
    linkElement.setAttribute('download', exportFileDefaultName);
    linkElement.click();
  };

  const importData = (event) => {
    const file = event.target.files[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onload = (e) => {
      try {
        const importedData = JSON.parse(e.target.result);
        // Validate the data structure
        if (importedData.nodes && importedData.links) {
          setGraphData(importedData);
        } else {
          setError("Invalid data format. The file must contain 'nodes' and 'links' arrays.");
        }
      } catch (err) {
        setError("Error parsing the file. Please make sure it's a valid JSON file.");
      }
    };
    reader.readAsText(file);
  };

  useEffect(() => {
    if (!svgRef.current || !dimensions.width) return;

    const svg = d3.select(svgRef.current);
    svg.selectAll("*").remove();

    const g = svg.append("g");

    // Create a zoom behavior
    const zoom = d3.zoom()
      .scaleExtent([0.1, 4])
      .on("zoom", (event) => {
        g.attr("transform", event.transform);
      });

    svg.call(zoom);

    // Color scale for node types
    const colorScale = d3.scaleOrdinal()
      .domain(["theme", "feature", "persona"])
      .range(["#3182CE", "#38A169", "#DD6B20"]);

    // Create a force simulation
    const simulation = d3.forceSimulation(graphData.nodes)
      .force("link", d3.forceLink(graphData.links)
        .id(d => d.id)
        .distance(d => 150 / (d.strength || 0.5))
      )
      .force("charge", d3.forceManyBody().strength(-150))
      .force("center", d3.forceCenter(dimensions.width / 2, dimensions.height / 2))
      .force("collide", d3.forceCollide().radius(20));

    // Create links
    const link = g.append("g")
      .selectAll("line")
      .data(graphData.links)
      .enter()
      .append("line")
      .attr("stroke-width", d => d.strength * 5)
      .attr("stroke", d => {
        const sourceNode = graphData.nodes.find(n => n.id === (d.source.id || d.source));
        const targetNode = graphData.nodes.find(n => n.id === (d.target.id || d.target));

        if (!sourceNode || !targetNode) return "#999";

        if (sourceNode.type === "theme" && targetNode.type === "feature") {
          return "#6366F1";
        } else if (sourceNode.type === "feature" && targetNode.type === "theme") {
          return "#6366F1";
        } else if ((sourceNode.type === "persona" && targetNode.type === "theme") ||
                   (sourceNode.type === "theme" && targetNode.type === "persona")) {
          return "#F59E0B";
        } else if ((sourceNode.type === "persona" && targetNode.type === "feature") ||
                   (sourceNode.type === "feature" && targetNode.type === "persona")) {
          return "#10B981";
        }

        return "#999";
      })
      .attr("stroke-opacity", 0.7)
      .attr("stroke-dasharray", d => d.isNew ? "5,5" : "none");

    // Create node groups
    const nodeGroup = g.append("g")
      .selectAll("g")
      .data(graphData.nodes)
      .enter()
      .append("g")
      .call(d3.drag()
        .on("start", dragStarted)
        .on("drag", dragged)
        .on("end", dragEnded)
      )
      .on("click", (event, d) => {
        event.stopPropagation();
        setSelectedNode(d);
      });

    // Add circles to node groups
    const node = nodeGroup.append("circle")
      .attr("r", d => {
        if (d.type === "theme") return 10 + (d.feedbackCount / feedbackWeight);
        if (d.type === "feature") {
          if (d.priority === "High") return 12;
          return 10;
        }
        return 12;
      })
      .attr("fill", d => colorScale(d.type))
      .attr("stroke", "#fff")
      .attr("stroke-width", 1.5);

    // Add pulsing animation to theme nodes
    svg.selectAll("circle")
      .filter(d => d.type === "theme")
      .transition()
      .duration(2000)
      .attr("r", d => (10 + (d.feedbackCount / feedbackWeight)) * 1.2)
      .transition()
      .duration(2000)
      .attr("r", d => 10 + (d.feedbackCount / feedbackWeight))
      .on("end", function repeat() {
        d3.select(this)
          .transition()
          .duration(2000)
          .attr("r", d => (10 + (d.feedbackCount / feedbackWeight)) * 1.2)
          .transition()
          .duration(2000)
          .attr("r", d => 10 + (d.feedbackCount / feedbackWeight))
          .on("end", repeat);
      });

    // Add labels to nodes
    nodeGroup.append("text")
      .text(d => d.name)
      .attr("x", 0)
      .attr("y", d => {
        if (d.type === "theme") return -15 - (d.feedbackCount / feedbackWeight);
        return -15;
      })
      .attr("text-anchor", "middle")
      .attr("font-size", "12px")
      .attr("fill", "#333")
      .attr("pointer-events", "none");

    // Highlight mode implementation
    if (highlightMode !== 'none' || selectedNode) {
      link.attr("opacity", 0.1);
      node.attr("opacity", 0.2);

      if (selectedNode) {
        // Get connected nodes
        const linkedNodes = new Set();
        graphData.links.forEach(l => {
          if (l.source.id === selectedNode.id || l.source === selectedNode.id) {
            linkedNodes.add(l.target.id || l.target);
          }
          if (l.target.id === selectedNode.id || l.target === selectedNode.id) {
            linkedNodes.add(l.source.id || l.source);
          }
        });

        linkedNodes.add(selectedNode.id);

        link.filter(l => {
          const sourceId = l.source.id || l.source;
          const targetId = l.target.id || l.target;
          return linkedNodes.has(sourceId) && linkedNodes.has(targetId);
        }).attr("opacity", 1);

        node.filter(d => linkedNodes.has(d.id)).attr("opacity", 1);
      } else if (highlightMode === 'themes') {
        node.filter(d => d.type === 'theme').attr("opacity", 1);
      } else if (highlightMode === 'features') {
        node.filter(d => d.type === 'feature').attr("opacity", 1);
      }
    }

    // Update positions on each tick of the simulation
    simulation.on("tick", () => {
      link
        .attr("x1", d => d.source.x)
        .attr("y1", d => d.source.y)
        .attr("x2", d => d.target.x)
        .attr("y2", d => d.target.y);

      nodeGroup.attr("transform", d => `translate(${d.x}, ${d.y})`);
    });

    // Drag functions
    function dragStarted(event, d) {
      if (!event.active) simulation.alphaTarget(0.3).restart();
      d.fx = d.x;
      d.fy = d.y;
    }

    function dragged(event, d) {
      d.fx = event.x;
      d.fy = event.y;
    }

    function dragEnded(event, d) {
      if (!event.active) simulation.alphaTarget(0);
      d.fx = null;
      d.fy = null;
    }

    // Pause simulation if needed
    if (!isSimulationRunning) {
      simulation.stop();
    }

    return () => {
      simulation.stop();
    };
  }, [graphData, dimensions, feedbackWeight, isSimulationRunning, selectedNode, highlightMode]);

  // Update graph dimensions when window resizes
  useEffect(() => {
    const updateDimensions = () => {
      if (containerRef.current) {
        const { width, height } = containerRef.current.getBoundingClientRect();
        setDimensions({ width, height });
      }
    };

    window.addEventListener('resize', updateDimensions);
    updateDimensions();

    return () => window.removeEventListener('resize', updateDimensions);
  }, []);

  // Create a new theme with custom name
  const addTheme = () => {
    setModalType('theme');
    setNewNodeName(`Feedback Theme ${graphData.nodes.filter(n => n.type === 'theme').length + 1}`);
    setNewNodeFeedbackCount(10);
    setShowModal(true);
  };

  // Create a new feature with custom name
  const addFeature = () => {
    setModalType('feature');
    setNewNodeName(`Feature Idea ${graphData.nodes.filter(n => n.type === 'feature').length + 1}`);
    setNewNodePriority('Medium');
    setShowModal(true);
  };

  // Add a function to handle form submission
  const handleModalSubmit = () => {
    if (modalType === 'theme') {
      const newId = `theme${graphData.nodes.filter(n => n.type === 'theme').length + 1}`;
      const newTheme = {
        id: newId,
        name: newNodeName,
        type: "theme",
        feedbackCount: newNodeFeedbackCount
      };

      setGraphData({
        nodes: [...graphData.nodes, newTheme],
        links: graphData.links
      });
    } else if (modalType === 'feature') {
      const newId = `feature${graphData.nodes.filter(n => n.type === 'feature').length + 1}`;
      const newFeature = {
        id: newId,
        name: newNodeName,
        type: "feature",
        priority: newNodePriority
      };

      setGraphData({
        nodes: [...graphData.nodes, newFeature],
        links: graphData.links
      });
    }

    setShowModal(false);
  };

  // Create a connection between nodes
  const connectNodes = () => {
    if (!selectedNode) return;

    // Find suitable target nodes to connect to (different type than selected node)
    const targets = graphData.nodes.filter(n => n.type !== selectedNode.type);

    if (targets.length === 0) {
      alert("No compatible nodes found to connect with.");
      return;
    }

    setConnectionSource(selectedNode);
    setAvailableTargets(targets);
    setConnectionTarget(targets[0].id); // Default to first available target
    setConnectionStrength(0.5);
    setShowConnectionModal(true);
  };

  // Reset the visualization
  const resetVisualization = () => {
    setGraphData(initialData);
    setSelectedNode(null);
    setHighlightMode('none');
  };

  // Handle node selection
  const handleNodeSelection = () => {
    if (selectedNode) {
      setSelectedNode(null);
    } else {
      // Select a random node if none is selected
      const randomIndex = Math.floor(Math.random() * graphData.nodes.length);
      setSelectedNode(graphData.nodes[randomIndex]);
    }
  };

  // Handle connection modal submission
  const handleConnectionSubmit = () => {
    const newLinks = [];

    // If single connection mode
    if (!multipleConnections.length) {
      // Check if connection already exists
      const existingConnection = graphData.links.find(
        l => (l.source.id === connectionSource.id && l.target === connectionTarget) ||
             (l.source === connectionTarget && l.target.id === connectionSource.id)
      );

      if (!existingConnection) {
        newLinks.push({
          source: connectionSource.id,
          target: connectionTarget,
          strength: connectionStrength
        });
      }
    } else {
      // Multiple connections mode
      multipleConnections.forEach(targetId => {
        const existingConnection = graphData.links.find(
          l => (l.source.id === connectionSource.id && l.target === targetId) ||
               (l.source === targetId && l.target.id === connectionSource.id)
        );

        if (!existingConnection) {
          newLinks.push({
            source: connectionSource.id,
            target: targetId,
            strength: connectionStrength
          });
        }
      });
    }

    if (newLinks.length > 0) {
      setGraphData({
        nodes: graphData.nodes,
        links: [...graphData.links, ...newLinks]
      });
    }

    setShowConnectionModal(false);
    setMultipleConnections([]);
  };

  const suggestConnections = () => {
    if (!selectedNode) return;

    // Find nodes of the same type that have similar connections
    const sameTypeNodes = graphData.nodes.filter(n => 
      n.id !== selectedNode.id && n.type === selectedNode.type
    );

    // Get current connections of the selected node
    const currentConnections = graphData.links
      .filter(l => l.source.id === selectedNode.id || l.source === selectedNode.id 
              || l.target.id === selectedNode.id || l.target === selectedNode.id)
      .map(l => {
        const targetId = l.source.id === selectedNode.id || l.source === selectedNode.id 
          ? (l.target.id || l.target) 
          : (l.source.id || l.source);
        return targetId;
      });

    // Find potential new connections based on what similar nodes are connected to
    const potentialConnections = [];

    sameTypeNodes.forEach(similarNode => {
      // Find connections of this similar node
      graphData.links
        .filter(l => l.source.id === similarNode.id || l.source === similarNode.id 
                || l.target.id === similarNode.id || l.target === similarNode.id)
        .forEach(l => {
          const targetId = l.source.id === similarNode.id || l.source === similarNode.id 
            ? (l.target.id || l.target) 
            : (l.source.id || l.source);

          // If this is a new potential connection
          if (!currentConnections.includes(targetId)) {
            const targetNode = graphData.nodes.find(n => n.id === targetId);
            if (targetNode && targetNode.type !== selectedNode.type) {
              potentialConnections.push(targetNode);
            }
          }
        });
    });

    // Remove duplicates
    const uniquePotentials = Array.from(new Set(potentialConnections.map(n => n.id)))
      .map(id => potentialConnections.find(n => n.id === id));

    if (uniquePotentials.length === 0) {
      alert("No suggested connections found.");
      return;
    }

    // Set up the connection modal with suggested connections
    setConnectionSource(selectedNode);
    setAvailableTargets(uniquePotentials);
    setConnectionTarget(uniquePotentials[0].id);
    setConnectionStrength(0.5);
    setShowConnectionModal(true);
  };

  if (loading) {
    return (
      <div className="flex flex-col h-full items-center justify-center bg-gray-50">
        <div className="text-2xl text-blue-800 mb-4">Loading Insights Network...</div>
        <div className="w-16 h-16 border-4 border-blue-600 border-t-transparent rounded-full animate-spin"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="flex flex-col h-full items-center justify-center bg-gray-50">
        <div className="text-2xl text-red-800 mb-4">Error Loading Data</div>
        <div className="text-gray-600">{error}</div>
        <button 
          className="mt-4 px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
          onClick={() => window.location.reload()}
        >
          Retry
        </button>
      </div>
    );
  }

  return (
    <div className="flex flex-col h-full">
      <div className="bg-white p-4 shadow-md">
        <h1 className="text-2xl font-bold text-blue-800">User Research Insights Network</h1>
        <p className="text-gray-600">Visualizing connections between user feedback, features, and personas</p>

        <div className="flex flex-wrap gap-2 mt-4">
          <button 
            className="px-3 py-1 bg-blue-600 text-white rounded hover:bg-blue-700 text-sm"
            onClick={() => setIsSimulationRunning(!isSimulationRunning)}
          >
            {isSimulationRunning ? 'Pause' : 'Resume'} Simulation
          </button>

          <button 
            className="px-3 py-1 bg-green-600 text-white rounded hover:bg-green-700 text-sm"
            onClick={addTheme}
          >
            Add Feedback Theme
          </button>

          <button 
            className="px-3 py-1 bg-orange-600 text-white rounded hover:bg-orange-700 text-sm"
            onClick={addFeature}
          >
            Add Feature Idea
          </button>

          <button 
            className={`px-3 py-1 rounded text-sm ${selectedNode ? 'bg-purple-600 text-white hover:bg-purple-700' : 'bg-gray-300 text-gray-600'}`}
            onClick={connectNodes}
            disabled={!selectedNode}
          >
            {selectedNode ? `Connect "${selectedNode.name}"` : 'Select a Node to Connect'}
          </button>

          <button 
            className="px-3 py-1 bg-red-600 text-white rounded hover:bg-red-700 text-sm"
            onClick={resetVisualization}
          >
            Reset Visualization
          </button>

          <button 
            className="px-3 py-1 bg-indigo-600 text-white rounded hover:bg-indigo-700 text-sm"
            onClick={exportData}
          >
            Export Data
          </button>

          <div className="relative">
            <button 
              className="px-3 py-1 bg-teal-600 text-white rounded hover:bg-teal-700 text-sm"
              onClick={() => document.getElementById('file-upload').click()}
            >
              Import Data
            </button>
            <input 
              id="file-upload" 
              type="file" 
              accept=".json" 
              className="hidden" 
              onChange={importData} 
            />
          </div>
        </div>

        <div className="flex flex-wrap gap-4 mt-4">
          <div className="flex items-center space-x-2">
            <span className="text-sm text-gray-600">Feedback Weight:</span>
            <input 
              type="range" 
              min="2" 
              max="10" 
              value={feedbackWeight}
              onChange={(e) => setFeedbackWeight(Number(e.target.value))}
              className="w-32"
            />
          </div>

          <div className="flex items-center space-x-2">
            <span className="text-sm text-gray-600">Highlight:</span>
            <select 
              value={highlightMode}
              onChange={(e) => setHighlightMode(e.target.value)}
              className="border rounded px-2 py-1 text-sm"
            >
              <option value="none">None</option>
              <option value="themes">Feedback Themes</option>
              <option value="features">Feature Ideas</option>
              <option value="connections">Connected Nodes</option>
            </select>

            <button 
              className={`px-3 py-1 rounded text-sm ${highlightMode === 'connections' ? 'bg-indigo-600 text-white hover:bg-indigo-700' : 'bg-gray-200 text-gray-600 hover:bg-gray-300'}`}
              onClick={handleNodeSelection}
            >
              {selectedNode ? 'Clear Selection' : 'Select Random Node'}
            </button>
          </div>
        </div>
      </div>

      <div className="flex-1 bg-gray-50 p-4" ref={containerRef}>
        <svg 
          ref={svgRef} 
          width={dimensions.width} 
          height={dimensions.height}
          className="border border-gray-200 rounded-lg bg-white shadow-inner w-full h-full"
          onClick={() => setSelectedNode(null)}
        />
      </div>

      <div className="bg-white p-4 shadow-md mt-4">
        <div className="flex flex-wrap gap-8">
          <div className="flex items-center gap-2">
            <div className="w-4 h-4 rounded-full bg-blue-600"></div>
            <span className="text-sm">Feedback Themes</span>
          </div>
          <div className="flex items-center gap-2">
            <div className="w-4 h-4 rounded-full bg-green-600"></div>
            <span className="text-sm">Feature Ideas</span>
          </div>
          <div className="flex items-center gap-2">
            <div className="w-4 h-4 rounded-full bg-orange-600"></div>
            <span className="text-sm">User Personas</span>
          </div>

          <div className="flex-1"></div>

          {selectedNode && (
            <div className="bg-gray-100 px-4 py-2 rounded-md flex items-center">
              <div>
                <span className="font-medium">{selectedNode.name}</span>
                {selectedNode.type === 'theme' && (
                  <span> ({selectedNode.feedbackCount} feedbacks)</span>
                )}
              </div>
              <button
                className="ml-4 px-2 py-1 bg-purple-600 text-white rounded hover:bg-purple-700 text-xs"
                onClick={connectNodes}
              >
                Connect
              </button>
            </div>
          )}
        </div>
      </div>

      {/* Modal for adding new nodes */}
      {showModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded-lg shadow-lg w-96">
            <h2 className="text-xl font-bold mb-4">
              {modalType === 'theme' ? 'Add New Feedback Theme' : 'Add New Feature Idea'}
            </h2>

            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-bold mb-2">
                Name
              </label>
              <input
                type="text"
                value={newNodeName}
                onChange={(e) => setNewNodeName(e.target.value)}
                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
              />
            </div>

            {modalType === 'theme' ? (
              <div className="mb-4">
                <label className="block text-gray-700 text-sm font-bold mb-2">
                  Feedback Count
                </label>
                <input
                  type="number"
                  min="1"
                  max="100"
                  value={newNodeFeedbackCount}
                  onChange={(e) => setNewNodeFeedbackCount(Number(e.target.value))}
                  className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                />
              </div>
            ) : (
              <div className="mb-4">
                <label className="block text-gray-700 text-sm font-bold mb-2">
                  Priority
                </label>
                <select
                  value={newNodePriority}
                  onChange={(e) => setNewNodePriority(e.target.value)}
                  className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                >
                  <option value="Low">Low</option>
                  <option value="Medium">Medium</option>
                  <option value="High">High</option>
                </select>
              </div>
            )}

            <div className="flex justify-end gap-2">
              <button
                onClick={() => setShowModal(false)}
                className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-bold py-2 px-4 rounded"
              >
                Cancel
              </button>
              <button
                onClick={handleModalSubmit}
                className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
              >
                Add
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Modal for creating connections */}
      {showConnectionModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded-lg shadow-lg w-96">
            <h2 className="text-xl font-bold mb-4">Create Connection</h2>

            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-bold mb-2">
                From: {connectionSource?.name} ({connectionSource?.type})
              </label>
            </div>

            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-bold mb-2">
                To:
              </label>
              <select
                value={connectionTarget}
                onChange={(e) => setConnectionTarget(e.target.value)}
                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
              >
                {availableTargets.map(node => (
                  <option key={node.id} value={node.id}>
                    {node.name} ({node.type})
                  </option>
                ))}
              </select>
            </div>

            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-bold mb-2">
                Connection Strength: {connectionStrength}
              </label>
              <input
                type="range"
                min="0.1"
                max="1"
                step="0.1"
                value={connectionStrength}
                onChange={(e) => setConnectionStrength(Number(e.target.value))}
                className="w-full"
              />
              <div className="flex justify-between text-xs text-gray-500">
                <span>Weak</span>
                <span>Strong</span>
              </div>
            </div>

            <div className="flex justify-end gap-2">
              <button
                onClick={() => setShowConnectionModal(false)}
                className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-bold py-2 px-4 rounded"
              >
                Cancel
              </button>
              <button
                onClick={handleConnectionSubmit}
                className="bg-purple-600 hover:bg-purple-700 text-white font-bold py-2 px-4 rounded"
              >
                Connect
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default UserResearchNetwork;
