import React, { useState, useEffect, useRef } from 'react';
import * as d3 from 'd3';
import { classifyFeedback, generateFeatureIdeas } from '../services/openaiService';

const UserResearchNetwork = () => {
  const svgRef = useRef(null);
  const containerRef = useRef(null);
  const [dimensions, setDimensions] = useState({ width: 800, height: 600 });
  const [isSimulationRunning, setIsSimulationRunning] = useState(true);
  const [selectedNode, setSelectedNode] = useState(null);
  const [highlightMode, setHighlightMode] = useState('none');
  const [feedbackWeight, setFeedbackWeight] = useState(5);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [modalType, setModalType] = useState(''); // 'theme' or 'feature'
  const [newNodeName, setNewNodeName] = useState('');
  const [newNodeFeedbackCount, setNewNodeFeedbackCount] = useState(10);
  const [newNodePriority, setNewNodePriority] = useState('Medium');
  const [showConnectionModal, setShowConnectionModal] = useState(false);
  const [connectionSource, setConnectionSource] = useState(null);
  const [connectionTarget, setConnectionTarget] = useState('');
  const [connectionStrength, setConnectionStrength] = useState(0.5);
  const [availableTargets, setAvailableTargets] = useState([]);
  const [multipleConnections, setMultipleConnections] = useState([]);
  const [showMiniMap, setShowMiniMap] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [showSearchResults, setShowSearchResults] = useState(false);
  const [isAIProcessing, setIsAIProcessing] = useState(false);
  const [aiError, setAIError] = useState(null);
  const [showFeedbackModal, setShowFeedbackModal] = useState(false);
  const [newFeedback, setNewFeedback] = useState('');
  const [newFeedbackRole, setNewFeedbackRole] = useState('');
  const [feedbackItems, setFeedbackItems] = useState([]);
  const [showAIInsightsPanel, setShowAIInsightsPanel] = useState(false);
  const [aiInsights, setAIInsights] = useState(null);
  const [isPanelCollapsed, setIsPanelCollapsed] = useState(false);
  const [panelWidth, setPanelWidth] = useState(320); // Default width

  // Add this constant at the top of your component, right after your state declarations
  const colorScale = d3.scaleOrdinal()
    .domain(["theme", "feature", "persona"])
    .range(["#3182CE", "#38A169", "#DD6B20"]);

  // Initial data structure
  const initialData = {
    nodes: [
      // Feedback themes (blue)
      { id: "theme1", name: "Slow Performance", type: "theme", feedbackCount: 37 },
      { id: "theme2", name: "Confusing Navigation", type: "theme", feedbackCount: 42 },
      { id: "theme3", name: "Missing Features", type: "theme", feedbackCount: 28 },
      { id: "theme4", name: "Sync Issues", type: "theme", feedbackCount: 15 },
      { id: "theme5", name: "Cluttered Interface", type: "theme", feedbackCount: 31 },
      { id: "theme6", name: "Mobile Experience", type: "theme", feedbackCount: 25 },
      { id: "theme7", name: "Data Visualization", type: "theme", feedbackCount: 19 },

      // Feature ideas (green)
      { id: "feature1", name: "Performance Optimization", type: "feature", priority: "High" },
      { id: "feature2", name: "Navigation Redesign", type: "feature", priority: "High" },
      { id: "feature3", name: "Bulk Actions", type: "feature", priority: "Medium" },
      { id: "feature4", name: "Offline Mode", type: "feature", priority: "Medium" },
      { id: "feature5", name: "Interface Simplification", type: "feature", priority: "High" },
      { id: "feature6", name: "Mobile App", type: "feature", priority: "High" },
      { id: "feature7", name: "Advanced Charts", type: "feature", priority: "Medium" },

      // User personas (orange)
      { id: "persona1", name: "Power User", type: "persona" },
      { id: "persona2", name: "Casual User", type: "persona" },
      { id: "persona3", name: "Admin", type: "persona" },
      { id: "persona4", name: "Mobile User", type: "persona" }
    ],
    links: [
      // Theme to Feature connections
      { source: "theme1", target: "feature1", strength: 0.8 },
      { source: "theme1", target: "feature4", strength: 0.4 },
      { source: "theme2", target: "feature2", strength: 0.9 },
      { source: "theme2", target: "feature5", strength: 0.5 },
      { source: "theme3", target: "feature3", strength: 0.6 },
      { source: "theme3", target: "feature7", strength: 0.5 },
      { source: "theme4", target: "feature4", strength: 0.8 },
      { source: "theme5", target: "feature5", strength: 0.9 },
      { source: "theme6", target: "feature6", strength: 0.9 },
      { source: "theme7", target: "feature7", strength: 0.8 },

      // Persona connections to themes
      { source: "persona1", target: "theme1", strength: 0.7 },
      { source: "persona1", target: "theme3", strength: 0.8 },
      { source: "persona1", target: "theme7", strength: 0.9 },
      { source: "persona2", target: "theme2", strength: 0.8 },
      { source: "persona2", target: "theme5", strength: 0.7 },
      { source: "persona3", target: "theme1", strength: 0.6 },
      { source: "persona3", target: "theme4", strength: 0.9 },
      { source: "persona4", target: "theme6", strength: 0.9 },

      // Persona connections to features
      { source: "persona1", target: "feature1", strength: 0.8 },
      { source: "persona1", target: "feature3", strength: 0.7 },
      { source: "persona1", target: "feature7", strength: 0.9 },
      { source: "persona2", target: "feature2", strength: 0.7 },
      { source: "persona2", target: "feature5", strength: 0.8 },
      { source: "persona3", target: "feature1", strength: 0.6 },
      { source: "persona3", target: "feature4", strength: 0.8 },
      { source: "persona4", target: "feature6", strength: 0.9 }
    ]
  };

  const [graphData, setGraphData] = useState(initialData);

  // Add this at the component level (outside any useEffect)
  const zoomRef = useRef(null);

  // Add these refs at the component level, near your other refs
  const miniMapSizeRef = useRef(0);
  const miniMapScaleRef = useRef(0);

  // Move this useEffect to be with the other useEffect hooks at the top of the component
  useEffect(() => {
    // Simulate loading data
    setLoading(true);
    setTimeout(() => {
      setLoading(false);
    }, 1000);
  }, []);

  // Add this useEffect right after the loading useEffect
  useEffect(() => {
    if (showMiniMap && svgRef.current && dimensions.width) {
      console.log("Creating mini-map from useEffect...");
      createMiniMap();
    }
  }, [showMiniMap, graphData, dimensions.width, dimensions.height]);

  useEffect(() => {
    if (!svgRef.current || !dimensions.width) return;

    const svg = d3.select(svgRef.current);
    svg.selectAll("*").remove();

    const g = svg.append("g");

    // Create a zoom behavior and store it in the ref
    const zoom = d3.zoom()
      .scaleExtent([0.1, 4])
      .on("zoom", (event) => {
        g.attr("transform", event.transform);

        // Update the mini-map viewport to show the current view
        if (showMiniMap) {
          const miniMapSize = Math.min(dimensions.width, dimensions.height) * 0.25;
          const viewportRect = svg.select(".mini-map-viewport");

          if (!viewportRect.empty()) {
            const { x, y, k } = event.transform;

            // Calculate the viewport rectangle in the mini-map
            const vpWidth = miniMapSize / k;
            const vpHeight = miniMapSize / k;
            const vpX = (-x / dimensions.width) * miniMapSize;
            const vpY = (-y / dimensions.height) * miniMapSize;

            // Update the viewport rectangle
            viewportRect
              .attr("x", Math.max(0, vpX))
              .attr("y", Math.max(0, vpY))
              .attr("width", Math.min(miniMapSize, vpWidth))
              .attr("height", Math.min(miniMapSize, vpHeight));
          }
        }
      });

    // Store the zoom behavior in the ref
    zoomRef.current = zoom;

    svg.call(zoom);

    // Ensure all nodes are objects and all links reference nodes by ID
    const nodes = graphData.nodes.map(node => ({...node}));

    // Create a map of node IDs to node objects for quick lookup
    const nodeMap = {};
    nodes.forEach(node => {
      nodeMap[node.id] = node;
    });

    // Process links to ensure they reference nodes by ID
    const links = graphData.links.map(link => {
      // Create a new link object to avoid modifying the original
      return {
        source: typeof link.source === 'object' ? link.source.id : link.source,
        target: typeof link.target === 'object' ? link.target.id : link.target,
        strength: link.strength
      };
    });

    // Create links
    const linkElements = g.selectAll(".link")
      .data(links)
      .enter()
      .append("line")
      .attr("class", "link")
      .attr("stroke", d => {
        // Determine color based on the types of nodes being connected
        const sourceNode = nodeMap[d.source];
        const targetNode = nodeMap[d.target];

        if (!sourceNode || !targetNode) return "#CBD5E0"; // Default gray if node not found

        const sourceType = sourceNode.type;
        const targetType = targetNode.type;

        if (sourceType === "theme" && targetType === "feature") return "#90CDF4"; // Light blue
        if (sourceType === "feature" && targetType === "theme") return "#90CDF4"; // Light blue
        if (sourceType === "theme" && targetType === "persona") return "#FBD38D"; // Light orange
        if (sourceType === "persona" && targetType === "theme") return "#FBD38D"; // Light orange
        if (sourceType === "feature" && targetType === "persona") return "#9AE6B4"; // Light green
        if (sourceType === "persona" && targetType === "feature") return "#9AE6B4"; // Light green

        return "#CBD5E0"; // Default gray
      })
      .attr("stroke-width", d => Math.max(1, d.strength * 5))
      .attr("stroke-opacity", 0.6);

    // Create node groups
    const nodeGroup = g.selectAll(".node")
      .data(nodes)
      .enter()
      .append("g")
      .attr("class", "node")
      .call(d3.drag()
        .on("start", dragstarted)
        .on("drag", dragged)
        .on("end", dragended)
      )
      .on("click", (event, d) => {
        event.stopPropagation();
        setSelectedNode(d);
        handleNodeClick(d);
      });

    // Add circles to nodes
    nodeGroup.append("circle")
      .attr("r", d => {
        if (d.type === "theme") return 20 + (d.feedbackCount / feedbackWeight);
        if (d.type === "feature") {
          if (d.priority === "High") return 22;
          return 20;
        }
        return 22;
      })
      .attr("fill", d => colorScale(d.type))
      .attr("stroke", "#fff")
      .attr("stroke-width", 2)
      .attr("opacity", d => {
        if (highlightMode === 'none') return 0.8;
        if (highlightMode === 'themes' && d.type === 'theme') return 0.9;
        if (highlightMode === 'features' && d.type === 'feature') return 0.9;
        if (highlightMode === 'personas' && d.type === 'persona') return 0.9;
        return 0.3;
      });

    // Add labels to nodes
    nodeGroup.append("text")
      .text(d => d.name)
      .attr("text-anchor", "middle")
      .attr("dy", d => {
        if (d.type === "theme") return (20 + (d.feedbackCount / feedbackWeight)) + 15;
        if (d.type === "feature") {
          if (d.priority === "High") return 22 + 15;
          return 20 + 15;
        }
        return 22 + 15;
      })
      .attr("font-size", "12px")
      .attr("fill", "#4A5568")
      .attr("opacity", d => {
        if (highlightMode === 'none') return 1;
        if (highlightMode === 'themes' && d.type === 'theme') return 1;
        if (highlightMode === 'features' && d.type === 'feature') return 1;
        if (highlightMode === 'personas' && d.type === 'persona') return 1;
        return 0.5;
      });

    // Create a force simulation with the processed nodes and links
    const simulation = d3.forceSimulation(nodes)
      .force("link", d3.forceLink(links)
        .id(d => d.id)
        .distance(d => 150 / (d.strength || 0.5))
      )
      .force("charge", d3.forceManyBody().strength(-300).distanceMax(500))
      .force("center", d3.forceCenter(dimensions.width / 2, dimensions.height / 2))
      .force("collide", d3.forceCollide().radius(d => {
        if (d.type === "theme") return 20 + (d.feedbackCount / feedbackWeight);
        if (d.type === "feature") {
          if (d.priority === "High") return 22;
          return 20;
        }
        return 22;
      }).strength(0.8))
      .force("x", d3.forceX(dimensions.width / 2).strength(0.05))
      .force("y", d3.forceY(dimensions.height / 2).strength(0.05));

    // Add a boundary force to keep nodes within the visible area
    simulation.force("boundary", () => {
      const padding = 50;
      nodes.forEach(node => {
        // Add some random movement if simulation is running
        if (isSimulationRunning) {
          node.vx = (node.vx || 0) + (Math.random() - 0.5) * 2;
          node.vy = (node.vy || 0) + (Math.random() - 0.5) * 2;
        }

        // Update positions
        if (node.x < padding) node.x = padding;
        if (node.x > dimensions.width - padding) node.x = dimensions.width - padding;
        if (node.y < padding) node.y = padding;
        if (node.y > dimensions.height - padding) node.y = dimensions.height - padding;
      });
    });

    // Update the simulation on tick
    simulation.on("tick", () => {
      // Apply the boundary force
      simulation.force("boundary")();

      // Update positions
      linkElements
        .attr("x1", d => {
          const source = typeof d.source === 'object' ? d.source : nodeMap[d.source];
          return source ? source.x : 0;
        })
        .attr("y1", d => {
          const source = typeof d.source === 'object' ? d.source : nodeMap[d.source];
          return source ? source.y : 0;
        })
        .attr("x2", d => {
          const target = typeof d.target === 'object' ? d.target : nodeMap[d.target];
          return target ? target.x : 0;
        })
        .attr("y2", d => {
          const target = typeof d.target === 'object' ? d.target : nodeMap[d.target];
          return target ? target.y : 0;
        });

      nodeGroup.attr("transform", d => `translate(${d.x}, ${d.y})`);
    });

    // Pause or resume the simulation based on state
    if (!isSimulationRunning) {
      simulation.alpha(0).stop();
    } else {
      simulation.alpha(0.3).restart();
    }

    // Create the mini-map if it's enabled
    if (showMiniMap) {
      createMiniMap();
    }

    // Center the view after the simulation has started
    setTimeout(() => {
      centerView();
    }, 100);

    // Fit all nodes to screen after the simulation has started
    setTimeout(() => {
      fitToScreen();
    }, 300);

    // Drag functions
    function dragstarted(event, d) {
      if (!event.active) simulation.alphaTarget(0.3).restart();
      d.fx = d.x;
      d.fy = d.y;
    }

    function dragged(event, d) {
      d.fx = event.x;
      d.fy = event.y;
    }

    function dragended(event, d) {
      if (!event.active) simulation.alphaTarget(0);
      d.fx = null;
      d.fy = null;
    }

    return () => {
      simulation.stop();
    };
  }, [dimensions, graphData, feedbackWeight, showMiniMap, isSimulationRunning, highlightMode]);

  // Add the feedbackItems useEffect here, with the other useEffect hooks
  useEffect(() => {
    if (feedbackItems.length > 0) {
      recalculateFeedbackCounts();
    }
  }, [feedbackItems]);

  // Then define your functions
  const exportData = () => {
    const dataStr = JSON.stringify(graphData, null, 2);
    const dataUri = 'data:application/json;charset=utf-8,'+ encodeURIComponent(dataStr);

    const exportFileDefaultName = 'feedback-insights-data.json';

    const linkElement = document.createElement('a');
    linkElement.setAttribute('href', dataUri);
    linkElement.setAttribute('download', exportFileDefaultName);
    linkElement.click();
  };

  const importData = (event) => {
    const file = event.target.files[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onload = (e) => {
      try {
        const importedData = JSON.parse(e.target.result);
        // Validate the data structure
        if (importedData.nodes && importedData.links) {
          setGraphData(importedData);
        } else {
          setError("Invalid data format. The file must contain 'nodes' and 'links' arrays.");
        }
      } catch (err) {
        setError("Error parsing the file. Please make sure it's a valid JSON file.");
      }
    };
    reader.readAsText(file);
  };

  const importSimplifiedData = (event) => {
    const file = event.target.files[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onload = async (e) => {
      try {
        const importedData = JSON.parse(e.target.result);

        // Check if it has the simplified format
        if (importedData.feedback && Array.isArray(importedData.feedback)) {
          setLoading(true);
          setFeedbackItems(importedData.feedback);

          // Process with AI instead of the simple grouping
          await processFeedbackBatch(importedData.feedback);

          setLoading(false);
