    const miniMapSimulation = d3.forceSimulation(miniMapNodes)
      .force("charge", d3.forceManyBody().strength(-20)) // Even stronger repulsion
      .force("center", d3.forceCenter(miniMapSize / 2, miniMapSize / 2))
      .force("collide", d3.forceCollide().radius(5)) // Larger collision radius
      .force("x", d3.forceX(miniMapSize / 2).strength(0.3)) // Stronger centering
      .force("y", d3.forceY(miniMapSize / 2).strength(0.3)); // Stronger centering

    // Add a boundary force to keep mini-map nodes within the mini-map
    miniMapSimulation.force("boundary", () => {
      const padding = 8; // Increased padding from the edges

      miniMapNodes.forEach(node => {
        // Keep nodes within horizontal bounds
        if (node.miniX < padding) node.miniX = padding;
        if (node.miniX > miniMapSize - padding) node.miniX = miniMapSize - padding;

        // Keep nodes within vertical bounds
        if (node.miniY < padding) node.miniY = padding;
        if (node.miniY > miniMapSize - padding) node.miniY = miniMapSize - padding;
      });
    });

    // Add mini-map nodes with larger radius and stronger colors
    const miniNodes = miniMapContainer.selectAll(".mini-node")
      .data(miniMapNodes)
      .enter()
      .append("circle")
      .attr("class", "mini-node")
      .attr("r", 4.5) // Even larger radius for better visibility
      .attr("fill", d => {
        // Use the same color scale but with higher opacity
        const baseColor = colorScale(d.type);
        return d3.color(baseColor).brighter(0.3); // Brighter colors
      })
      .attr("stroke", "#fff") // Add white stroke for better contrast
      .attr("stroke-width", 1.5); // Thicker stroke

    // Add a label with better visibility
    miniMapContainer.append("text")
      .attr("x", 10)
      .attr("y", 20)
      .attr("font-size", "14px") // Larger font
      .attr("font-weight", "bold") // Bold text
      .attr("fill", "#2D3748") // Darker text for better contrast
      .text("Network Overview");

    // Add a border to show the current viewport
    const viewportRect = miniMapContainer.append("rect")
      .attr("class", "mini-map-viewport")
      .attr("width", miniMapSize)
      .attr("height", miniMapSize)
      .attr("fill", "none")
      .attr("stroke", "#3182ce")
      .attr("stroke-width", 2)
      .attr("stroke-dasharray", "4,4");

    // Run the mini-map simulation for a fixed number of ticks
    miniMapSimulation.stop();
    for (let i = 0; i < 500; i++) { // More ticks for better layout
      miniMapSimulation.tick();
    }

    // Update the mini-map nodes with their final positions
    miniNodes
      .attr("cx", d => d.miniX)
      .attr("cy", d => d.miniY);

    // Add a semi-transparent overlay to make the mini-map stand out
    miniMapContainer.append("rect")
      .attr("width", miniMapSize)
      .attr("height", miniMapSize)
      .attr("fill", "transparent")
      .attr("stroke", "none")
      .style("cursor", "pointer")
      .on("click", (event) => {
        if (!zoomRef.current) return;

        // Get click position relative to mini-map
        const [x, y] = d3.pointer(event);

        // Calculate the center point in the main view
        const centerX = (x / miniMapSize) * dimensions.width;
        const centerY = (y / miniMapSize) * dimensions.height;

        // Create a transform that centers on the clicked point
        const transform = d3.zoomIdentity
          .translate(dimensions.width / 2, dimensions.height / 2)
          .scale(1)
          .translate(-centerX, -centerY);

        // Apply the transform with a smooth transition
        svg.transition()
          .duration(750)
          .call(zoomRef.current.transform, transform);
      });

    // Add a title to make it clear this is clickable
    miniMapContainer.append("text")
      .attr("x", miniMapSize / 2)
      .attr("y", miniMapSize - 12)
      .attr("text-anchor", "middle")
      .attr("font-size", "11px")
      .attr("font-weight", "medium")
      .attr("fill", "#4A5568")
      .text("Click to navigate");

    // Add a legend for node types
    const legendY = miniMapSize + 5;
    const legendX = 10;
    const legendSpacing = 20;

    // Theme legend item
    miniMapContainer.append("circle")
      .attr("cx", legendX)
      .attr("cy", legendY)
      .attr("r", 4)
      .attr("fill", colorScale("theme"));

    miniMapContainer.append("text")
      .attr("x", legendX + 8)
      .attr("y", legendY + 4)
      .attr("font-size", "10px")
      .attr("fill", "#4A5568")
      .text("Themes");

    // Feature legend item
    miniMapContainer.append("circle")
      .attr("cx", legendX + 70)
      .attr("cy", legendY)
      .attr("r", 4)
      .attr("fill", colorScale("feature"));

    miniMapContainer.append("text")
      .attr("x", legendX + 78)
      .attr("y", legendY + 4)
      .attr("font-size", "10px")
      .attr("fill", "#4A5568")
      .text("Features");

    // Persona legend item
    miniMapContainer.append("circle")
      .attr("cx", legendX + 140)
      .attr("cy", legendY)
      .attr("r", 4)
      .attr("fill", colorScale("persona"));

    miniMapContainer.append("text")
      .attr("x", legendX + 148)
      .attr("y", legendY + 4)
      .attr("font-size", "10px")
      .attr("fill", "#4A5568")
      .text("Personas");

    // Log that the mini-map was created
    console.log("Mini-map created with size:", miniMapSize, "at position: top-left");

    return miniMapSimulation;
  };

  // Replace the loading section with this code
  if (loading) {
    return (
      <div className="flex flex-col h-full items-center justify-center bg-gray-50">
        <div className="text-2xl text-blue-800 mb-4">Loading Insights Network...</div>
        <div className="w-16 h-16 border-4 border-blue-600 border-t-transparent rounded-full animate-spin"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="flex flex-col h-full items-center justify-center bg-gray-50">
        <div className="text-2xl text-red-800 mb-4">Error Loading Data</div>
        <div className="text-gray-600">{error}</div>
        <button 
          className="mt-4 px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
          onClick={() => window.location.reload()}
        >
          Retry
        </button>
      </div>
    );
  }

  // Add this function to toggle panel collapse state
  const togglePanelCollapse = () => {
    setIsPanelCollapsed(!isPanelCollapsed);
  };

  // Function to initiate a connection from a selected node
  const initiateConnection = (sourceNode) => {
    setConnectionSource(sourceNode);

    // Filter available targets based on the source node type
    let targets = [];
    if (sourceNode.type === "theme") {
      // Themes can connect to features and personas
      targets = graphData.nodes.filter(node => 
        (node.type === "feature" || node.type === "persona") && 
        node.id !== sourceNode.id
      );
    } else if (sourceNode.type === "feature") {
      // Features can connect to themes and personas
      targets = graphData.nodes.filter(node => 
        (node.type === "theme" || node.type === "persona") && 
        node.id !== sourceNode.id
      );
    } else if (sourceNode.type === "persona") {
      // Personas can connect to themes and features
      targets = graphData.nodes.filter(node => 
        (node.type === "theme" || node.type === "feature") && 
        node.id !== sourceNode.id
      );
    }

    setAvailableTargets(targets);
    setShowConnectionModal(true);
  };

  // Function to handle search result clicks
  const handleSearchResultClick = (node) => {
    setSelectedNode(node);
    setShowSearchResults(false);

    // Focus on the selected node
    if (node && node.x && node.y && zoomRef.current) {
      const svg = d3.select(svgRef.current);
      const transform = d3.zoomIdentity
        .translate(dimensions.width / 2, dimensions.height / 2)
        .scale(1.5)
        .translate(-node.x, -node.y);

      svg.transition()
        .duration(750)
        .call(zoomRef.current.transform, transform);
    }
  };

  // Add this function to recalculate all feedback counts
  const recalculateFeedbackCounts = () => {
    // Create a map to count feedback by theme and persona
    const themeCounts = {};
    const personaCounts = {};

    // Count feedback for each theme and persona
    feedbackItems.forEach(item => {
      // Count by persona (user_role)
      if (item.user_role) {
        personaCounts[item.user_role] = (personaCounts[item.user_role] || 0) + 1;
      }

      // Try to find theme classification for this feedback
      let theme = null;
      if (aiInsights && aiInsights.results) {
        const existingInsight = aiInsights.results.find(result => 
          feedbackItems[result.feedbackIndex] && 
          feedbackItems[result.feedbackIndex].id === item.id
        );

        if (existingInsight) {
          theme = existingInsight.theme;
        }
      }

      // If we found a theme, count it
      if (theme) {
        themeCounts[theme] = (themeCounts[theme] || 0) + 1;
      }

      // Also check if the feedback text contains any theme names
      graphData.nodes.forEach(node => {
        if (node.type === "theme" && item.text.toLowerCase().includes(node.name.toLowerCase())) {
          themeCounts[node.name] = (themeCounts[node.name] || 0) + 1;
        }
      });
    });

    // Update the graph nodes with the new counts
    const updatedNodes = graphData.nodes.map(node => {
      if (node.type === "theme" && themeCounts[node.name]) {
        return {
          ...node,
          feedbackCount: themeCounts[node.name]
        };
      }

      if (node.type === "persona" && personaCounts[node.name]) {
        return {
          ...node,
          feedbackCount: personaCounts[node.name]
        };
      }

      // For feature nodes, calculate based on connected themes
      if (node.type === "feature") {
        const connectedThemeIds = graphData.links
          .filter(link => (link.source.id === node.id || link.target.id === node.id))
          .map(link => {
            const connectedNodeId = link.source.id === node.id ? link.target.id : link.source.id;
            return connectedNodeId;
          });

        const connectedThemes = graphData.nodes.filter(n => 
          n.type === "theme" && connectedThemeIds.includes(n.id)
        );

        const totalFeedbackCount = connectedThemes.reduce((sum, theme) => 
          sum + (themeCounts[theme.name] || 0), 0
        );

        return {
          ...node,
          feedbackCount: totalFeedbackCount
        };
      }

      return node;
    });

    setGraphData({
      ...graphData,
      nodes: updatedNodes
    });
  };

  return (
    <div className="flex flex-col h-full">
      <div className="bg-white p-4 shadow-md">
        <div className="flex justify-between items-center mb-4">
          <div>
            <h1 className="text-2xl font-bold text-blue-800">User Research Insights Network</h1>
            <p className="text-gray-600">Visualizing connections between user feedback, features, and personas</p>
          </div>

          {/* Search */}
          <div className="relative w-64">
            <input
              type="text"
              placeholder="Search nodes..."
              value={searchTerm}
              onChange={(e) => {
                setSearchTerm(e.target.value);
                if (e.target.value.length >= 2) {
                  const results = graphData.nodes.filter(node => 
                    node.name.toLowerCase().includes(e.target.value.toLowerCase())
                  );
                  setSearchResults(results);
                  setShowSearchResults(true);
                } else {
                  setShowSearchResults(false);
                  setSearchResults([]);
                }
              }}
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
            {showSearchResults && searchResults.length > 0 && (
              <div className="absolute top-full left-0 right-0 mt-1 bg-white border border-gray-300 rounded-md shadow-lg z-20 max-h-60 overflow-y-auto">
                {searchResults.map((result, index) => (
                  <div 
                    key={index}
                    className="px-3 py-2 hover:bg-gray-100 cursor-pointer"
                    onClick={() => {
                      setSelectedNode(result);
                      handleNodeClick(result);
                      setShowSearchResults(false);
                    }}
                  >
                    <div className="flex items-center">
                      <div 
                        className="w-3 h-3 rounded-full mr-2"
                        style={{ backgroundColor: colorScale(result.type) }}
                      ></div>
                      <span>{result.name}</span>
                      <span className="ml-2 text-xs text-gray-500">({result.type})</span>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>

        {/* Controls */}
        <div className="flex flex-wrap gap-2 mb-4">
          <div className="flex items-center space-x-2">
            <span className="text-sm text-gray-600">Data:</span>
            <button 
              className="px-3 py-1 bg-blue-600 text-white rounded hover:bg-blue-700 text-sm"
              onClick={exportData}
            >
              Export
            </button>
            <label className="px-3 py-1 bg-blue-600 text-white rounded hover:bg-blue-700 text-sm cursor-pointer">
              Import
              <input 
                type="file" 
                accept=".json" 
                className="hidden" 
                onChange={importData}
              />
            </label>
            <button 
              className="px-3 py-1 bg-red-600 text-white rounded hover:bg-red-700 text-sm"
              onClick={() => {
                if (window.confirm("Are you sure you want to reset the graph? This will remove all custom nodes and connections.")) {
                  setGraphData(initialData);
                }
              }}
            >
              Reset
            </button>
          </div>

          <div className="flex items-center space-x-2">
            <span className="text-sm text-gray-600">View:</span>
            <button 
              className="px-3 py-1 bg-purple-600 text-white rounded hover:bg-purple-700 text-sm"
              onClick={() => setIsSimulationRunning(!isSimulationRunning)}
            >
              {isSimulationRunning ? 'Pause Simulation' : 'Resume Simulation'}
            </button>
            <button 
              className="px-3 py-1 bg-purple-600 text-white rounded hover:bg-purple-700 text-sm"
              onClick={centerView}
            >
              Center View
            </button>
            <button 
              className="px-3 py-1 bg-purple-600 text-white rounded hover:bg-purple-700 text-sm"
              onClick={toggleMiniMap}
            >
              {showMiniMap ? 'Hide Mini-Map' : 'Show Mini-Map'}
            </button>
            <button 
              className="px-3 py-1 bg-blue-600 text-white rounded hover:bg-blue-700 text-sm"
              onClick={fitToScreen}
              title="Fit all nodes to screen"
            >
              Fit to Screen
            </button>
          </div>

          <div className="flex items-center space-x-2">
            <span className="text-sm text-gray-600">Add:</span>
            <button 
              className="px-3 py-1 bg-green-600 text-white rounded hover:bg-green-700 text-sm"
              onClick={() => {
                setModalType('theme');
                setShowModal(true);
              }}
            >
              Add Theme
            </button>
            <button 
              className="px-3 py-1 bg-green-600 text-white rounded hover:bg-green-700 text-sm"
              onClick={() => {
                setModalType('feature');
                setShowModal(true);
              }}
            >
              Add Feature
            </button>
            <button 
              className="px-3 py-1 bg-green-600 text-white rounded hover:bg-green-700 text-sm"
              onClick={() => setShowFeedbackModal(true)}
            >
              Add Feedback
            </button>
          </div>
        </div>

        <div className="flex flex-wrap gap-4">
          <div className="flex items-center space-x-2">
            <span className="text-sm text-gray-600">Connect:</span>
            <button 
              className={`px-3 py-1 rounded text-sm ${
                selectedNode ? 'bg-orange-600 text-white hover:bg-orange-700' : 'bg-gray-300 text-gray-500 cursor-not-allowed'
              }`}
              onClick={() => {
                if (selectedNode) {
                  initiateConnection(selectedNode);
                }
              }}
              disabled={!selectedNode}
            >
              Connect Nodes
            </button>
          </div>

          <div className="flex items-center space-x-2">
            <span className="text-sm text-gray-600">Feedback Weight:</span>
            <input 
              type="range" 
              min="1" 
              max="10" 
              value={feedbackWeight}
              onChange={(e) => setFeedbackWeight(parseInt(e.target.value))}
              className="w-32"
            />
            <span className="text-sm text-gray-600">{feedbackWeight}</span>
          </div>

          <div className="flex items-center space-x-2">
            <span className="text-sm text-gray-600">Highlight:</span>
            <button 
              className={`px-2 py-1 rounded text-xs ${highlightMode === 'none' ? 'bg-blue-600 text-white' : 'bg-gray-200 text-gray-700'}`}
              onClick={() => setHighlightMode('none')}
            >
              None
            </button>
            <button 
              className={`px-2 py-1 rounded text-xs ${highlightMode === 'themes' ? 'bg-blue-600 text-white' : 'bg-gray-200 text-gray-700'}`}
              onClick={() => setHighlightMode('themes')}
            >
              Themes
            </button>
            <button 
              className={`px-2 py-1 rounded text-xs ${highlightMode === 'features' ? 'bg-blue-600 text-white' : 'bg-gray-200 text-gray-700'}`}
              onClick={() => setHighlightMode('features')}
            >
              Features
            </button>
            <button 
              className={`px-2 py-1 rounded text-xs ${highlightMode === 'personas' ? 'bg-blue-600 text-white' : 'bg-gray-200 text-gray-700'}`}
              onClick={() => setHighlightMode('personas')}
            >
              Personas
            </button>
          </div>
        </div>
      </div>

      <div 
        className={`flex-1 bg-gray-50 p-4 transition-all duration-300`}
        ref={containerRef}
        style={{
          marginRight: showAIInsightsPanel && !isPanelCollapsed ? `${panelWidth}px` : '0'
        }}
      >
        <svg 
          ref={svgRef} 
          width={dimensions.width} 
          height={dimensions.height}
          className="border border-gray-200 rounded-lg bg-white shadow-inner w-full h-full"
          onClick={() => setSelectedNode(null)}
        />
      </div>

      {/* Add Node Modal */}
      {showModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded-lg shadow-xl w-96">
            <h2 className="text-xl font-bold mb-4">
              Add New {modalType === 'theme' ? 'Theme' : 'Feature'}
            </h2>

            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-bold mb-2">
                Name
              </label>
              <input 
                type="text" 
                value={newNodeName}
                onChange={(e) => setNewNodeName(e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                placeholder={`Enter ${modalType} name`}
              />
            </div>

            {modalType === 'theme' && (
              <div className="mb-4">
                <label className="block text-gray-700 text-sm font-bold mb-2">
                  Feedback Count
                </label>
                <input 
                  type="number" 
                  value={newNodeFeedbackCount}
                  onChange={(e) => setNewNodeFeedbackCount(parseInt(e.target.value))}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                  min="0"
                />
              </div>
            )}

            {modalType === 'feature' && (
              <div className="mb-4">
                <label className="block text-gray-700 text-sm font-bold mb-2">
                  Priority
                </label>
                <select 
                  value={newNodePriority}
                  onChange={(e) => setNewNodePriority(e.target.value)}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                >
                  <option value="Low">Low</option>
                  <option value="Medium">Medium</option>
                  <option value="High">High</option>
                </select>
              </div>
            )}

            <div className="flex justify-end space-x-2">
              <button 
                className="px-4 py-2 bg-gray-300 text-gray-700 rounded hover:bg-gray-400"
                onClick={() => {
                  setShowModal(false);
                  setNewNodeName('');
                  setNewNodeFeedbackCount(10);
                  setNewNodePriority('Medium');
                }}
              >
                Cancel
              </button>
              <button 
                className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
                onClick={() => {
                  if (!newNodeName.trim()) {
                    alert("Please enter a name");
                    return;
                  }

                  const newNode = {
                    id: `${modalType}-${Date.now()}`,
                    name: newNodeName,
                    type: modalType,
                    ...(modalType === 'theme' ? { feedbackCount: newNodeFeedbackCount } : {}),
                    ...(modalType === 'feature' ? { priority: newNodePriority } : {})
                  };

                  setGraphData({
                    nodes: [...graphData.nodes, newNode],
                    links: [...graphData.links]
                  });

                  setShowModal(false);
                  setNewNodeName('');
                  setNewNodeFeedbackCount(10);
                  setNewNodePriority('Medium');
                }}
              >
                Add
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Connection Modal */}
      {showConnectionModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded-lg shadow-xl w-96">
            <h2 className="text-xl font-bold mb-4">
              Connect Nodes
            </h2>

            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-bold mb-2">
                From
              </label>
              <div className="px-3 py-2 border border-gray-300 rounded-md bg-gray-50">
                {connectionSource?.name} ({connectionSource?.type})
              </div>
            </div>

            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-bold mb-2">
                To
              </label>
              <select 
                value={connectionTarget}
                onChange={(e) => setConnectionTarget(e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              >
                <option value="">Select a target node</option>
                {availableTargets.map((target) => (
                  <option key={target.id} value={target.id}>
                    {target.name} ({target.type})
                  </option>
                ))}
              </select>
            </div>

            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-bold mb-2">
                Connection Strength: {connectionStrength.toFixed(1)}
              </label>
              <input 
                type="range" 
                min="0.1" 
                max="1" 
                step="0.1"
                value={connectionStrength}
                onChange={(e) => setConnectionStrength(parseFloat(e.target.value))}
                className="w-full"
              />
            </div>

            <div className="flex justify-end space-x-2">
              <button 
                className="px-4 py-2 bg-gray-300 text-gray-700 rounded hover:bg-gray-400"
                onClick={() => {
                  setShowConnectionModal(false);
                  setConnectionSource(null);
                  setConnectionTarget('');
                  setConnectionStrength(0.5);
                }}
              >
                Cancel
              </button>
              <button 
                className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
                onClick={() => {
                  if (!connectionTarget) {
                    alert("Please select a target node");
                    return;
                  }

                  // Check if connection already exists
                  const connectionExists = graphData.links.some(link => 
                    (link.source === connectionSource.id && link.target === connectionTarget) ||
                    (link.source === connectionTarget && link.target === connectionSource.id)
                  );

                  if (connectionExists) {
                    alert("This connection already exists");
                    return;
                  }

                  const newLink = {
                    source: connectionSource.id,
                    target: connectionTarget,
                    strength: connectionStrength
                  };

                  setGraphData({
                    nodes: [...graphData.nodes],
                    links: [...graphData.links, newLink]
                  });

                  setShowConnectionModal(false);
                  setConnectionSource(null);
                  setConnectionTarget('');
                  setConnectionStrength(0.5);
                }}
              >
                Connect
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Add Feedback Modal */}
      {showFeedbackModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded-lg shadow-xl w-96">
            <h2 className="text-xl font-bold mb-4">
              Add New Feedback
            </h2>

            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-bold mb-2">
                Feedback Text
              </label>
              <textarea 
                value={newFeedback}
                onChange={(e) => setNewFeedback(e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                placeholder="Enter user feedback"
                rows={4}
              />
            </div>

            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-bold mb-2">
                User Role
              </label>
              <input 
                type="text" 
                value={newFeedbackRole}
                onChange={(e) => setNewFeedbackRole(e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                placeholder="e.g. Mobile User, Admin, Power User"
              />
            </div>

            <div className="flex justify-end space-x-2">
              <button 
                className="px-4 py-2 bg-gray-300 text-gray-700 rounded hover:bg-gray-400"
                onClick={() => {
                  setShowFeedbackModal(false);
                  setNewFeedback('');
                  setNewFeedbackRole('');
                }}
              >
                Cancel
              </button>
              <button 
                className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
                onClick={handleAddFeedback}
                disabled={isAIProcessing}
              >
                {isAIProcessing ? (
                  <div className="flex items-center">
                    <div className="w-4 h-4 border-2 border-white border-t-transparent rounded-full animate-spin mr-2"></div>
                    Processing...
                  </div>
                ) : 'Submit'}
              </button>
            </div>

            {aiError && (
              <div className="mt-4 p-2 bg-red-100 text-red-700 rounded">
                {aiError}
              </div>
            )}
          </div>
        </div>
      )}

      {/* AI Insights Panel */}
      {showAIInsightsPanel && aiInsights && (
        <div 
          className={`fixed right-0 top-0 bottom-0 bg-white shadow-lg z-40 transition-all duration-300`}
          style={{ 
            width: isPanelCollapsed ? '50px' : `${panelWidth}px`,
            maxWidth: '40%' // Limit maximum width to prevent excessive overlap
          }}
        >
          {/* Collapse/Expand Button */}
          <div className="w-10 flex flex-col items-center pt-4 border-r border-gray-200 bg-gray-50">
            <button 
              onClick={togglePanelCollapse}
              className="w-8 h-8 flex items-center justify-center rounded-full bg-white hover:bg-gray-100 text-gray-600 shadow-sm"
              title={isPanelCollapsed ? "Expand panel" : "Collapse panel"}
            >
              <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d={
                  isPanelCollapsed 
                    ? "M13 5l7 7-7 7M5 5l7 7-7 7" // Expand icon
                    : "M11 19l-7-7 7-7M19 19l-7-7 7-7" // Collapse icon
                } />
              </svg>
            </button>

            {isPanelCollapsed && (
              <div className="mt-4 transform -rotate-90 whitespace-nowrap text-sm font-medium text-gray-600 origin-center">
                {aiInsights.isFeatureView ? 'Feature Details' : 
                 aiInsights.persona ? `${aiInsights.persona}` : 
                 aiInsights.theme ? `${aiInsights.theme}` : 'AI Insights'}
              </div>
            )}

            <button 
              onClick={() => setShowAIInsightsPanel(false)}
              className="w-8 h-8 flex items-center justify-center rounded-full bg-white hover:bg-gray-100 text-gray-600 mt-4 shadow-sm"
              title="Close panel"
            >
              <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
              </svg>
            </button>
          </div>

          {/* Panel Content - Only shown when expanded */}
          {!isPanelCollapsed && (
            <div className="flex-1 p-4 overflow-y-auto">
              <div className="flex justify-between items-center mb-4">
                <h2 className="text-lg font-bold">
                  {aiInsights.isFeatureView ? 'Feature Details' : 
                   aiInsights.persona ? `${aiInsights.persona} Feedback` : 
                   aiInsights.theme ? `${aiInsights.theme} Feedback` : 'AI Insights'}
                </h2>
              </div>

              {/* Theme View */}
              {aiInsights.theme && !aiInsights.isFeatureView && !aiInsights.persona && (
                <>
                  <div className="mb-4">
                    <div className="text-sm text-gray-600">Feedback Count</div>
                    <div className="text-lg font-semibold">
                      {aiInsights.feedbackCount > 0 ? aiInsights.feedbackCount : 
                       (aiInsights.results && aiInsights.results.length) || 0}
                    </div>
                  </div>

                  {/* Connected Features */}
                  {aiInsights.connectedFeatures && aiInsights.connectedFeatures.length > 0 && (
                    <div className="mb-4">
                      <div className="text-sm text-gray-600 mb-1">Related Features</div>
                      <div className="space-y-1">
                        {aiInsights.connectedFeatures.map((feature, index) => (
                          <div 
                            key={index} 
                            className="px-2 py-1 bg-green-50 text-green-800 rounded text-sm flex justify-between items-center"
                          >
                            <span>{feature.name}</span>
                            <span className={`text-xs px-1.5 py-0.5 rounded ${
                              feature.priority === 'High' ? 'bg-red-100 text-red-800' :
                              feature.priority === 'Medium' ? 'bg-yellow-100 text-yellow-800' :
                              'bg-green-100 text-green-800'
                            }`}>
                              {feature.priority}
                            </span>
                          </div>
                        ))}
                      </div>
                    </div>
                  )}

                  {/* Connected Personas */}
                  {aiInsights.connectedPersonas && aiInsights.connectedPersonas.length > 0 && (
                    <div className="mb-4">
                      <div className="text-sm text-gray-600 mb-1">Related Personas</div>
                      <div className="space-y-1">
                        {aiInsights.connectedPersonas.map((persona, index) => (
                          <div 
                            key={index} 
                            className="px-2 py-1 bg-orange-50 text-orange-800 rounded text-sm"
                          >
                            {persona.name}
                          </div>
                        ))}
                      </div>
                    </div>
                  )}

                  {/* AI Generated Feature Ideas */}
                  {aiInsights.featureIdeas && aiInsights.featureIdeas.length > 0 && (
                    <div className="mb-4">
                      <div className="text-sm text-gray-600 mb-1">AI Feature Suggestions</div>
                      <div className="space-y-2">
                        {aiInsights.featureIdeas.map((idea, index) => (
                          <div key={index} className="bg-blue-50 p-2 rounded">
                            <div className="font-medium text-blue-800">{idea.name}</div>
                            <div className="text-sm text-blue-600 mt-1">{idea.description}</div>
                          </div>
                        ))}
                      </div>
                    </div>
                  )}
                </>
              )}

              {/* Feature View */}
              {aiInsights.isFeatureView && (
                <>
                  <div className="mb-4 flex justify-between">
                    <div>
                      <div className="text-sm text-gray-600">Priority</div>
                      <div className={`inline-block px-2 py-1 rounded text-xs font-medium ${
                        aiInsights.priority === 'High' ? 'bg-red-100 text-red-800' :
                        aiInsights.priority === 'Medium' ? 'bg-yellow-100 text-yellow-800' :
                        'bg-green-100 text-green-800'
                      }`}>
                        {aiInsights.priority}
                      </div>
                    </div>
                    <div>
                      <div className="text-sm text-gray-600">Feedback Count</div>
                      <div className="text-lg font-semibold text-right">
                        {aiInsights.feedbackCount > 0 ? aiInsights.feedbackCount : 
                         (aiInsights.results && aiInsights.results.length) || 0}
                      </div>
                    </div>
                  </div>

                  {/* Connected Themes */}
                  {aiInsights.connectedThemes && aiInsights.connectedThemes.length > 0 && (
                    <div className="mb-4">
                      <div className="text-sm text-gray-600 mb-1">Related Themes</div>
                      <div className="space-y-1">
                        {aiInsights.connectedThemes.map((theme, index) => (
                          <div 
                            key={index} 
                            className="px-2 py-1 bg-blue-50 text-blue-800 rounded text-sm flex justify-between items-center"
                          >
                            <span>{theme.name}</span>
                            <span className="text-xs text-blue-600">
                              {theme.feedbackCount || 0} feedback
                            </span>
                          </div>
                        ))}
                      </div>
                    </div>
                  )}

                  {/* Connected Personas */}
                  {aiInsights.connectedPersonas && aiInsights.connectedPersonas.length > 0 && (
                    <div className="mb-4">
                      <div className="text-sm text-gray-600 mb-1">Related Personas</div>
                      <div className="space-y-1">
                        {aiInsights.connectedPersonas.map((persona, index) => (
                          <div 
                            key={index} 
                            className="px-2 py-1 bg-orange-50 text-orange-800 rounded text-sm"
                          >
                            {persona.name}
                          </div>
                        ))}
                      </div>
                    </div>
                  )}

                  {/* Implementation Details */}
                  {aiInsights.implementationDetails && (
                    <div className="mb-4">
                      <div className="text-sm text-gray-600 mb-1">Implementation Details</div>
                      <div className="bg-gray-50 p-3 rounded text-sm">
                        {aiInsights.implementationDetails}
                      </div>
                    </div>
                  )}
                </>
              )}

              {/* Persona View */}
              {aiInsights.persona && (
                <>
                  <div className="mb-4">
                    <div className="text-sm text-gray-600">Feedback Count</div>
                    <div className="text-lg font-semibold">
                      {aiInsights.feedbackCount > 0 ? aiInsights.feedbackCount : 
                       (aiInsights.results && aiInsights.results.length) || 0}
                    </div>
                  </div>

                  {/* Connected Themes */}
                  {aiInsights.connectedThemes && aiInsights.connectedThemes.length > 0 && (
                    <div className="mb-4">
                      <div className="text-sm text-gray-600 mb-1">Common Themes</div>
                      <div className="space-y-1">
                        {aiInsights.connectedThemes.map((theme, index) => (
                          <div 
                            key={index} 
                            className="px-2 py-1 bg-blue-50 text-blue-800 rounded text-sm flex justify-between items-center"
                          >
                            <span>{theme.name}</span>
                            <span className="text-xs text-blue-600">
                              {theme.feedbackCount || 0} feedback
                            </span>
                          </div>
                        ))}
                      </div>
                    </div>
                  )}

                  {/* Connected Features */}
                  {aiInsights.connectedFeatures && aiInsights.connectedFeatures.length > 0 && (
                    <div className="mb-4">
                      <div className="text-sm text-gray-600 mb-1">Relevant Features</div>
                      <div className="space-y-1">
                        {aiInsights.connectedFeatures.map((feature, index) => (
                          <div 
                            key={index} 
                            className="px-2 py-1 bg-green-50 text-green-800 rounded text-sm flex justify-between items-center"
                          >
                            <span>{feature.name}</span>
                            <span className={`text-xs px-1.5 py-0.5 rounded ${
                              feature.priority === 'High' ? 'bg-red-100 text-red-800' :
                              feature.priority === 'Medium' ? 'bg-yellow-100 text-yellow-800' :
                              'bg-green-100 text-green-800'
                            }`}>
                              {feature.priority}
                            </span>
                          </div>
                        ))}
                      </div>
                    </div>
                  )}

                  {/* Persona Insights */}
                  {aiInsights.personaInsights && (
                    <div className="mb-4">
                      <div className="text-sm text-gray-600 mb-1">User Insights</div>
                      <div className="bg-orange-50 p-3 rounded text-sm text-orange-800">
                        {aiInsights.personaInsights}
                      </div>
                    </div>
                  )}
                </>
              )}

              {/* Feedback Items */}
              {aiInsights.results && aiInsights.results.length > 0 ? (
                <>
                  <h3 className="font-semibold text-sm text-gray-600 mb-2">
                    {aiInsights.persona ? 'User Feedback' : 'Feedback Classification'}
                  </h3>
                  {aiInsights.results.map((result, index) => (
                    <div key={index} className="bg-gray-50 p-2 rounded mb-2">
                      <div className="text-sm">{result.text}</div>
                      <div className="flex justify-between mt-1 text-xs text-gray-500">
                        <span>{result.user_role}</span>
                        <span>{result.theme}</span>
                      </div>
                    </div>
                  ))}
                </>
              ) : (
                <div className="text-gray-500 italic">No feedback data available</div>
              )}
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default UserResearchNetwork;
