// UserResearchNetwork.js
import React, { useState, useEffect, useRef, useCallback, useMemo } from 'react';
import * as d3 from 'd3';
import { classifyFeedback, generateFeatureIdeas } from '../services/openaiService';

// Color scale for node types
const colorScale = d3.scaleOrdinal()
  .domain(["theme", "feature", "persona"])
  .range(["#3182CE", "#38A169", "#DD6B20"]);

// Initial data structure
const initialData = {
  nodes: [
    { id: "theme1", name: "Performance", type: "theme", feedbackCount: 10 },
    { id: "theme2", name: "User Interface", type: "theme", feedbackCount: 8 },
    { id: "theme3", name: "Mobile Experience", type: "theme", feedbackCount: 6 },
    { id: "persona1", name: "Data Analyst", type: "persona" },
    { id: "persona2", name: "Marketing Manager", type: "persona" },
    { id: "feature1", name: "Performance Optimization", type: "feature", priority: "High" }
  ],
  links: [
    { source: "theme1", target: "persona1", strength: 0.7 },
    { source: "theme2", target: "persona1", strength: 0.5 },
    { source: "theme1", target: "feature1", strength: 0.8 },
    { source: "persona1", target: "feature1", strength: 0.6 }
  ]
};

// Main component
const UserResearchNetwork = () => {
  // Refs
  const svgRef = useRef(null);
  const containerRef = useRef(null);
  const zoomRef = useRef(null);
  const simulationRef = useRef(null);

  // State
  const [dimensions, setDimensions] = useState({ width: 800, height: 600 });
  const [graphData, setGraphData] = useState(initialData);
  const [isSimulationRunning, setIsSimulationRunning] = useState(true);
  const [selectedNode, setSelectedNode] = useState(null);
  const [highlightMode, setHighlightMode] = useState('none');
  const [feedbackWeight, setFeedbackWeight] = useState(5);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Modal states
  const [showModal, setShowModal] = useState(false);
  const [modalType, setModalType] = useState(''); // 'theme' or 'feature'
  const [showConnectionModal, setShowConnectionModal] = useState(false);
  const [showFeedbackModal, setShowFeedbackModal] = useState(false);

  // Node form state
  const [newNodeName, setNewNodeName] = useState('');
  const [newNodeFeedbackCount, setNewNodeFeedbackCount] = useState(10);
  const [newNodePriority, setNewNodePriority] = useState('Medium');

  // Connection state
  const [connectionSource, setConnectionSource] = useState(null);
  const [connectionTarget, setConnectionTarget] = useState('');
  const [connectionStrength, setConnectionStrength] = useState(0.5);
  const [availableTargets, setAvailableTargets] = useState([]);
  const [multipleConnections, setMultipleConnections] = useState([]);

  // Minimap state
  const [showMiniMap, setShowMiniMap] = useState(true);

  // Search state
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [showSearchResults, setShowSearchResults] = useState(false);

  // AI state
  const [isAIProcessing, setIsAIProcessing] = useState(false);
  const [aiError, setAIError] = useState(null);
  const [feedbackItems, setFeedbackItems] = useState([]);
  const [showAIInsightsPanel, setShowAIInsightsPanel] = useState(false);
  const [aiInsights, setAIInsights] = useState(null);
  const [isPanelCollapsed, setIsPanelCollapsed] = useState(false);
  const [panelWidth, setPanelWidth] = useState(320);

  // New feedback state
  const [newFeedback, setNewFeedback] = useState('');
  const [newFeedbackRole, setNewFeedbackRole] = useState('');

  // Simulate loading data on initial render
  useEffect(() => {
    const timer = setTimeout(() => {
      setLoading(false);
    }, 1000);
    return () => clearTimeout(timer);
  }, []);

  // Update dimensions when window resizes
  useEffect(() => {
    if (!containerRef.current) return;

    const updateDimensions = () => {
      if (containerRef.current) {
        const { width, height } = containerRef.current.getBoundingClientRect();
        setDimensions({ width, height });
      }
    };

    const resizeObserver = new ResizeObserver(entries => {
      for (let entry of entries) {
        const { width, height } = entry.contentRect;
        setDimensions({ width, height });
      }
    });

    resizeObserver.observe(containerRef.current);
    updateDimensions();

    return () => {
      window.removeEventListener('resize', updateDimensions);
      resizeObserver.disconnect();
    };
  }, []);

  // Adjust container dimensions when panel is open
  useEffect(() => {
    if (containerRef.current) {
      const containerWidth = containerRef.current.clientWidth;
      const containerHeight = containerRef.current.clientHeight;

      // Adjust dimensions based on panel state
      if (showAIInsightsPanel && !isPanelCollapsed) {
        // Reduce width to make room for the panel
        setDimensions({
          width: containerWidth - panelWidth,
          height: containerHeight
        });
      } else {
        // Use full width
        setDimensions({
          width: containerWidth,
          height: containerHeight
        });
      }
    }
  }, [showAIInsightsPanel, isPanelCollapsed, panelWidth]);

  // Recenter view when panel state changes
  useEffect(() => {
    const timer = setTimeout(() => {
      centerView();
    }, 300);

    return () => clearTimeout(timer);
  }, [showAIInsightsPanel, isPanelCollapsed]);

  // Recalculate feedback counts when feedback items change
  useEffect(() => {
    if (feedbackItems.length > 0) {
      recalculateFeedbackCounts();
    }
  }, [feedbackItems]);

  // Create a minimap when needed
  useEffect(() => {
    if (showMiniMap && svgRef.current && dimensions.width > 0) {
      const cleanupMiniMap = createMiniMap();
      return cleanupMiniMap;
    }
  }, [showMiniMap, graphData, dimensions.width, dimensions.height]);

  // Main visualization rendering effect
  useEffect(() => {
    if (!svgRef.current || !dimensions.width || !dimensions.height) return;

    console.log("Rendering with data:", graphData);

    // Clear the SVG
    const svg = d3.select(svgRef.current);
    svg.selectAll("*").remove();

    // Create a container group for all elements
    const g = svg.append("g");

    // Set up zoom behavior
    const zoom = d3.zoom()
      .scaleExtent([0.1, 4])
      .on("zoom", (event) => {
        g.attr("transform", event.transform);
      });

    svg.call(zoom);
    zoomRef.current = zoom;

    // Verify we have valid data
    if (!graphData.nodes || !graphData.links || 
        !Array.isArray(graphData.nodes) || !Array.isArray(graphData.links) ||
        graphData.nodes.length === 0) {
      console.error("Invalid or empty graph data:", graphData);
      return;
    }

    // Create working copies of the data with proper link references
    const nodes = graphData.nodes.map(node => ({...node}));

    // Normalize links to ensure proper format for D3
    const normalizedLinks = graphData.links.map(link => {
      // Extract source and target IDs consistently
      const sourceId = typeof link.source === 'object' ? link.source.id : link.source;
      const targetId = typeof link.target === 'object' ? link.target.id : link.target;

      // Return a new link object with just IDs (D3 will convert to objects)
      return {
        source: sourceId,
        target: targetId,
        strength: link.strength || 0.5
      };
    });

    // Verify all links have valid source and target nodes
    const validLinks = normalizedLinks.filter(link => {
      const sourceExists = nodes.some(node => node.id === link.source);
      const targetExists = nodes.some(node => node.id === link.target);
      if (!sourceExists) console.warn(`Link has invalid source: ${link.source}`);
      if (!targetExists) console.warn(`Link has invalid target: ${link.target}`);
      return sourceExists && targetExists;
    });

    console.log(`Found ${validLinks.length} valid links out of ${normalizedLinks.length}`);

    // Create the force simulation with strong link forces
    const simulation = d3.forceSimulation(nodes)
      .force("link", d3.forceLink(validLinks)
        .id(d => d.id)
        .distance(100)
        .strength(1)) // Stronger link force for better connections
      .force("charge", d3.forceManyBody().strength(-300))
      .force("center", d3.forceCenter(dimensions.width / 2, dimensions.height / 2))
      .force("collide", d3.forceCollide(30));

    simulationRef.current = simulation;

    if (!isSimulationRunning) {
      simulation.stop();
    }

    // Create the links
    const link = g.append("g")
      .attr("class", "links")
      .selectAll("line")
      .data(validLinks)
      .enter()
      .append("line")
      .attr("stroke", d => {
        // Get the source and target nodes
        const source = nodes.find(n => n.id === d.source);
        const target = nodes.find(n => n.id === d.target);

        if (!source || !target) return "#999";

        const sourceType = source.type;
        const targetType = target.type;

        if (sourceType === "theme" && targetType === "feature") return "#90CDF4"; // Light blue
        if (sourceType === "feature" && targetType === "theme") return "#90CDF4"; // Light blue
        if (sourceType === "theme" && targetType === "persona") return "#FBD38D"; // Light orange
        if (sourceType === "persona" && targetType === "theme") return "#FBD38D"; // Light orange
        if (sourceType === "persona" && targetType === "feature") return "#9AE6B4"; // Light green
        if (sourceType === "feature" && targetType === "persona") return "#9AE6B4"; // Light green

        return "#999"; // Default gray
      })
      .attr("stroke-opacity", 0.6)
      .attr("stroke-width", d => Math.max(2, d.strength * 6)); // Thicker lines for visibility

    // Create the nodes
    const node = g.append("g")
      .attr("class", "nodes")
      .selectAll("g")
      .data(nodes)
      .enter()
      .append("g")
      .call(d3.drag()
        .on("start", dragstarted)
        .on("drag", dragged)
        .on("end", dragended))
      .on("click", (event, d) => {
        event.stopPropagation();

        if (selectedNode && selectedNode.id === d.id) {
          // If clicking the same node again, release it
          d.fx = null;
          d.fy = null;
          setSelectedNode(null);
        } else {
          // If clicking a new node, fix its position
          d.fx = d.x;
          d.fy = d.y;
          setSelectedNode(d);
          handleNodeClick(d);
        }
      });

    // Add circles to nodes
    node.append("circle")
      .attr("r", d => {
        if (d.type === "theme") return 20 + (d.feedbackCount / feedbackWeight);
        if (d.type === "feature") return d.priority === "High" ? 22 : 20;
        return 18;
      })
      .attr("fill", d => colorScale(d.type))
      .attr("stroke", "#fff")
      .attr("stroke-width", 2);

    // Add labels to nodes
    node.append("text")
      .text(d => d.name)
      .attr("x", 0)
      .attr("y", d => {
        if (d.type === "theme") return 30 + (d.feedbackCount / feedbackWeight);
        if (d.type === "feature") return d.priority === "High" ? 32 : 30;
        return 28;
      })
      .attr("text-anchor", "middle")
      .attr("font-size", "12px")
      .attr("fill", "#333");

    // Add title for hover effect
    node.append("title")
      .text(d => d.name);

    // Run a few ticks before adding listener to get nodes in better positions
    for (let i = 0; i < 20; i++) {
      simulation.tick();
    }

    // Update link positions immediately with node positions from simulation ticks
    link
      .attr("x1", d => {
        const source = nodes.find(n => n.id === d.source);
        return source ? source.x : 0;
      })
      .attr("y1", d => {
        const source = nodes.find(n => n.id === d.source);
        return source ? source.y : 0;
      })
      .attr("x2", d => {
        const target = nodes.find(n => n.id === d.target);
        return target ? target.x : 0;
      })
      .attr("y2", d => {
        const target = nodes.find(n => n.id === d.target);
        return target ? target.y : 0;
      });

    // Now add the tick listener for ongoing updates
    simulation.on("tick", () => {
      link
        .attr("x1", d => {
          // Handle both ID and object references
          const sourceId = typeof d.source === 'object' ? d.source.id : d.source;
          const source = nodes.find(n => n.id === sourceId);
          return source ? source.x : 0;
        })
        .attr("y1", d => {
          const sourceId = typeof d.source === 'object' ? d.source.id : d.source;
          const source = nodes.find(n => n.id === sourceId);
          return source ? source.y : 0;
        })
        .attr("x2", d => {
          const targetId = typeof d.target === 'object' ? d.target.id : d.target;
          const target = nodes.find(n => n.id === targetId);
          return target ? target.x : 0;
        })
        .attr("y2", d => {
          const targetId = typeof d.target === 'object' ? d.target.id : d.target;
          const target = nodes.find(n => n.id === targetId);
          return target ? target.y : 0;
        });

      node.attr("transform", d => `translate(${d.x}, ${d.y})`);
    });

    // Drag functions
    function dragstarted(event, d) {
      if (!event.active) simulation.alphaTarget(0.3).restart();
      d.fx = d.x;
      d.fy = d.y;
    }

    function dragged(event, d) {
      d.fx = event.x;
      d.fy = event.y;
    }

    function dragended(event, d) {
      if (!event.active) simulation.alphaTarget(0);

      // Only release the node if it's not the selected node
      if (!selectedNode || selectedNode.id !== d.id) {
        d.fx = null;
        d.fy = null;
      }
    }

    // Center the view
    svg.call(zoom.transform, d3.zoomIdentity.translate(
      dimensions.width / 2 - dimensions.width * 0.5,
      dimensions.height / 2 - dimensions.height * 0.5
    ).scale(0.8));

    // Clean up on unmount
    return () => {
      simulation.stop();
    };
  }, [dimensions, graphData, feedbackWeight, highlightMode, selectedNode, isSimulationRunning]);

  // MiniMap implementation
  const createMiniMap = useCallback(() => {
    if (!svgRef.current) return () => {};

    const svg = d3.select(svgRef.current);

    // Remove any existing mini-map
    svg.selectAll(".mini-map-container").remove();

    // Calculate mini-map size based on the main SVG dimensions
    const miniMapSize = Math.min(dimensions.width, dimensions.height) * 0.25;
    const miniMapMargin = 20;

    // Create a container for the mini-map
    const miniMapContainer = svg.append("g")
      .attr("class", "mini-map-container")
      .attr("transform", `translate(${miniMapMargin}, ${miniMapMargin})`);

    // Add a background rectangle
    miniMapContainer.append("rect")
      .attr("width", miniMapSize)
      .attr("height", miniMapSize)
      .attr("fill", "#f0f4f8")
      .attr("fill-opacity", 0.9)
      .attr("stroke", "#3182CE")
      .attr("stroke-width", 2)
      .attr("rx", 6);

    // Create a separate mini-map simulation
    const miniMapNodes = graphData.nodes.map(node => ({
      ...node,
      miniX: Math.random() * miniMapSize,
      miniY: Math.random() * miniMapSize
    }));

    const miniMapLinks = graphData.links.map(link => ({
      source: typeof link.source === 'object' ? link.source.id : link.source,
      target: typeof link.target === 'object' ? link.target.id : link.target
    }));

    // Create a separate force simulation just for the mini-map
    const miniMapSimulation = d3.forceSimulation(miniMapNodes)
      .force("link", d3.forceLink(miniMapLinks).id(d => d.id).distance(5))
      .force("charge", d3.forceManyBody().strength(-20))
      .force("center", d3.forceCenter(miniMapSize / 2, miniMapSize / 2))
      .force("collide", d3.forceCollide().radius(4));

    // Add mini-map links
    miniMapContainer.selectAll(".mini-link")
      .data(miniMapLinks)
      .enter()
      .append("line")
      .attr("class", "mini-link")
      .attr("stroke", "#ccc")
      .attr("stroke-width", 0.5);

    // Add mini-map nodes
    const miniNodes = miniMapContainer.selectAll(".mini-node")
      .data(miniMapNodes)
      .enter()
      .append("circle")
      .attr("class", "mini-node")
      .attr("r", 3)
      .attr("fill", d => {
        // Use the same color scale but with higher opacity
        const baseColor = colorScale(d.type);
        return d3.color(baseColor).brighter(0.3);
      })
      .attr("stroke", "#fff")
      .attr("stroke-width", 1);

    // Add a label
    miniMapContainer.append("text")
      .attr("x", 10)
      .attr("y", 15)
      .attr("font-size", "12px")
      .attr("font-weight", "bold")
      .attr("fill", "#2D3748")
      .text("Network Overview");

    // Add a border to show the current viewport
    const viewportRect = miniMapContainer.append("rect")
      .attr("class", "mini-map-viewport")
      .attr("width", miniMapSize)
      .attr("height", miniMapSize)
      .attr("fill", "none")
      .attr("stroke", "#3182ce")
      .attr("stroke-width", 2)
      .attr("stroke-dasharray", "4,4");

    // Run the mini-map simulation for a fixed number of ticks
    miniMapSimulation.stop();
    for (let i = 0; i < 300; i++) {
      miniMapSimulation.tick();
    }

    // Update the mini-map nodes with their final positions
    miniMapContainer.selectAll(".mini-link")
      .attr("x1", d => {
        const source = miniMapNodes.find(n => n.id === d.source);
        return source ? source.miniX : 0;
      })
      .attr("y1", d => {
        const source = miniMapNodes.find(n => n.id === d.source);
        return source ? source.miniY : 0;
      })
      .attr("x2", d => {
        const target = miniMapNodes.find(n => n.id === d.target);
        return target ? target.miniX : 0;
      })
      .attr("y2", d => {
        const target = miniMapNodes.find(n => n.id === d.target);
        return target ? target.miniY : 0;
      });

    miniNodes
      .attr("cx", d => d.miniX)
      .attr("cy", d => d.miniY);

    // Add click behavior
    miniMapContainer.append("rect")
      .attr("width", miniMapSize)
      .attr("height", miniMapSize)
      .attr("fill", "transparent")
      .attr("stroke", "none")
      .style("cursor", "pointer")
      .on("click", (event) => {
        if (!zoomRef.current) return;

        // Get click position relative to mini-map
        const [x, y] = d3.pointer(event);

        // Calculate the center point in the main view
        const centerX = (x / miniMapSize) * dimensions.width;
        const centerY = (y / miniMapSize) * dimensions.height;

        // Create a transform that centers on the clicked point
        const transform = d3.zoomIdentity
          .translate(dimensions.width / 2, dimensions.height / 2)
          .scale(1)
          .translate(-centerX, -centerY);

        // Apply the transform with a smooth transition
        svg.transition()
          .duration(750)
          .call(zoomRef.current.transform, transform);
      });

    // Return cleanup function
    return () => {
      svg.selectAll(".mini-map-container").remove();
      if (miniMapSimulation) miniMapSimulation.stop();
    };
  }, [dimensions, graphData]);

  // Recalculate feedback counts based on graph connections
  const recalculateFeedbackCounts = useCallback(() => {
    // Create a map to track feedback counts
    const feedbackCounts = {
      themes: {},
      personas: {},
      features: {}
    };

    // Count feedback for each node type based on connections
    graphData.links.forEach(link => {
      const sourceId = typeof link.source === 'object' ? link.source.id : link.source;
      const targetId = typeof link.target === 'object' ? link.target.id : link.target;

      const sourceNode = graphData.nodes.find(n => n.id === sourceId);
      const targetNode = graphData.nodes.find(n => n.id === targetId);

      if (!sourceNode || !targetNode) return;

      // Count theme connections
      if (sourceNode.type === "theme" && targetNode.type === "feedback") {
        feedbackCounts.themes[sourceNode.id] = (feedbackCounts.themes[sourceNode.id] || 0) + 1;
      }
      else if (targetNode.type === "theme" && sourceNode.type === "feedback") {
        feedbackCounts.themes[targetNode.id] = (feedbackCounts.themes[targetNode.id] || 0) + 1;
      }

      // Count persona connections
      if (sourceNode.type === "persona" && targetNode.type === "feedback") {
        feedbackCounts.personas[sourceNode.id] = (feedbackCounts.personas[sourceNode.id] || 0) + 1;
      }
      else if (targetNode.type === "persona" && sourceNode.type === "feedback") {
        feedbackCounts.personas[targetNode.id] = (feedbackCounts.personas[targetNode.id] || 0) + 1;
      }
    });

    // Update node feedback counts
    const updatedNodes = graphData.nodes.map(node => {
      if (node.type === "theme" && feedbackCounts.themes[node.id]) {
        return { ...node, feedbackCount: feedbackCounts.themes[node.id] };
      }
      if (node.type === "persona" && feedbackCounts.personas[node.id]) {
        return { ...node, feedbackCount: feedbackCounts.personas[node.id] };
      }
      return node;
    });

    // Update the graph data
    setGraphData(prevData => ({
      ...prevData,
      nodes: updatedNodes
    }));
  }, [graphData]);

  // Handle import of data
  const importData = useCallback((event) => {
    const file = event.target.files[0];
    if (!file) return;

    setLoading(true);

    const reader = new FileReader();
    reader.onload = (e) => {
      try {
        const importedData = JSON.parse(e.target.result);
        console.log("Imported data:", importedData);

        // Check if it's the simple feedback format
        if (importedData.feedback && Array.isArray(importedData.feedback)) {
          setFeedbackItems(importedData.feedback);

          // Transform the simple format to nodes/links format
          transformFeedbackToGraph(importedData.feedback);
        } 
        // Check if it's the original format with nodes and links
        else if (importedData.nodes && importedData.links) {
          // Normalize and validate the data
          const validNodes = importedData.nodes.map(node => ({
            id: node.id || `node-${Date.now()}-${Math.random().toString(36).substring(2, 9)}`,
            name: node.name || 'Unnamed Node',
            type: node.type || 'theme',
            feedbackCount: node.feedbackCount || 0,
            priority: node.priority || 'Medium',
            ...node // preserve other properties
          }));

          // Create a map of node IDs for quick lookups
          const nodeMap = {};
          validNodes.forEach(node => {
            nodeMap[node.id] = true;
          });

          // Normalize links and ensure they reference valid nodes
          const validLinks = importedData.links
            .map(link => {
              // Extract source and target consistently
              const sourceId = typeof link.source === 'object' ? link.source.id : link.source;
              const targetId = typeof link.target === 'object' ? link.target.id : link.target;

              // Validate sourceId and targetId
              if (!sourceId || !targetId) {
                console.warn('Link missing source or target:', link);
                return null;
              }

              // Create a normalized link
              return {
                source: sourceId,
                target: targetId,
                strength: link.strength || 0.5
              };
            })
            .filter(link => link !== null && nodeMap[link.source] && nodeMap[link.target]);

          console.log(`Found ${validLinks.length} valid links out of ${importedData.links.length}`);

          // Set the validated graph data
          setGraphData({
            nodes: validNodes,
            links: validLinks
          });

          // Wait a moment for D3 to process the data
          setTimeout(() => {
            setLoading(false);

            // Pre-compute positions for better layout
            if (simulationRef.current) {
              simulationRef.current.stop();

              // Create a temporary simulation to calculate positions
              const tempSimulation = d3.forceSimulation(validNodes)
                .force("link", d3.forceLink(validLinks).id(d => d.id).distance(100))
                .force("charge", d3.forceManyBody().strength(-300))
                .force("center", d3.forceCenter(dimensions.width/2, dimensions.height/2))
                .force("collide", d3.forceCollide(40));

              // Run 100 ticks synchronously to get a decent layout
              for (let i = 0; i < 100; i++) {
                tempSimulation.tick();
              }

              tempSimulation.stop();
            }
          }, 100);
        } 
        // Invalid format
        else {
          setError(
            "Invalid data format. The file should contain either:" +
            "\n1. A 'feedback' array with user feedback items, or" +
            "\n2. 'nodes' and 'links' arrays for direct graph data"
          );
          setLoading(false);
        }
      } catch (err) {
        console.error("Error parsing the file:", err);
        setError("Error parsing the file. Please make sure it's a valid JSON file.");
        setLoading(false);
      }
    };
    reader.readAsText(file);
  }, [dimensions]);

  // Transform feedback items to graph structure
  const transformFeedbackToGraph = useCallback(async (feedbackItems) => {
    if (!feedbackItems || feedbackItems.length === 0) {
      setLoading(false);
      return;
    }

    try {
      // Start with an empty graph
      const newGraph = {
        nodes: [],
        links: []
      };

      // Maps to track created nodes
      const nodeMap = {
        personas: {},
        themes: {},
        features: {}
      };

      // First, create persona nodes
      for (const item of feedbackItems) {
        if (!item.user_role) continue;

        if (!nodeMap.personas[item.user_role]) {
          const personaId = `persona-${Date.now()}-${Object.keys(nodeMap.personas).length}`;
          const personaNode = {
            id: personaId,
            name: item.user_role,
            type: "persona",
            feedbackCount: 1
          };

          newGraph.nodes.push(personaNode);
          nodeMap.personas[item.user_role] = personaNode;
        } else {
          // Increment existing persona's feedback count
          nodeMap.personas[item.user_role].feedbackCount += 1;
        }
      }

      // Process each feedback to extract themes
      for (const item of feedbackItems) {
        if (!item.text || !item.user_role) continue;

        try {
          // Get theme for this feedback
          const result = await classifyFeedback(item.text);

          if (result && result.theme) {
            // Create or update theme node
            if (!nodeMap.themes[result.theme]) {
              const themeId = `theme-${Date.now()}-${Object.keys(nodeMap.themes).length}`;
              const themeNode = {
                id: themeId,
                name: result.theme,
                type: "theme",
                feedbackCount: 1
              };

              newGraph.nodes.push(themeNode);
              nodeMap.themes[result.theme] = themeNode;
            } else {
              // Increment existing theme's feedback count
              nodeMap.themes[result.theme].feedbackCount += 1;
            }

            // Create link between persona and theme
            const personaNode = nodeMap.personas[item.user_role];
            const themeNode = nodeMap.themes[result.theme];

            if (personaNode && themeNode) {
              const linkExists = newGraph.links.some(link => 
                (link.source === personaNode.id && link.target === themeNode.id) ||
                (link.source === themeNode.id && link.target === personaNode.id)
              );

              if (!linkExists) {
                newGraph.links.push({
                  source: personaNode.id,
                  target: themeNode.id,
                  strength: 0.7
                });
              }
            }
          }
        } catch (innerError) {
          console.error("Error processing feedback item:", innerError);
        }
      }

      // Generate feature suggestions for top themes
      const significantThemes = Object.values(nodeMap.themes)
        .filter(theme => theme.feedbackCount >= 2)
        .sort((a, b) => b.feedbackCount - a.feedbackCount)
        .slice(0, 3);

      if (significantThemes.length > 0) {
        try {
          // Generate feature ideas based on themes
          for (const theme of significantThemes) {
            const featureId = `feature-${Date.now()}-${Object.keys(nodeMap.features).length}`;
            const featureNode = {
              id: featureId,
              name: `${theme.name} Improvement`,
              type: "feature",
              priority: theme.feedbackCount >= 4 ? "High" : "Medium"
            };

            newGraph.nodes.push(featureNode);
            nodeMap.features[featureNode.name] = featureNode;

            // Link feature to theme
            newGraph.links.push({
              source: theme.id,
              target: featureId,
              strength: 0.8
            });

            // Link feature to personas that gave feedback on this theme
            const relatedPersonas = Object.values(nodeMap.personas).filter(persona => {
              return newGraph.links.some(link => 
                (link.source === persona.id && link.target === theme.id) ||
                (link.source === theme.id && link.target === persona.id)
              );
            });

            for (const persona of relatedPersonas) {
              newGraph.links.push({
                source: persona.id,
                target: featureId,
                strength: 0.6
              });
            }
          }
        } catch (error) {
          console.error("Error generating features:", error);
        }
      }

      // Update graph data and finish loading
      setGraphData(newGraph);
      setLoading(false);

    } catch (error) {
      console.error("Error transforming feedback to graph:", error);
      setError("Error processing feedback. Please try again.");
      setLoading(false);
    }
  }, []);

  // Add a new node (theme or feature)
  const handleAddNode = useCallback(() => {
    if (!newNodeName.trim()) {
      alert("Please enter a node name");
      return;
    }

    let newNode;

    if (modalType === 'theme') {
      newNode = {
        id: `theme-${Date.now()}`,
        name: newNodeName,
        type: "theme",
        feedbackCount: newNodeFeedbackCount
      };
    } else if (modalType === 'feature') {
      newNode = {
        id: `feature-${Date.now()}`,
        name: newNodeName,
        type: "feature",
        priority: newNodePriority
      };
    }

    if (newNode) {
      setGraphData(prevData => ({
        nodes: [...prevData.nodes, newNode],
        links: [...prevData.links]
      }));

      setShowModal(false);
      setNewNodeName('');
      setNewNodeFeedbackCount(10);
      setNewNodePriority('Medium');
    }
  }, [modalType, newNodeName, newNodeFeedbackCount, newNodePriority]);

  // Handle node selection and display node details
  const handleNodeClick = useCallback((node) => {
    if (!node) return;

    // Find connected nodes
    const connectedLinks = graphData.links.filter(link => {
      const sourceId = typeof link.source === 'object' ? link.source.id : link.source;
      const targetId = typeof link.target === 'object' ? link.target.id : link.target;
      return sourceId === node.id || targetId === node.id;
    });

    const connectedNodeIds = connectedLinks.map(link => {
      const sourceId = typeof link.source === 'object' ? link.source.id : link.source;
      const targetId = typeof link.target === 'object' ? link.target.id : link.target;
      return sourceId === node.id ? targetId : sourceId;
    });

    const connectedNodes = graphData.nodes.filter(n => connectedNodeIds.includes(n.id));

    // Prepare insights
    let insights = {
      results: []
    };

    if (node.type === "theme") {
      // Theme view
      insights.theme = node.name;
      insights.feedbackCount = node.feedbackCount || 0;

      // Connected features
      const connectedFeatures = connectedNodes.filter(n => n.type === "feature");
      if (connectedFeatures.length > 0) {
        insights.connectedFeatures = connectedFeatures;
      }

      // Connected personas
      const connectedPersonas = connectedNodes.filter(n => n.type === "persona");
      if (connectedPersonas.length > 0) {
        insights.connectedPersonas = connectedPersonas;
      }
    } else if (node.type === "feature") {
      // Feature view
      insights.isFeatureView = true;
      insights.name = node.name;
      insights.priority = node.priority || "Medium";

      // Connected themes
      const connectedThemes = connectedNodes.filter(n => n.type === "theme");
      if (connectedThemes.length > 0) {
        insights.connectedThemes = connectedThemes;
      }

      // Connected personas
      const connectedPersonas = connectedNodes.filter(n => n.type === "persona");
      if (connectedPersonas.length > 0) {
        insights.connectedPersonas = connectedPersonas;
      }
    } else if (node.type === "persona") {
      // Persona view
      insights.persona = node.name;
      insights.feedbackCount = node.feedbackCount || 0;

      // Connected themes
      const connectedThemes = connectedNodes.filter(n => n.type === "theme");
      if (connectedThemes.length > 0) {
        insights.connectedThemes = connectedThemes;
      }

      // Connected features
      const connectedFeatures = connectedNodes.filter(n => n.type === "feature");
      if (connectedFeatures.length > 0) {
        insights.connectedFeatures = connectedFeatures;
      }

      // Find feedback from this persona
      if (feedbackItems.length > 0) {
        const personaFeedback = feedbackItems.filter(item => 
          item.user_role === node.name
        );

        if (personaFeedback.length > 0) {
          insights.results = personaFeedback.map((item, index) => ({
            feedbackIndex: index,
            text: item.text,
            user_role: item.user_role
          }));
        }
      }
    }

    // Update insights and show panel
    setAIInsights(insights);
    setShowAIInsightsPanel(true);
  }, [graphData, feedbackItems]);

  // Create a connection between nodes
  const handleConnectionSubmit = useCallback(() => {
    if (!connectionSource || !connectionTarget) return;

    // Check if connection already exists
    const existingConnection = graphData.links.some(link => {
      const sourceId = typeof link.source === 'object' ? link.source.id : link.source;
      const targetId = typeof link.target === 'object' ? link.target.id : link.target;

      return (sourceId === connectionSource.id && targetId === connectionTarget) || 
             (sourceId === connectionTarget && targetId === connectionSource.id);
    });

    if (!existingConnection) {
      const newLink = {
        source: connectionSource.id,
        target: connectionTarget,
        strength: connectionStrength
      };

      setGraphData(prevData => ({
        ...prevData,
        links: [...prevData.links, newLink]
      }));
    }

    setShowConnectionModal(false);
    setConnectionSource(null);
    setConnectionTarget('');
    setConnectionStrength(0.5);
  }, [connectionSource, connectionTarget, connectionStrength, graphData]);

  // Initialize connection creation
  const connectNodes = useCallback(() => {
    if (!selectedNode) return;

    // Find suitable targets (different type than selected node)
    const targets = graphData.nodes.filter(n => n.id !== selectedNode.id && n.type !== selectedNode.type);

    if (targets.length === 0) {
      alert("No compatible nodes found to connect with.");
      return;
    }

    setConnectionSource(selectedNode);
    setAvailableTargets(targets);
    setConnectionTarget(targets[0].id);
    setConnectionStrength(0.5);
    setShowConnectionModal(true);
  }, [selectedNode, graphData]);

  // Export the current graph data
  const exportData = useCallback(() => {
    const dataStr = JSON.stringify(graphData, null, 2);
    const dataUri = 'data:application/json;charset=utf-8,'+ encodeURIComponent(dataStr);

    const exportFileDefaultName = 'feedback-insights-data.json';

    const linkElement = document.createElement('a');
    linkElement.setAttribute('href', dataUri);
    linkElement.setAttribute('download', exportFileDefaultName);
    linkElement.click();
  }, [graphData]);

  // Reset to initial data
  const resetVisualization = useCallback(() => {
    if (window.confirm("Are you sure you want to reset? This will remove all custom data.")) {
      setGraphData(initialData);
      setSelectedNode(null);
      setHighlightMode('none');
    }
  }, []);

  // Handle new feedback submission
  const handleAddFeedback = useCallback(async () => {
    if (!newFeedback || !newFeedbackRole) {
      setError("Please provide both feedback text and user role");
      return;
    }

    // Create a new feedback item
    const newFeedbackItem = {
      id: `feedback-${Date.now()}`,
      text: newFeedback,
      user_role: newFeedbackRole,
      timestamp: new Date().toISOString()
    };

    // Add to feedback items
    setFeedbackItems(prev => [...prev, newFeedbackItem]);

    // Process with AI
    setIsAIProcessing(true);
    try {
      const result = await classifyFeedback(newFeedback);

      if (result && result.theme) {
        // Process the feedback and update the graph

        // Check if the persona exists
        let personaNode = graphData.nodes.find(node => 
          node.type === "persona" && node.name === newFeedbackRole
        );

        if (!personaNode) {
          // Create a new persona node
          const personaId = `persona-${Date.now()}`;
          personaNode = {
            id: personaId,
            name: newFeedbackRole,
            type: "persona",
            feedbackCount: 1
          };

          // Add persona to graph
          setGraphData(prev => ({
            nodes: [...prev.nodes, personaNode],
            links: [...prev.links]
          }));
        } else {
          // Update persona feedback count
          setGraphData(prev => ({
            nodes: prev.nodes.map(node => 
              node.id === personaNode.id 
                ? {...node, feedbackCount: (node.feedbackCount || 0) + 1}
                : node
            ),
            links: [...prev.links]
          }));
        }

        // Check if theme exists
        let themeNode = graphData.nodes.find(node => 
          node.type === "theme" && node.name === result.theme
        );

        if (!themeNode) {
          // Create a new theme node
          const themeId = `theme-${Date.now()}`;
          themeNode = {
            id: themeId,
            name: result.theme,
            type: "theme",
            feedbackCount: 1
          };

          // Add theme to graph
          setGraphData(prev => ({
            nodes: [...prev.nodes, themeNode],
            links: [
              ...prev.links,
              // Add link between persona and theme
              {
                source: personaNode.id,
                target: themeId,
                strength: 0.7
              }
            ]
          }));
        } else {
          // Update theme and check for link
          const link = graphData.links.find(link => {
            const sourceId = typeof link.source === 'object' ? link.source.id : link.source;
            const targetId = typeof link.target === 'object' ? link.target.id : link.target;

            return (sourceId === personaNode.id && targetId === themeNode.id) ||
                   (sourceId === themeNode.id && targetId === personaNode.id);
          });

          if (!link) {
            // Create link if it doesn't exist
            setGraphData(prev => ({
              nodes: prev.nodes.map(node => 
                node.id === themeNode.id 
                  ? {...node, feedbackCount: (node.feedbackCount || 0) + 1}
                  : node
              ),
              links: [
                ...prev.links,
                {
                  source: personaNode.id,
                  target: themeNode.id,
                  strength: 0.7
                }
              ]
            }));
          } else {
            // Just update the theme
            setGraphData(prev => ({
              nodes: prev.nodes.map(node => 
                node.id === themeNode.id 
                  ? {...node, feedbackCount: (node.feedbackCount || 0) + 1}
                  : node
              ),
              links: [...prev.links]
            }));
          }
        }
      }

      // Reset form
      setNewFeedback('');
      setNewFeedbackRole('');
      setShowFeedbackModal(false);

    } catch (error) {
      console.error("Error processing feedback:", error);
      setAIError("Failed to process feedback. Please try again.");
    } finally {
      setIsAIProcessing(false);
    }
  }, [newFeedback, newFeedbackRole, graphData]);

  // Zoom control functions
  const zoomIn = useCallback(() => {
    if (!svgRef.current || !zoomRef.current) return;

    const svg = d3.select(svgRef.current);
    const currentTransform = d3.zoomTransform(svg.node());

    svg.transition()
      .duration(300)
      .call(
        zoomRef.current.transform,
        d3.zoomIdentity
          .translate(currentTransform.x, currentTransform.y)
          .scale(currentTransform.k * 1.3)
      );
  }, []);

  const zoomOut = useCallback(() => {
    if (!svgRef.current || !zoomRef.current) return;

    const svg = d3.select(svgRef.current);
    const currentTransform = d3.zoomTransform(svg.node());

    svg.transition()
      .duration(300)
      .call(
        zoomRef.current.transform,
        d3.zoomIdentity
          .translate(currentTransform.x, currentTransform.y)
          .scale(currentTransform.k / 1.3)
      );
  }, []);

  const centerView = useCallback(() => {
    if (!svgRef.current || !zoomRef.current) return;

    const svg = d3.select(svgRef.current);

    // Calculate the center point
    const centerX = dimensions.width / 2;
    const centerY = dimensions.height / 2;

    // Create a transform that centers the view
    const transform = d3.zoomIdentity
      .translate(centerX, centerY)
      .scale(1)
      .translate(-centerX, -centerY);

    // Apply the transform with a smooth transition
    svg.transition()
      .duration(750)
      .call(zoomRef.current.transform, transform);
  }, [dimensions]);

  const fitToScreen = useCallback(() => {
    if (!svgRef.current || !zoomRef.current || !graphData.nodes.length) return;

    const svg = d3.select(svgRef.current);

    // Find the bounds of all nodes
    let minX = Infinity, minY = Infinity, maxX = -Infinity, maxY = -Infinity;

    graphData.nodes.forEach(node => {
      if (!node.x || !node.y) return;

      if (node.x < minX) minX = node.x;
      if (node.y < minY) minY = node.y;
      if (node.x > maxX) maxX = node.x;
      if (node.y > maxY) maxY = node.y;
    });

    // If we don't have valid bounds, center view instead
    if (!isFinite(minX) || !isFinite(minY) || !isFinite(maxX) || !isFinite(maxY)) {
      centerView();
      return;
    }

    // Add padding
    const padding = 50;
    minX -= padding;
    minY -= padding;
    maxX += padding;
    maxY += padding;

    // Calculate width and height of the content
    const contentWidth = maxX - minX;
    const contentHeight = maxY - minY;

    // Calculate scale to fit the content
    const scale = Math.min(
      dimensions.width / contentWidth,
      dimensions.height / contentHeight,
      2 // Maximum scale factor
    ) * 0.9; // 90% to add some margin

    // Calculate the center of the content
    const centerX = (minX + maxX) / 2;
    const centerY = (minY + maxY) / 2;

    // Create a transform that centers and scales the content
    const transform = d3.zoomIdentity
      .translate(dimensions.width / 2, dimensions.height / 2)
      .scale(scale)
      .translate(-centerX, -centerY);

    // Apply the transform with a smooth transition
    svg.transition()
      .duration(750)
      .call(zoomRef.current.transform, transform);
  }, [dimensions, graphData.nodes, centerView]);

  // Toggle mini-map visibility
  const toggleMiniMap = useCallback(() => {
    setShowMiniMap(!showMiniMap);
  }, [showMiniMap]);

  // Loading screen
  if (loading) {
    return (
      <div className="flex flex-col h-full items-center justify-center bg-gray-50">
        <div className="text-2xl text-blue-800 mb-4">Loading Insights Network...</div>
        <div className="w-16 h-16 border-4 border-blue-600 border-t-transparent rounded-full animate-spin"></div>
      </div>
    );
  }

  // Error screen
  if (error) {
    return (
      <div className="flex flex-col h-full items-center justify-center bg-gray-50">
        <div className="text-2xl text-red-800 mb-4">Error Loading Data</div>
        <div className="text-gray-600">{error}</div>
        <button 
          className="mt-4 px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
          onClick={() => window.location.reload()}
        >
          Retry
        </button>
      </div>
    );
  }

  return (
    <div className="flex flex-col h-full">
      {/* Header and controls */}
      <div className="bg-white p-4 shadow-md">
        <div className="flex justify-between items-center mb-4">
          <div>
            <h1 className="text-2xl font-bold text-blue-800">User Research Insights Network</h1>
            <p className="text-gray-600">Visualizing connections between user feedback, features, and personas</p>
          </div>

          {/* Search bar */}
          <div className="relative w-64">
            <input
              type="text"
              placeholder="Search nodes..."
              value={searchTerm}
              onChange={(e) => {
                setSearchTerm(e.target.value);
                if (e.target.value.length >= 2) {
                  const results = graphData.nodes.filter(node => 
                    node.name.toLowerCase().includes(e.target.value.toLowerCase())
                  );
                  setSearchResults(results);
                  setShowSearchResults(true);
                } else {
                  setShowSearchResults(false);
                  setSearchResults([]);
                }
              }}
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
            {showSearchResults && searchResults.length > 0 && (
              <div className="absolute top-full left-0 right-0 mt-1 bg-white border border-gray-300 rounded-md shadow-lg z-20 max-h-60 overflow-y-auto">
                {searchResults.map((result, index) => (
                  <div 
                    key={index}
                    className="px-3 py-2 hover:bg-gray-100 cursor-pointer"
                    onClick={() => {
                      setSelectedNode(result);
                      handleNodeClick(result);
                      setShowSearchResults(false);
                    }}
                  >
                    <div className="flex items-center">
                      <div 
                        className="w-3 h-3 rounded-full mr-2"
                        style={{ backgroundColor: colorScale(result.type) }}
                      ></div>
                      <span>{result.name}</span>
                      <span className="ml-2 text-xs text-gray-500">({result.type})</span>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>

        {/* Controls */}
        <div className="flex flex-wrap gap-2 mb-4">
          <div className="flex items-center space-x-2">
            <span className="text-sm text-gray-600">Data:</span>
            <button 
              className="px-3 py-1 bg-blue-600 text-white rounded hover:bg-blue-700 text-sm"
              onClick={exportData}
            >
              Export
            </button>
            <label className="px-3 py-1 bg-blue-600 text-white rounded hover:bg-blue-700 text-sm cursor-pointer">
              Import
              <input 
                type="file" 
                accept=".json" 
                className="hidden" 
                onChange={importData}
              />
            </label>
            <button 
              className="px-3 py-1 bg-red-600 text-white rounded hover:bg-red-700 text-sm"
              onClick={resetVisualization}
            >
              Reset
            </button>
          </div>

          <div className="flex items-center space-x-2">
            <span className="text-sm text-gray-600">View:</span>
            <button 
              className="px-3 py-1 bg-purple-600 text-white rounded hover:bg-purple-700 text-sm"
              onClick={() => setIsSimulationRunning(!isSimulationRunning)}
            >
              {isSimulationRunning ? 'Pause Simulation' : 'Resume Simulation'}
            </button>
            <button 
              className="px-3 py-1 bg-purple-600 text-white rounded hover:bg-purple-700 text-sm"
              onClick={centerView}
            >
              Center View
            </button>
            <button 
              className="px-3 py-1 bg-purple-600 text-white rounded hover:bg-purple-700 text-sm"
              onClick={toggleMiniMap}
            >
              {showMiniMap ? 'Hide Mini-Map' : 'Show Mini-Map'}
            </button>
            <button 
              className="px-3 py-1 bg-blue-600 text-white rounded hover:bg-blue-700 text-sm"
              onClick={fitToScreen}
              title="Fit all nodes to screen"
            >
              Fit to Screen
            </button>
          </div>

          <div className="flex items-center space-x-2">
            <span className="text-sm text-gray-600">Add:</span>
            <button 
              className="px-3 py-1 bg-green-600 text-white rounded hover:bg-green-700 text-sm"
              onClick={() => {
                setModalType('theme');
                setNewNodeName(`Theme ${graphData.nodes.filter(n => n.type === 'theme').length + 1}`);
                setShowModal(true);
              }}
            >
              Add Theme
            </button>
            <button 
              className="px-3 py-1 bg-green-600 text-white rounded hover:bg-green-700 text-sm"
              onClick={() => {
                setModalType('feature');
                setNewNodeName(`Feature ${graphData.nodes.filter(n => n.type === 'feature').length + 1}`);
                setShowModal(true);
              }}
            >
              Add Feature
            </button>
            <button 
              className="px-3 py-1 bg-green-600 text-white rounded hover:bg-green-700 text-sm"
              onClick={() => setShowFeedbackModal(true)}
            >
              Add Feedback
            </button>
          </div>
        </div>

        <div className="flex flex-wrap gap-4">
          <div className="flex items-center space-x-2">
            <span className="text-sm text-gray-600">Connect:</span>
            <button 
              className={`px-3 py-1 rounded text-sm ${
                selectedNode ? 'bg-orange-600 text-white hover:bg-orange-700' : 'bg-gray-300 text-gray-500 cursor-not-allowed'
              }`}
              onClick={connectNodes}
              disabled={!selectedNode}
            >
              Connect Nodes
            </button>
          </div>

          <div className="flex items-center space-x-2">
            <span className="text-sm text-gray-600">Feedback Weight:</span>
            <input 
              type="range" 
              min="1" 
              max="10" 
              value={feedbackWeight}
              onChange={(e) => setFeedbackWeight(parseInt(e.target.value))}
              className="w-32"
            />
            <span className="text-sm text-gray-600">{feedbackWeight}</span>
          </div>

          <div className="flex items-center space-x-2">
            <span className="text-sm text-gray-600">Highlight:</span>
            <button 
              className={`px-2 py-1 rounded text-xs ${highlightMode === 'none' ? 'bg-blue-600 text-white' : 'bg-gray-200 text-gray-700'}`}
              onClick={() => setHighlightMode('none')}
            >
              None
            </button>
            <button 
              className={`px-2 py-1 rounded text-xs ${highlightMode === 'themes' ? 'bg-blue-600 text-white' : 'bg-gray-200 text-gray-700'}`}
              onClick={() => setHighlightMode('themes')}
            >
              Themes
            </button>
            <button 
              className={`px-2 py-1 rounded text-xs ${highlightMode === 'features' ? 'bg-blue-600 text-white' : 'bg-gray-200 text-gray-700'}`}
              onClick={() => setHighlightMode('features')}
            >
              Features
            </button>
            <button 
              className={`px-2 py-1 rounded text-xs ${highlightMode === 'personas' ? 'bg-blue-600 text-white' : 'bg-gray-200 text-gray-700'}`}
              onClick={() => setHighlightMode('personas')}
            >
              Personas
            </button>
          </div>
        </div>
      </div>

      {/* Main visualization container */}
      <div className="flex flex-1 relative">
        <div 
          ref={containerRef} 
          className="flex-1 bg-gray-50 p-4 transition-all duration-300"
          style={{
            marginRight: showAIInsightsPanel && !isPanelCollapsed ? `${panelWidth}px` : '0',
            minHeight: '500px'
          }}
        >
          <svg 
            ref={svgRef} 
            width="100%" 
            height="100%"
            className="border border-gray-200 rounded-lg bg-white shadow-inner w-full h-full"
            style={{ minHeight: '500px' }}
            onClick={() => setSelectedNode(null)}
          />

          {/* Zoom controls */}
          <div className="absolute bottom-8 right-8 flex flex-col bg-white rounded-lg shadow p-2 space-y-2 z-10">
            <button 
              className="w-8 h-8 flex items-center justify-center bg-gray-100 hover:bg-gray-200 rounded-full"
              onClick={zoomIn}
            >
              <span className="text-xl">+</span>
            </button>
            <button 
              className="w-8 h-8 flex items-center justify-center bg-gray-100 hover:bg-gray-200 rounded-full"
              onClick={zoomOut}
            >
              <span className="text-xl">-</span>
            </button>
            <button 
              className="w-8 h-8 flex items-center justify-center bg-gray-100 hover:bg-gray-200 rounded-full"
              onClick={fitToScreen}
            >
              <span className="text-xl"></span>
            </button>
          </div>
        </div>

        {/* AI Insights Panel */}
        {showAIInsightsPanel && aiInsights && (
          <div 
            className={`fixed right-0 top-0 bottom-0 bg-white shadow-lg z-40 transition-all duration-300`}
            style={{ 
              width: isPanelCollapsed ? '50px' : `${panelWidth}px`,
              maxWidth: '40%'
            }}
          >
            {/* Collapse/Expand Button */}
            <div className="w-10 flex flex-col items-center pt-4 border-r border-gray-200 bg-gray-50">
              <button 
                onClick={() => setIsPanelCollapsed(!isPanelCollapsed)}
                className="w-8 h-8 flex items-center justify-center rounded-full bg-white hover:bg-gray-100 text-gray-600 shadow-sm"
                title={isPanelCollapsed ? "Expand panel" : "Collapse panel"}
              >
                <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d={
                    isPanelCollapsed 
                      ? "M13 5l7 7-7 7M5 5l7 7-7 7" // Expand icon
                      : "M11 19l-7-7 7-7M19 19l-7-7 7-7" // Collapse icon
                  } />
                </svg>
              </button>

              {isPanelCollapsed && (
                <div className="mt-4 transform -rotate-90 whitespace-nowrap text-sm font-medium text-gray-600 origin-center">
                  {aiInsights.isFeatureView ? 'Feature Details' : 
                   aiInsights.persona ? `${aiInsights.persona}` : 
                   aiInsights.theme ? `${aiInsights.theme}` : 'AI Insights'}
                </div>
              )}

              <button 
                onClick={() => setShowAIInsightsPanel(false)}
                className="w-8 h-8 flex items-center justify-center rounded-full bg-white hover:bg-gray-100 text-gray-600 mt-4 shadow-sm"
                title="Close panel"
              >
                <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                </svg>
              </button>
            </div>

            {/* Panel Content - Only shown when expanded */}
            {!isPanelCollapsed && (
              <div className="flex-1 p-4 overflow-y-auto">
                <div className="flex justify-between items-center mb-4">
                  <h2 className="text-lg font-bold">
                    {aiInsights.isFeatureView ? 'Feature Details' : 
                     aiInsights.persona ? `${aiInsights.persona} Feedback` : 
                     aiInsights.theme ? `${aiInsights.theme} Feedback` : 'AI Insights'}
                  </h2>
                </div>

                {/* Theme View */}
                {aiInsights.theme && !aiInsights.isFeatureView && !aiInsights.persona && (
                  <>
                    <div className="mb-4">
                      <div className="text-sm text-gray-600">Feedback Count</div>
                      <div className="text-lg font-semibold">
                        {aiInsights.feedbackCount > 0 ? aiInsights.feedbackCount : 
                         (aiInsights.results && aiInsights.results.length) || 0}
                      </div>
                    </div>

                    {/* Connected Features */}
                    {aiInsights.connectedFeatures && aiInsights.connectedFeatures.length > 0 && (
                      <div className="mb-4">
                        <div className="text-sm text-gray-600 mb-1">Related Features</div>
                        <div className="space-y-1">
                          {aiInsights.connectedFeatures.map((feature, index) => (
                            <div 
                              key={index} 
                              className="px-2 py-1 bg-green-50 text-green-800 rounded text-sm flex justify-between items-center"
                            >
                              <span>{feature.name}</span>
                              <span className={`text-xs px-1.5 py-0.5 rounded ${
                                feature.priority === 'High' ? 'bg-red-100 text-red-800' :
                                feature.priority === 'Medium' ? 'bg-yellow-100 text-yellow-800' :
                                'bg-green-100 text-green-800'
                              }`}>
                                {feature.priority}
                              </span>
                            </div>
                          ))}
                        </div>
                      </div>
                    )}

                    {/* Connected Personas */}
                    {aiInsights.connectedPersonas && aiInsights.connectedPersonas.length > 0 && (
                      <div className="mb-4">
                        <div className="text-sm text-gray-600 mb-1">Related Personas</div>
                        <div className="space-y-1">
                          {aiInsights.connectedPersonas.map((persona, index) => (
                            <div 
                              key={index} 
                              className="px-2 py-1 bg-orange-50 text-orange-800 rounded text-sm"
                            >
                              {persona.name}
                            </div>
                          ))}
                        </div>
                      </div>
                    )}
                  </>
                )}

                {/* Feature View */}
                {aiInsights.isFeatureView && (
                  <>
                    <div className="mb-4 flex justify-between">
                      <div>
                        <div className="text-sm text-gray-600">Priority</div>
                        <div className={`inline-block px-2 py-1 rounded text-xs font-medium ${
                          aiInsights.priority === 'High' ? 'bg-red-100 text-red-800' :
                          aiInsights.priority === 'Medium' ? 'bg-yellow-100 text-yellow-800' :
                          'bg-green-100 text-green-800'
                        }`}>
                          {aiInsights.priority}
                        </div>
                      </div>
                    </div>

                    {/* Connected Themes */}
                    {aiInsights.connectedThemes && aiInsights.connectedThemes.length > 0 && (
                      <div className="mb-4">
                        <div className="text-sm text-gray-600 mb-1">Related Themes</div>
                        <div className="space-y-1">
                          {aiInsights.connectedThemes.map((theme, index) => (
                            <div 
                              key={index} 
                              className="px-2 py-1 bg-blue-50 text-blue-800 rounded text-sm flex justify-between items-center"
                            >
                              <span>{theme.name}</span>
                              <span className="text-xs text-blue-600">
                                {theme.feedbackCount || 0} feedback
                              </span>
                            </div>
                          ))}
                        </div>
                      </div>
                    )}

                    {/* Connected Personas */}
                    {aiInsights.connectedPersonas && aiInsights.connectedPersonas.length > 0 && (
                      <div className="mb-4">
                        <div className="text-sm text-gray-600 mb-1">Related Personas</div>
                        <div className="space-y-1">
                          {aiInsights.connectedPersonas.map((persona, index) => (
                            <div 
                              key={index} 
                              className="px-2 py-1 bg-orange-50 text-orange-800 rounded text-sm"
                            >
                              {persona.name}
                            </div>
                          ))}
                        </div>
                      </div>
                    )}
                  </>
                )}

                {/* Persona View */}
                {aiInsights.persona && (
                  <>
                    <div className="mb-4">
                      <div className="text-sm text-gray-600">Feedback Count</div>
                      <div className="text-lg font-semibold">
                        {aiInsights.feedbackCount > 0 ? aiInsights.feedbackCount : 
                         (aiInsights.results && aiInsights.results.length) || 0}
                      </div>
                    </div>

                    {/* Connected Themes */}
                    {aiInsights.connectedThemes && aiInsights.connectedThemes.length > 0 && (
                      <div className="mb-4">
                        <div className="text-sm text-gray-600 mb-1">Common Themes</div>
                        <div className="space-y-1">
                          {aiInsights.connectedThemes.map((theme, index) => (
                            <div 
                              key={index} 
                              className="px-2 py-1 bg-blue-50 text-blue-800 rounded text-sm flex justify-between items-center"
                            >
                              <span>{theme.name}</span>
                              <span className="text-xs text-blue-600">
                                {theme.feedbackCount || 0} feedback
                              </span>
                            </div>
                          ))}
                        </div>
                      </div>
                    )}

                    {/* Connected Features */}
                    {aiInsights.connectedFeatures && aiInsights.connectedFeatures.length > 0 && (
                      <div className="mb-4">
                        <div className="text-sm text-gray-600 mb-1">Relevant Features</div>
                        <div className="space-y-1">
                          {aiInsights.connectedFeatures.map((feature, index) => (
                            <div 
                              key={index} 
                              className="px-2 py-1 bg-green-50 text-green-800 rounded text-sm flex justify-between items-center"
                            >
                              <span>{feature.name}</span>
                              <span className={`text-xs px-1.5 py-0.5 rounded ${
                                feature.priority === 'High' ? 'bg-red-100 text-red-800' :
                                feature.priority === 'Medium' ? 'bg-yellow-100 text-yellow-800' :
                                'bg-green-100 text-green-800'
                              }`}>
                                {feature.priority}
                              </span>
                            </div>
                          ))}
                        </div>
                      </div>
                    )}
                  </>
                )}

                {/* Feedback Items */}
                {aiInsights.results && aiInsights.results.length > 0 ? (
                  <>
                    <h3 className="font-semibold text-sm text-gray-600 mb-2">
                      {aiInsights.persona ? 'User Feedback' : 'Feedback Classification'}
                    </h3>
                    {aiInsights.results.map((result, index) => (
                      <div key={index} className="bg-gray-50 p-2 rounded mb-2">
                        <div className="text-sm">{result.text}</div>
                        <div className="mt-1 text-xs text-gray-500">
                          {result.user_role}
                        </div>
                      </div>
                    ))}
                  </>
                ) : (
                  <div className="text-gray-500 italic">No feedback data available</div>
                )}
              </div>
            )}
          </div>
        )}
      </div>

      {/* Add Node Modal */}
      {showModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded-lg shadow-xl w-96">
            <h2 className="text-xl font-bold mb-4">
              Add New {modalType === 'theme' ? 'Theme' : 'Feature'}
            </h2>

            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-bold mb-2">
                Name
              </label>
              <input 
                type="text" 
                value={newNodeName}
                onChange={(e) => setNewNodeName(e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                placeholder={`Enter ${modalType} name`}
              />
            </div>

            {modalType === 'theme' && (
              <div className="mb-4">
                <label className="block text-gray-700 text-sm font-bold mb-2">
                  Feedback Count
                </label>
                <input 
                  type="number" 
                  value={newNodeFeedbackCount}
                  onChange={(e) => setNewNodeFeedbackCount(parseInt(e.target.value))}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                  min="0"
                />
              </div>
            )}

            {modalType === 'feature' && (
              <div className="mb-4">
                <label className="block text-gray-700 text-sm font-bold mb-2">
                  Priority
                </label>
                <select 
                  value={newNodePriority}
                  onChange={(e) => setNewNodePriority(e.target.value)}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                >
                  <option value="Low">Low</option>
                  <option value="Medium">Medium</option>
                  <option value="High">High</option>
                </select>
              </div>
            )}

            <div className="flex justify-end space-x-2">
              <button 
                className="px-4 py-2 bg-gray-300 text-gray-700 rounded hover:bg-gray-400"
                onClick={() => {
                  setShowModal(false);
                  setNewNodeName('');
                  setNewNodeFeedbackCount(10);
                  setNewNodePriority('Medium');
                }}
              >
                Cancel
              </button>
              <button 
                className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
                onClick={handleAddNode}
              >
                Add
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Connection Modal */}
      {showConnectionModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded-lg shadow-xl w-96">
            <h2 className="text-xl font-bold mb-4">
              Connect Nodes
            </h2>

            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-bold mb-2">
                From
              </label>
              <div className="px-3 py-2 border border-gray-300 rounded-md bg-gray-50">
                {connectionSource?.name} ({connectionSource?.type})
              </div>
            </div>

            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-bold mb-2">
                To
              </label>
              <select 
                value={connectionTarget}
                onChange={(e) => setConnectionTarget(e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              >
                <option value="">Select a target node</option>
                {availableTargets.map((target) => (
                  <option key={target.id} value={target.id}>
                    {target.name} ({target.type})
                  </option>
                ))}
              </select>
            </div>

            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-bold mb-2">
                Connection Strength: {connectionStrength.toFixed(1)}
              </label>
              <input 
                type="range" 
                min="0.1" 
                max="1" 
                step="0.1"
                value={connectionStrength}
                onChange={(e) => setConnectionStrength(parseFloat(e.target.value))}
                className="w-full"
              />
            </div>

            <div className="flex justify-end space-x-2">
              <button 
                className="px-4 py-2 bg-gray-300 text-gray-700 rounded hover:bg-gray-400"
                onClick={() => {
                  setShowConnectionModal(false);
                  setConnectionSource(null);
                  setConnectionTarget('');
                  setConnectionStrength(0.5);
                }}
              >
                Cancel
              </button>
              <button 
                className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
                onClick={handleConnectionSubmit}
                disabled={!connectionTarget}
              >
                Connect
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Add Feedback Modal */}
      {showFeedbackModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded-lg shadow-xl w-96">
            <h2 className="text-xl font-bold mb-4">
              Add New Feedback
            </h2>

            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-bold mb-2">
                Feedback Text
              </label>
              <textarea 
                value={newFeedback}
                onChange={(e) => setNewFeedback(e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                placeholder="Enter user feedback"
                rows={4}
              />
            </div>

            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-bold mb-2">
                User Role
              </label>
              <input 
                type="text" 
                value={newFeedbackRole}
                onChange={(e) => setNewFeedbackRole(e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                placeholder="e.g. Mobile User, Admin, Power User"
              />
            </div>

            <div className="flex justify-end space-x-2">
              <button 
                className="px-4 py-2 bg-gray-300 text-gray-700 rounded hover:bg-gray-400"
                onClick={() => {
                  setShowFeedbackModal(false);
                  setNewFeedback('');
                  setNewFeedbackRole('');
                }}
              >
                Cancel
              </button>
              <button 
                className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
                onClick={handleAddFeedback}
                disabled={isAIProcessing}
              >
                {isAIProcessing ? (
                  <div className="flex items-center">
                    <div className="w-4 h-4 border-2 border-white border-t-transparent rounded-full animate-spin mr-2"></div>
                    Processing...
                  </div>
                ) : 'Submit'}
              </button>
            </div>

            {aiError && (
              <div className="mt-4 p-2 bg-red-100 text-red-700 rounded">
                {aiError}
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default UserResearchNetwork;
