  // Create a connection between nodes
  const connectNodes = () => {
    if (!selectedNode) return;

    // Find suitable target nodes to connect to (different type than selected node)
    const targets = graphData.nodes.filter(n => n.type !== selectedNode.type);

    if (targets.length === 0) {
      alert("No compatible nodes found to connect with.");
      return;
