        }}
      >
        <svg 
          ref={svgRef} 
          width={dimensions.width} 
          height={dimensions.height}
          className="border border-gray-200 rounded-lg bg-white shadow-inner w-full h-full"
          onClick={() => setSelectedNode(null)}
        />
      </div>

      {/* Zoom Controls - Keep these at the bottom right */}
      <div className="absolute bottom-4 right-4 flex gap-2">
        <button 
          className="w-10 h-10 bg-white rounded-full shadow-md flex items-center justify-center hover:bg-gray-100"
          onClick={zoomIn}
        >
          <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6v6m0 0v6m0-6h6m-6 0H6" />
          </svg>
        </button>
        <button 
          className="w-10 h-10 bg-white rounded-full shadow-md flex items-center justify-center hover:bg-gray-100"
          onClick={zoomOut}
        >
          <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M18 12H6" />
          </svg>
        </button>
        <button 
          className="w-10 h-10 bg-white rounded-full shadow-md flex items-center justify-center hover:bg-gray-100"
          onClick={centerView}
        >
          <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 12l2-2m0 0l7-7 7 7M5 10v10a1 1 0 001 1h3m10-11l2 2m-2-2v10a1 1 0 01-1 1h-3m-6 0a1 1 0 001-1v-4a1 1 0 011-1h2a1 1 0 011 1v4a1 1 0 001 1m-6 0h6" />
          </svg>
        </button>
      </div>

      <div className="bg-white p-4 shadow-md mt-4">
        <div className="flex flex-wrap gap-8">
          <div className="flex items-center gap-2">
            <div className="w-4 h-4 rounded-full bg-blue-600"></div>
            <span className="text-sm">Feedback Themes</span>
          </div>
          <div className="flex items-center gap-2">
            <div className="w-4 h-4 rounded-full bg-green-600"></div>
            <span className="text-sm">Feature Ideas</span>
          </div>
          <div className="flex items-center gap-2">
            <div className="w-4 h-4 rounded-full bg-orange-600"></div>
            <span className="text-sm">User Personas</span>
          </div>

          <div className="flex-1"></div>

          {selectedNode && (
            <div className="bg-gray-100 px-4 py-2 rounded-md flex items-center">
              <div>
                <span className="font-medium">{selectedNode.name}</span>
                {selectedNode.type === 'theme' && (
                  <span> ({selectedNode.feedbackCount} feedbacks)</span>
                )}
              </div>
              <button
                className="ml-2 px-2 py-1 bg-gray-600 text-white rounded hover:bg-gray-700 text-xs"
                onClick={() => focusOnNode(selectedNode)}
              >
                Focus
              </button>
              <button
                className="ml-2 px-2 py-1 bg-purple-600 text-white rounded hover:bg-purple-700 text-xs"
                onClick={connectNodes}
              >
                Connect
              </button>
            </div>
          )}
        </div>
      </div>

      {/* Modal for adding new nodes */}
      {showModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded-lg shadow-lg w-96">
            <h2 className="text-xl font-bold mb-4">
              {modalType === 'theme' ? 'Add New Feedback Theme' : 'Add New Feature Idea'}
            </h2>

            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-bold mb-2">
                Name
              </label>
              <input
                type="text"
                value={newNodeName}
                onChange={(e) => setNewNodeName(e.target.value)}
                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
              />
            </div>

            {modalType === 'theme' ? (
              <div className="mb-4">
                <label className="block text-gray-700 text-sm font-bold mb-2">
                  Feedback Count
                </label>
                <input
                  type="number"
                  min="1"
                  max="100"
                  value={newNodeFeedbackCount}
                  onChange={(e) => setNewNodeFeedbackCount(Number(e.target.value))}
                  className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                />
              </div>
            ) : (
              <div className="mb-4">
                <label className="block text-gray-700 text-sm font-bold mb-2">
                  Priority
                </label>
                <select
                  value={newNodePriority}
                  onChange={(e) => setNewNodePriority(e.target.value)}
                  className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                >
                  <option value="Low">Low</option>
                  <option value="Medium">Medium</option>
                  <option value="High">High</option>
                </select>
              </div>
            )}

            <div className="flex justify-end gap-2">
              <button
                onClick={() => setShowModal(false)}
                className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-bold py-2 px-4 rounded"
              >
                Cancel
              </button>
              <button
                onClick={handleModalSubmit}
                className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
              >
                Add
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Modal for creating connections */}
      {showConnectionModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded-lg shadow-lg w-96">
            <h2 className="text-xl font-bold mb-4">Create Connection</h2>

            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-bold mb-2">
                From: {connectionSource?.name} ({connectionSource?.type})
              </label>
            </div>

            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-bold mb-2">
                To:
              </label>
              <select
                value={connectionTarget}
                onChange={(e) => setConnectionTarget(e.target.value)}
                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
              >
                {availableTargets.map(node => (
                  <option key={node.id} value={node.id}>
                    {node.name} ({node.type})
                  </option>
                ))}
              </select>
            </div>

            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-bold mb-2">
                Connection Strength: {connectionStrength}
              </label>
              <input
                type="range"
                min="0.1"
                max="1"
                step="0.1"
                value={connectionStrength}
                onChange={(e) => setConnectionStrength(Number(e.target.value))}
                className="w-full"
              />
              <div className="flex justify-between text-xs text-gray-500">
                <span>Weak</span>
                <span>Strong</span>
              </div>
            </div>

            <div className="flex justify-end gap-2">
              <button
                onClick={() => setShowConnectionModal(false)}
                className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-bold py-2 px-4 rounded"
              >
                Cancel
              </button>
              <button
                onClick={handleConnectionSubmit}
                className="bg-purple-600 hover:bg-purple-700 text-white font-bold py-2 px-4 rounded"
              >
                Connect
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Add these buttons to your UI */}
      <div className="absolute bottom-4 right-4 flex gap-2">
        <button 
          className="w-10 h-10 bg-white rounded-full shadow-md flex items-center justify-center hover:bg-gray-100"
          onClick={zoomIn}
        >
          <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6v6m0 0v6m0-6h6m-6 0H6" />
          </svg>
        </button>
        <button 
          className="w-10 h-10 bg-white rounded-full shadow-md flex items-center justify-center hover:bg-gray-100"
          onClick={zoomOut}
        >
          <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M18 12H6" />
          </svg>
        </button>
        <button 
          className="w-10 h-10 bg-white rounded-full shadow-md flex items-center justify-center hover:bg-gray-100"
          onClick={centerView}
        >
          <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 12l2-2m0 0l7-7 7 7M5 10v10a1 1 0 001 1h3m10-11l2 2m-2-2v10a1 1 0 01-1 1h-3m-6 0a1 1 0 001-1v-4a1 1 0 011-1h2a1 1 0 011 1v4a1 1 0 001 1m-6 0h6" />
          </svg>
        </button>
      </div>

      {/* Add Feedback Modal */}
      {showFeedbackModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded-lg shadow-lg w-96">
            <h2 className="text-xl font-bold mb-4">Add New Feedback</h2>

            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-bold mb-2">
                Feedback Text
              </label>
              <textarea
                value={newFeedback}
                onChange={(e) => setNewFeedback(e.target.value)}
                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                rows="4"
                placeholder="Enter user feedback here..."
              />
            </div>

            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-bold mb-2">
                User Role
              </label>
              <input
                type="text"
                value={newFeedbackRole}
                onChange={(e) => setNewFeedbackRole(e.target.value)}
                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                placeholder="e.g., Product Manager, Developer, etc."
              />
            </div>

            <div className="flex justify-end gap-2">
              <button
                onClick={() => setShowFeedbackModal(false)}
                className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-bold py-2 px-4 rounded"
                disabled={isAIProcessing}
              >
                Cancel
              </button>
              <button
                onClick={handleFeedbackSubmit}
                className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded flex items-center"
                disabled={isAIProcessing}
              >
                {isAIProcessing ? (
                  <>
                    <svg className="animate-spin -ml-1 mr-2 h-4 w-4 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                      <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                      <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                    </svg>
                    Processing...
                  </>
                ) : (
                  'Add Feedback'
                )}
              </button>
            </div>

            {aiError && (
              <div className="mt-4 p-2 bg-red-100 text-red-700 rounded">
                {aiError}
              </div>
            )}
          </div>
        </div>
      )}

      {/* AI Insights Panel */}
      {showAIInsightsPanel && aiInsights && (
        <div 
          className={`fixed right-0 top-0 bottom-0 bg-white shadow-lg z-40 transition-all duration-300 flex border-l border-gray-300`}
          style={{ 
            width: isPanelCollapsed ? '50px' : `${panelWidth}px`,
            maxWidth: '40%' // Limit maximum width to prevent excessive overlap
          }}
        >
          {/* Collapse/Expand Button */}
          <div className="w-10 flex flex-col items-center pt-4 border-r border-gray-200 bg-gray-50">
            <button 
              onClick={togglePanelCollapse}
              className="w-8 h-8 flex items-center justify-center rounded-full bg-white hover:bg-gray-100 text-gray-600 shadow-sm"
              title={isPanelCollapsed ? "Expand panel" : "Collapse panel"}
            >
              <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d={
                  isPanelCollapsed 
                    ? "M13 5l7 7-7 7M5 5l7 7-7 7" // Expand icon
                    : "M11 19l-7-7 7-7M19 19l-7-7 7-7" // Collapse icon
                } />
              </svg>
            </button>

            {isPanelCollapsed && (
              <div className="mt-4 transform -rotate-90 whitespace-nowrap text-sm font-medium text-gray-600 origin-center">
                {aiInsights.isFeatureView ? 'Feature Details' : 
                 aiInsights.persona ? `${aiInsights.persona}` : 
                 aiInsights.theme ? `${aiInsights.theme}` : 'AI Insights'}
              </div>
            )}

            <button 
              onClick={() => setShowAIInsightsPanel(false)}
              className="w-8 h-8 flex items-center justify-center rounded-full bg-white hover:bg-gray-100 text-gray-600 mt-4 shadow-sm"
              title="Close panel"
            >
              <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
              </svg>
            </button>
          </div>

          {/* Panel Content - Only shown when expanded */}
          {!isPanelCollapsed && (
            <div className="flex-1 p-4 overflow-y-auto">
              <div className="flex justify-between items-center mb-4">
                <h2 className="text-lg font-bold">
                  {aiInsights.isFeatureView ? 'Feature Details' : 
                   aiInsights.persona ? `${aiInsights.persona} Feedback` : 
                   aiInsights.theme ? `${aiInsights.theme} Feedback` : 'AI Insights'}
                </h2>
              </div>

              {/* Theme View */}
              {aiInsights.theme && !aiInsights.isFeatureView && !aiInsights.persona && (
                <>
                  <div className="mb-4">
                    <div className="text-sm text-gray-600">Feedback Count</div>
                    <div className="text-lg font-semibold">{aiInsights.feedbackCount || 0}</div>
                  </div>

                  {/* Connected Features */}
                  {aiInsights.connectedFeatures && aiInsights.connectedFeatures.length > 0 && (
                    <div className="mb-4">
                      <div className="text-sm text-gray-600 mb-1">Related Features</div>
                      {aiInsights.connectedFeatures.map((feature, index) => (
                        <div key={index} className="bg-green-50 p-2 rounded mb-2 flex justify-between items-center">
                          <div className="font-medium">{feature.name}</div>
                          <div className="text-xs text-gray-500">
                            {Math.round(feature.connectionStrength * 100)}% match
                          </div>
                        </div>
                      ))}
                    </div>
                  )}

                  {/* Connected Personas */}
                  {aiInsights.connectedPersonas && aiInsights.connectedPersonas.length > 0 && (
                    <div className="mb-4">
                      <div className="text-sm text-gray-600 mb-1">User Roles</div>
                      {aiInsights.connectedPersonas.map((persona, index) => (
                        <div key={index} className="bg-orange-50 p-2 rounded mb-2">
                          <div className="font-medium">{persona.name}</div>
                        </div>
                      ))}
                    </div>
                  )}
                </>
              )}

              {/* Feature View */}
              {aiInsights.isFeatureView && (
                <>
                  <div className="mb-4 flex justify-between">
                    <div>
                      <div className="text-sm text-gray-600">Priority</div>
                      <div className={`inline-block px-2 py-1 rounded text-xs font-medium ${
                        aiInsights.priority === 'High' ? 'bg-red-100 text-red-800' :
                        aiInsights.priority === 'Medium' ? 'bg-yellow-100 text-yellow-800' :
                        'bg-green-100 text-green-800'
                      }`}>
                        {aiInsights.priority}
                      </div>
                    </div>
                    <div>
                      <div className="text-sm text-gray-600">Feedback Count</div>
                      <div className="text-lg font-semibold text-right">
                        {aiInsights.feedbackCount > 0 ? aiInsights.feedbackCount : 
                         (aiInsights.results && aiInsights.results.length) || 0}
                      </div>
                    </div>
                  </div>

                  {aiInsights.description && (
                    <div className="mb-4">
                      <div className="text-sm text-gray-600">Description</div>
                      <div className="text-sm">{aiInsights.description}</div>
                    </div>
                  )}

                  {aiInsights.connectedThemes && aiInsights.connectedThemes.length > 0 && (
                    <div className="mb-4">
                      <div className="text-sm text-gray-600 mb-1">Related Themes</div>
                      {aiInsights.connectedThemes.map((theme, index) => (
                        <div key={index} className="bg-blue-50 p-2 rounded mb-2 flex justify-between items-center">
                          <div className="font-medium">{theme.name}</div>
                          <div className="text-xs text-gray-500">
                            {Math.round(theme.connectionStrength * 100)}% match
                          </div>
                        </div>
                      ))}
                    </div>
                  )}

                  {aiInsights.connectedPersonas && aiInsights.connectedPersonas.length > 0 && (
                    <div className="mb-4">
                      <div className="text-sm text-gray-600 mb-1">User Roles</div>
                      {aiInsights.connectedPersonas.map((persona, index) => (
                        <div key={index} className="bg-orange-50 p-2 rounded mb-2">
                          <div className="font-medium">{persona.name}</div>
                        </div>
                      ))}
                    </div>
                  )}
                </>
              )}

              {/* Persona View */}
              {aiInsights.persona && (
                <>
                  <div className="mb-4">
                    <div className="text-sm text-gray-600">Feedback Count</div>
                    <div className="text-lg font-semibold">
                      {aiInsights.feedbackCount > 0 ? aiInsights.feedbackCount : 
                       (aiInsights.results && aiInsights.results.length) || 0}
                    </div>
                  </div>

                  {aiInsights.themeBreakdown && aiInsights.themeBreakdown.length > 0 && (
                    <div className="mb-4">
                      <div className="text-sm text-gray-600 mb-1">Feedback Themes</div>
                      {aiInsights.themeBreakdown.map((item, index) => (
                        <div key={index} className="bg-blue-50 p-2 rounded mb-2 flex justify-between items-center">
                          <div className="font-medium">{item.theme}</div>
                          <div className="text-xs bg-blue-200 px-2 py-1 rounded-full">
                            {item.count} items
                          </div>
                        </div>
                      ))}
                    </div>
                  )}

                  {aiInsights.connectedThemes && aiInsights.connectedThemes.length > 0 && (
                    <div className="mb-4">
                      <div className="text-sm text-gray-600 mb-1">Related Themes</div>
                      {aiInsights.connectedThemes.map((theme, index) => (
                        <div key={index} className="bg-blue-50 p-2 rounded mb-2">
                          <div className="font-medium">{theme.name}</div>
                        </div>
                      ))}
                    </div>
                  )}

                  {aiInsights.connectedFeatures && aiInsights.connectedFeatures.length > 0 && (
                    <div className="mb-4">
                      <div className="text-sm text-gray-600 mb-1">Related Features</div>
                      {aiInsights.connectedFeatures.map((feature, index) => (
                        <div key={index} className="bg-green-50 p-2 rounded mb-2">
                          <div className="font-medium">{feature.name}</div>
                        </div>
                      ))}
                    </div>
                  )}
                </>
              )}

              {/* New Themes Section */}
              {aiInsights.newThemes && aiInsights.newThemes.length > 0 && (
                <div className="mb-4">
                  <h3 className="font-semibold text-sm text-gray-600 mb-2">New Themes Identified</h3>
                  {aiInsights.newThemes.map((theme, index) => (
                    <div key={index} className="bg-blue-50 p-2 rounded mb-2">
                      <div className="font-medium">{theme.name}</div>
                      <div className="text-xs text-gray-600">{theme.description}</div>
                    </div>
                  ))}
                </div>
              )}

              {/* Feedback Items */}
              {aiInsights.results && aiInsights.results.length > 0 ? (
                <>
                  <h3 className="font-semibold text-sm text-gray-600 mb-2">
                    {aiInsights.persona ? 'User Feedback' : 'Feedback Classification'}
                  </h3>
                  {aiInsights.results.map((result, index) => (
                    <div key={index} className="bg-gray-50 p-2 rounded mb-2">
                      {result.text && (
                        <div className="text-sm mb-1 italic">"{result.text}"</div>
                      )}
                      <div className="flex justify-between">
                        <div className="font-medium">{result.theme}</div>
                        <div className={`text-xs px-2 py-1 rounded ${
                          result.sentiment === 'positive' ? 'bg-green-100 text-green-800' : 
                          result.sentiment === 'negative' ? 'bg-red-100 text-red-800' : 
                          'bg-gray-100 text-gray-800'
                        }`}>
                          {result.sentiment}
                        </div>
                      </div>
                      {result.user_role && (
                        <div className="text-xs text-gray-600 mt-1">
                          From: <span className="font-medium">{result.user_role}</span>
                        </div>
                      )}
                      <div className="text-xs text-gray-600 mt-1">
                        Priority: <span className="font-medium">{result.priority}/10</span>
                      </div>
                      {result.keywords && (
                        <div className="flex flex-wrap gap-1 mt-1">
                          {result.keywords.map((keyword, kidx) => (
                            <span key={kidx} className="text-xs bg-gray-200 px-1 rounded">
                              {keyword}
                            </span>
                          ))}
                        </div>
                      )}
                    </div>
                  ))}
                </>
              ) : (
                <div className="text-gray-500 italic">No feedback data available</div>
              )}
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default UserResearchNetwork;
