import React, { useState, useEffect, useRef } from 'react';
import * as d3 from 'd3';
import { classifyFeedback, generateFeatureIdeas } from '../services/openaiService';

const UserResearchNetwork = () => {
  const svgRef = useRef(null);
  const containerRef = useRef(null);
  const [dimensions, setDimensions] = useState({ width: 800, height: 600 });
  const [isSimulationRunning, setIsSimulationRunning] = useState(true);
  const [selectedNode, setSelectedNode] = useState(null);
  const [highlightMode, setHighlightMode] = useState('none');
  const [feedbackWeight, setFeedbackWeight] = useState(5);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [modalType, setModalType] = useState(''); // 'theme' or 'feature'
  const [newNodeName, setNewNodeName] = useState('');
  const [newNodeFeedbackCount, setNewNodeFeedbackCount] = useState(10);
  const [newNodePriority, setNewNodePriority] = useState('Medium');
  const [showConnectionModal, setShowConnectionModal] = useState(false);
  const [connectionSource, setConnectionSource] = useState(null);
  const [connectionTarget, setConnectionTarget] = useState('');
  const [connectionStrength, setConnectionStrength] = useState(0.5);
  const [availableTargets, setAvailableTargets] = useState([]);
  const [multipleConnections, setMultipleConnections] = useState([]);
  const [showMiniMap, setShowMiniMap] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [showSearchResults, setShowSearchResults] = useState(false);
  const [isAIProcessing, setIsAIProcessing] = useState(false);
  const [aiError, setAIError] = useState(null);
  const [showFeedbackModal, setShowFeedbackModal] = useState(false);
  const [newFeedback, setNewFeedback] = useState('');
  const [newFeedbackRole, setNewFeedbackRole] = useState('');
  const [feedbackItems, setFeedbackItems] = useState([]);
  const [showAIInsightsPanel, setShowAIInsightsPanel] = useState(false);
  const [aiInsights, setAIInsights] = useState(null);

  // Add this constant at the top of your component, right after your state declarations
  const colorScale = d3.scaleOrdinal()
    .domain(["theme", "feature", "persona"])
    .range(["#3182CE", "#38A169", "#DD6B20"]);

  // Initial data structure
  const initialData = {
    nodes: [
      // Feedback themes (blue)
      { id: "theme1", name: "Slow Performance", type: "theme", feedbackCount: 37 },
      { id: "theme2", name: "Confusing Navigation", type: "theme", feedbackCount: 42 },
      { id: "theme3", name: "Missing Features", type: "theme", feedbackCount: 28 },
      { id: "theme4", name: "Sync Issues", type: "theme", feedbackCount: 15 },
      { id: "theme5", name: "Cluttered Interface", type: "theme", feedbackCount: 31 },
      { id: "theme6", name: "Mobile Experience", type: "theme", feedbackCount: 25 },
      { id: "theme7", name: "Data Visualization", type: "theme", feedbackCount: 19 },

      // Feature ideas (green)
      { id: "feature1", name: "Performance Optimization", type: "feature", priority: "High" },
      { id: "feature2", name: "Navigation Redesign", type: "feature", priority: "High" },
      { id: "feature3", name: "Bulk Actions", type: "feature", priority: "Medium" },
      { id: "feature4", name: "Offline Mode", type: "feature", priority: "Medium" },
      { id: "feature5", name: "Interface Simplification", type: "feature", priority: "High" },
      { id: "feature6", name: "Mobile App", type: "feature", priority: "High" },
      { id: "feature7", name: "Advanced Charts", type: "feature", priority: "Medium" },

      // User personas (orange)
      { id: "persona1", name: "Power User", type: "persona" },
      { id: "persona2", name: "Casual User", type: "persona" },
      { id: "persona3", name: "Admin", type: "persona" },
      { id: "persona4", name: "Mobile User", type: "persona" }
    ],
    links: [
      // Theme to Feature connections
      { source: "theme1", target: "feature1", strength: 0.8 },
      { source: "theme1", target: "feature4", strength: 0.4 },
      { source: "theme2", target: "feature2", strength: 0.9 },
      { source: "theme2", target: "feature5", strength: 0.5 },
      { source: "theme3", target: "feature3", strength: 0.6 },
      { source: "theme3", target: "feature7", strength: 0.5 },
      { source: "theme4", target: "feature4", strength: 0.8 },
      { source: "theme5", target: "feature5", strength: 0.9 },
      { source: "theme6", target: "feature6", strength: 0.9 },
      { source: "theme7", target: "feature7", strength: 0.8 },

      // Persona connections to themes
      { source: "persona1", target: "theme1", strength: 0.7 },
      { source: "persona1", target: "theme3", strength: 0.8 },
      { source: "persona1", target: "theme7", strength: 0.9 },
      { source: "persona2", target: "theme2", strength: 0.8 },
      { source: "persona2", target: "theme5", strength: 0.7 },
      { source: "persona3", target: "theme1", strength: 0.6 },
      { source: "persona3", target: "theme4", strength: 0.9 },
      { source: "persona4", target: "theme6", strength: 0.9 },

      // Persona connections to features
      { source: "persona1", target: "feature1", strength: 0.8 },
      { source: "persona1", target: "feature3", strength: 0.7 },
      { source: "persona1", target: "feature7", strength: 0.9 },
      { source: "persona2", target: "feature2", strength: 0.7 },
      { source: "persona2", target: "feature5", strength: 0.8 },
      { source: "persona3", target: "feature1", strength: 0.6 },
      { source: "persona3", target: "feature4", strength: 0.8 },
      { source: "persona4", target: "feature6", strength: 0.9 }
    ]
  };

  const [graphData, setGraphData] = useState(initialData);

  // Add this at the component level (outside any useEffect)
  const zoomRef = useRef(null);

  // Add these refs at the component level, near your other refs
  const miniMapSizeRef = useRef(0);
  const miniMapScaleRef = useRef(0);

  useEffect(() => {
    // Simulate loading data
    setLoading(true);
    setTimeout(() => {
      setLoading(false);
    }, 1000);
  }, []);

  const exportData = () => {
    const dataStr = JSON.stringify(graphData, null, 2);
    const dataUri = 'data:application/json;charset=utf-8,'+ encodeURIComponent(dataStr);

    const exportFileDefaultName = 'feedback-insights-data.json';

    const linkElement = document.createElement('a');
    linkElement.setAttribute('href', dataUri);
    linkElement.setAttribute('download', exportFileDefaultName);
    linkElement.click();
  };

  const importData = (event) => {
    const file = event.target.files[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onload = (e) => {
      try {
        const importedData = JSON.parse(e.target.result);
        // Validate the data structure
        if (importedData.nodes && importedData.links) {
          setGraphData(importedData);
        } else {
          setError("Invalid data format. The file must contain 'nodes' and 'links' arrays.");
        }
      } catch (err) {
        setError("Error parsing the file. Please make sure it's a valid JSON file.");
      }
    };
    reader.readAsText(file);
  };

  const importSimplifiedData = (event) => {
    const file = event.target.files[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onload = async (e) => {
      try {
        const importedData = JSON.parse(e.target.result);

        // Check if it has the simplified format
        if (importedData.feedback && Array.isArray(importedData.feedback)) {
          setLoading(true);
          setFeedbackItems(importedData.feedback);

          // Process with AI instead of the simple grouping
          await processFeedbackBatch(importedData.feedback);

          setLoading(false);
        } else if (importedData.nodes && importedData.links) {
          // Handle the original format
          setGraphData(importedData);
        } else {
          setError("Invalid data format. The file must contain either 'nodes' and 'links' arrays or a 'feedback' array.");
        }
      } catch (err) {
        setError("Error parsing the file. Please make sure it's a valid JSON file.");
        setLoading(false);
      }
    };
    reader.readAsText(file);
  };

  useEffect(() => {
    if (!svgRef.current || !dimensions.width) return;

    const svg = d3.select(svgRef.current);
    svg.selectAll("*").remove();

    const g = svg.append("g");

    // Create a zoom behavior and store it in the ref
    const zoom = d3.zoom()
      .scaleExtent([0.1, 4])
      .on("zoom", (event) => {
        g.attr("transform", event.transform);
      });

    // Store the zoom behavior in the ref
    zoomRef.current = zoom;

    svg.call(zoom);

    // Create a force simulation
    const simulation = d3.forceSimulation(graphData.nodes)
      .force("link", d3.forceLink(graphData.links)
        .id(d => d.id)
        .distance(d => 150 / (d.strength || 0.5))
      )
      .force("charge", d3.forceManyBody().strength(-300).distanceMax(500))
      .force("center", d3.forceCenter(dimensions.width / 2, dimensions.height / 2))
      .force("collide", d3.forceCollide().radius(d => {
        if (d.type === "theme") return 20 + (d.feedbackCount / feedbackWeight);
        if (d.type === "feature") {
          if (d.priority === "High") return 22;
          return 20;
        }
        return 22;
      }).strength(0.8))
      .force("x", d3.forceX(dimensions.width / 2).strength(0.01))
      .force("y", d3.forceY(dimensions.height / 2).strength(0.01));

    // Add this after your other forces
    simulation.nodes().forEach(node => {
      node.fx = null; // Remove any fixed positions
      node.fy = null;
    });

    // Add a small random jitter to initial positions
    graphData.nodes.forEach(node => {
      node.x = dimensions.width / 2 + (Math.random() - 0.5) * dimensions.width * 0.8;
      node.y = dimensions.height / 2 + (Math.random() - 0.5) * dimensions.height * 0.8;
    });

    // Optionally, add a small random force that changes over time
    let tick = 0;
    simulation.on("tick", () => {
      tick++;

      // Every 30 ticks, add a small random force
      if (tick % 30 === 0 && isSimulationRunning) {
        graphData.nodes.forEach(node => {
          node.vx = (node.vx || 0) + (Math.random() - 0.5) * 2;
          node.vy = (node.vy || 0) + (Math.random() - 0.5) * 2;
        });
      }

      // Update positions
      link
        .attr("x1", d => d.source.x)
        .attr("y1", d => d.source.y)
        .attr("x2", d => d.target.x)
        .attr("y2", d => d.target.y);

      nodeGroup.attr("transform", d => `translate(${d.x}, ${d.y})`);

      if (showMiniMap) {
        // Update mini-map nodes using the refs
        svg.selectAll(".mini-node")
          .attr("cx", d => (d.x || 0) * miniMapScaleRef.current + miniMapSizeRef.current / 2)
          .attr("cy", d => (d.y || 0) * miniMapScaleRef.current + miniMapSizeRef.current / 2);
      }
    });

    // Create links
    const link = g.append("g")
      .selectAll("line")
      .data(graphData.links)
      .enter()
      .append("line")
      .attr("stroke-width", d => d.strength * 2)
      .attr("stroke", d => {
        const sourceNode = graphData.nodes.find(n => n.id === (d.source.id || d.source));
        const targetNode = graphData.nodes.find(n => n.id === (d.target.id || d.target));

        if (!sourceNode || !targetNode) return "#999";

        if (sourceNode.type === "theme" && targetNode.type === "feature") {
          return "#6366F1";
        } else if (sourceNode.type === "feature" && targetNode.type === "theme") {
          return "#6366F1";
        } else if ((sourceNode.type === "persona" && targetNode.type === "theme") ||
                   (sourceNode.type === "theme" && targetNode.type === "persona")) {
          return "#F59E0B";
        } else if ((sourceNode.type === "persona" && targetNode.type === "feature") ||
                   (sourceNode.type === "feature" && targetNode.type === "persona")) {
          return "#10B981";
        }

        return "#999";
      })
      .attr("stroke-opacity", 0.7)
      .attr("stroke-dasharray", d => d.isNew ? "5,5" : "none");

    // Create node groups
    const nodeGroup = g.append("g")
      .selectAll("g")
      .data(graphData.nodes)
      .enter()
      .append("g")
      .call(d3.drag()
        .on("start", dragStarted)
        .on("drag", dragged)
        .on("end", dragEnded)
      )
      .on("click", (event, d) => {
        event.stopPropagation();
        handleNodeClick(d);
      });

    // Add circles to node groups
    const node = nodeGroup.append("circle")
      .attr("r", d => {
        if (d.type === "theme") return 10 + (d.feedbackCount / feedbackWeight);
        if (d.type === "feature") {
          if (d.priority === "High") return 12;
          return 10;
        }
        return 12;
      })
      .attr("fill", d => colorScale(d.type))
      .attr("stroke", "#fff")
      .attr("stroke-width", 1.5);

    // Add pulsing animation to theme nodes
    svg.selectAll("circle")
      .filter(d => d.type === "theme")
      .transition()
      .duration(2000)
      .attr("r", d => (10 + (d.feedbackCount / feedbackWeight)) * 1.2)
      .transition()
      .duration(2000)
      .attr("r", d => 10 + (d.feedbackCount / feedbackWeight))
      .on("end", function repeat() {
        d3.select(this)
          .transition()
          .duration(2000)
