      const graphWidth = Math.max(dimensions.width, xExtent[1] - xExtent[0] + 100);
      const graphHeight = Math.max(dimensions.height, yExtent[1] - yExtent[0] + 100);

      // Scale for mini-map
      const miniMapScale = Math.min(
        miniMapSize / graphWidth,
        miniMapSize / graphHeight
