        .attr("class", "viewport")
        .attr("width", dimensions.width * miniMapScale)
        .attr("height", dimensions.height * miniMapScale)
        .attr("x", (dimensions.width / 2) * miniMapScale - (dimensions.width * miniMapScale) / 2 + miniMapSize / 2)
        .attr("y", (dimensions.height / 2) * miniMapScale - (dimensions.height * miniMapScale) / 2 + miniMapSize / 2)
