              );

              if (existingInsight) {
                sentiment = existingInsight.sentiment;
                priority = existingInsight.priority;
                keywords = existingInsight.keywords || keywords;
              }
            }

            return {
              feedbackIndex: index,
              theme: node.name,
              text: item.text,
              user_role: item.user_role,
              sentiment,
              priority,
              keywords
            };
          }),
          theme: node.name,
          feedbackCount: node.feedbackCount || relatedFeedback.length
        };

        setAIInsights(themeInsights);
        setShowAIInsightsPanel(true);
      }
    }

    // If it's a persona node, show related feedback from this persona
    if (node.type === "persona") {
      const personaFeedback = feedbackItems.filter(item => item.user_role === node.name);

      if (personaFeedback.length > 0) {
        // Group feedback by theme
        const feedbackByTheme = {};

        personaFeedback.forEach(item => {
          let theme = "General Feedback";

          // Try to find theme classification for this feedback
          if (aiInsights && aiInsights.results) {
            const existingInsight = aiInsights.results.find(result => 
              feedbackItems[result.feedbackIndex] && 
              feedbackItems[result.feedbackIndex].id === item.id
            );

            if (existingInsight) {
              theme = existingInsight.theme;
            }
          }

          if (!feedbackByTheme[theme]) {
            feedbackByTheme[theme] = [];
          }

          feedbackByTheme[theme].push(item);
        });

        // Create insights object for this persona
        const personaInsights = {
          results: personaFeedback.map((item, index) => {
            // Try to find existing classification for this feedback
            let sentiment = "neutral";
            let priority = 5;
            let theme = "General Feedback";
            let keywords = item.text.split(' ')
              .filter(word => word.length > 3)
              .map(word => word.toLowerCase().replace(/[^a-z0-9]/g, ''))
              .slice(0, 3);

            // Check if we have AI insights for this feedback
            if (aiInsights && aiInsights.results) {
              const existingInsight = aiInsights.results.find(result => 
                feedbackItems[result.feedbackIndex] && 
                feedbackItems[result.feedbackIndex].id === item.id
              );

              if (existingInsight) {
                sentiment = existingInsight.sentiment;
                priority = existingInsight.priority;
                theme = existingInsight.theme;
                keywords = existingInsight.keywords || keywords;
              }
            }

            return {
              feedbackIndex: index,
              theme,
              text: item.text,
              user_role: item.user_role,
              sentiment,
              priority,
              keywords
            };
          }),
          persona: node.name,
          feedbackCount: personaFeedback.length,
          themeBreakdown: Object.keys(feedbackByTheme).map(theme => ({
            theme,
            count: feedbackByTheme[theme].length
          }))
        };

        setAIInsights(personaInsights);
        setShowAIInsightsPanel(true);
      }
    }

    // If it's a feature node, show related themes and personas
    if (node.type === "feature") {
      // Find connected themes and personas
      const connectedNodes = [];

      graphData.links.forEach(link => {
        if (link.source.id === node.id || link.target.id === node.id) {
          const connectedNodeId = link.source.id === node.id ? link.target.id : link.source.id;
          const connectedNode = graphData.nodes.find(n => n.id === connectedNodeId);

          if (connectedNode) {
            connectedNodes.push({
              ...connectedNode,
              connectionStrength: link.strength
            });
          }
        }
      });

      if (connectedNodes.length > 0) {
        // Group connected nodes by type
        const connectedThemes = connectedNodes.filter(n => n.type === "theme");
        const connectedPersonas = connectedNodes.filter(n => n.type === "persona");

        const featureInsights = {
          feature: node.name,
          priority: node.priority || "Medium",
          description: node.description || `Feature addressing user needs related to ${connectedThemes.map(t => t.name).join(", ")}`,
          connectedThemes,
          connectedPersonas,
          isFeatureView: true
        };

        setAIInsights(featureInsights);
        setShowAIInsightsPanel(true);
      }
    }
  };

  if (loading) {
    return (
      <div className="flex flex-col h-full items-center justify-center bg-gray-50">
        <div className="text-2xl text-blue-800 mb-4">Loading Insights Network...</div>
        <div className="w-16 h-16 border-4 border-blue-600 border-t-transparent rounded-full animate-spin"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="flex flex-col h-full items-center justify-center bg-gray-50">
        <div className="text-2xl text-red-800 mb-4">Error Loading Data</div>
        <div className="text-gray-600">{error}</div>
        <button 
          className="mt-4 px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
          onClick={() => window.location.reload()}
        >
          Retry
        </button>
      </div>
    );
  }

  return (
    <div className="flex flex-col h-full">
      <div className="bg-white p-4 shadow-md">
        <h1 className="text-2xl font-bold text-blue-800">User Research Insights Network</h1>
        <p className="text-gray-600">Visualizing connections between user feedback, features, and personas</p>

        <div className="flex flex-wrap gap-2 mt-4">
          <button 
            className="px-3 py-1 bg-blue-600 text-white rounded hover:bg-blue-700 text-sm"
            onClick={() => setIsSimulationRunning(!isSimulationRunning)}
          >
            {isSimulationRunning ? 'Pause' : 'Resume'} Simulation
          </button>

          <button 
            className="px-3 py-1 bg-green-600 text-white rounded hover:bg-green-700 text-sm"
            onClick={addTheme}
          >
            Add Feedback Theme
          </button>

          <button 
            className="px-3 py-1 bg-orange-600 text-white rounded hover:bg-orange-700 text-sm"
            onClick={addFeature}
          >
            Add Feature Idea
          </button>

          <button 
            className={`px-3 py-1 rounded text-sm ${selectedNode ? 'bg-purple-600 text-white hover:bg-purple-700' : 'bg-gray-300 text-gray-600'}`}
            onClick={connectNodes}
            disabled={!selectedNode}
          >
            {selectedNode ? `Connect "${selectedNode.name}"` : 'Select a Node to Connect'}
          </button>

          <button 
            className="px-3 py-1 bg-red-600 text-white rounded hover:bg-red-700 text-sm"
            onClick={resetVisualization}
          >
            Reset Visualization
          </button>

          <button 
            className="px-3 py-1 bg-indigo-600 text-white rounded hover:bg-indigo-700 text-sm"
            onClick={exportData}
          >
            Export Data
          </button>

          <div className="relative">
            <button 
              className="px-3 py-1 bg-teal-600 text-white rounded hover:bg-teal-700 text-sm"
              onClick={() => document.getElementById('file-upload').click()}
            >
              Import Data
            </button>
            <input 
              id="file-upload" 
              type="file" 
              accept=".json" 
              className="hidden" 
              onChange={importData} 
            />
          </div>

          <button 
            className="px-3 py-1 bg-gray-600 text-white rounded hover:bg-gray-700 text-sm"
            onClick={centerView}
          >
            Center View
          </button>

          <button 
            className="px-3 py-1 bg-blue-500 text-white rounded hover:bg-blue-600 text-sm"
            onClick={() => setShowMiniMap(!showMiniMap)}
          >
            {showMiniMap ? 'Hide' : 'Show'} Mini-Map
          </button>

          <button 
            className="px-3 py-1 bg-purple-600 text-white rounded hover:bg-purple-700 text-sm"
            onClick={() => setShowFeedbackModal(true)}
          >
            Add Feedback
          </button>
        </div>

        <div className="flex flex-wrap gap-4 mt-4">
          <div className="flex items-center space-x-2">
            <span className="text-sm text-gray-600">Feedback Weight:</span>
            <input 
              type="range" 
              min="2" 
              max="10" 
              value={feedbackWeight}
              onChange={(e) => setFeedbackWeight(Number(e.target.value))}
              className="w-32"
            />
          </div>

          <div className="flex items-center space-x-2">
            <span className="text-sm text-gray-600">Highlight:</span>
            <select 
              value={highlightMode}
              onChange={(e) => setHighlightMode(e.target.value)}
              className="border rounded px-2 py-1 text-sm"
            >
              <option value="none">None</option>
              <option value="themes">Feedback Themes</option>
              <option value="features">Feature Ideas</option>
              <option value="connections">Connected Nodes</option>
            </select>

            <button 
              className={`px-3 py-1 rounded text-sm ${highlightMode === 'connections' ? 'bg-indigo-600 text-white hover:bg-indigo-700' : 'bg-gray-200 text-gray-600 hover:bg-gray-300'}`}
              onClick={handleNodeSelection}
            >
              {selectedNode ? 'Clear Selection' : 'Select Random Node'}
            </button>
          </div>
        </div>
      </div>

      <div className="flex-1 bg-gray-50 p-4" ref={containerRef}>
        <svg 
          ref={svgRef} 
          width={dimensions.width} 
          height={dimensions.height}
          className="border border-gray-200 rounded-lg bg-white shadow-inner w-full h-full"
          onClick={() => setSelectedNode(null)}
        />
      </div>

      <div className="bg-white p-4 shadow-md mt-4">
        <div className="flex flex-wrap gap-8">
          <div className="flex items-center gap-2">
            <div className="w-4 h-4 rounded-full bg-blue-600"></div>
            <span className="text-sm">Feedback Themes</span>
          </div>
          <div className="flex items-center gap-2">
            <div className="w-4 h-4 rounded-full bg-green-600"></div>
            <span className="text-sm">Feature Ideas</span>
          </div>
          <div className="flex items-center gap-2">
            <div className="w-4 h-4 rounded-full bg-orange-600"></div>
            <span className="text-sm">User Personas</span>
          </div>

          <div className="flex-1"></div>

          {selectedNode && (
            <div className="bg-gray-100 px-4 py-2 rounded-md flex items-center">
              <div>
                <span className="font-medium">{selectedNode.name}</span>
                {selectedNode.type === 'theme' && (
                  <span> ({selectedNode.feedbackCount} feedbacks)</span>
                )}
              </div>
              <button
                className="ml-2 px-2 py-1 bg-gray-600 text-white rounded hover:bg-gray-700 text-xs"
                onClick={() => focusOnNode(selectedNode)}
              >
                Focus
              </button>
              <button
                className="ml-2 px-2 py-1 bg-purple-600 text-white rounded hover:bg-purple-700 text-xs"
                onClick={connectNodes}
              >
                Connect
              </button>
            </div>
          )}
        </div>
      </div>

      {/* Modal for adding new nodes */}
      {showModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded-lg shadow-lg w-96">
            <h2 className="text-xl font-bold mb-4">
              {modalType === 'theme' ? 'Add New Feedback Theme' : 'Add New Feature Idea'}
            </h2>

            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-bold mb-2">
                Name
              </label>
              <input
                type="text"
                value={newNodeName}
                onChange={(e) => setNewNodeName(e.target.value)}
                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
              />
            </div>

            {modalType === 'theme' ? (
              <div className="mb-4">
                <label className="block text-gray-700 text-sm font-bold mb-2">
                  Feedback Count
                </label>
                <input
                  type="number"
                  min="1"
                  max="100"
                  value={newNodeFeedbackCount}
                  onChange={(e) => setNewNodeFeedbackCount(Number(e.target.value))}
                  className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                />
              </div>
            ) : (
              <div className="mb-4">
                <label className="block text-gray-700 text-sm font-bold mb-2">
                  Priority
                </label>
                <select
                  value={newNodePriority}
                  onChange={(e) => setNewNodePriority(e.target.value)}
                  className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                >
                  <option value="Low">Low</option>
                  <option value="Medium">Medium</option>
                  <option value="High">High</option>
                </select>
              </div>
            )}

            <div className="flex justify-end gap-2">
              <button
                onClick={() => setShowModal(false)}
                className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-bold py-2 px-4 rounded"
              >
                Cancel
              </button>
              <button
                onClick={handleModalSubmit}
                className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
              >
                Add
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Modal for creating connections */}
      {showConnectionModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded-lg shadow-lg w-96">
            <h2 className="text-xl font-bold mb-4">Create Connection</h2>

            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-bold mb-2">
                From: {connectionSource?.name} ({connectionSource?.type})
              </label>
            </div>

            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-bold mb-2">
                To:
              </label>
              <select
                value={connectionTarget}
                onChange={(e) => setConnectionTarget(e.target.value)}
                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
              >
                {availableTargets.map(node => (
                  <option key={node.id} value={node.id}>
                    {node.name} ({node.type})
                  </option>
                ))}
              </select>
            </div>

            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-bold mb-2">
                Connection Strength: {connectionStrength}
              </label>
              <input
                type="range"
                min="0.1"
                max="1"
                step="0.1"
                value={connectionStrength}
                onChange={(e) => setConnectionStrength(Number(e.target.value))}
                className="w-full"
              />
              <div className="flex justify-between text-xs text-gray-500">
                <span>Weak</span>
                <span>Strong</span>
              </div>
            </div>

            <div className="flex justify-end gap-2">
              <button
                onClick={() => setShowConnectionModal(false)}
                className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-bold py-2 px-4 rounded"
              >
                Cancel
              </button>
              <button
                onClick={handleConnectionSubmit}
                className="bg-purple-600 hover:bg-purple-700 text-white font-bold py-2 px-4 rounded"
              >
                Connect
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Search results dropdown */}
      {showSearchResults && searchResults.length > 0 && (
        <div className="absolute mt-1 w-64 bg-white border rounded shadow-lg z-10">
          <div className="p-2 border-b text-xs font-semibold text-gray-500">
            {searchResults.length} results found
          </div>
          <div className="max-h-60 overflow-y-auto">
            {searchResults.map(node => (
              <div 
                key={node.id}
                className="p-2 hover:bg-gray-100 cursor-pointer flex items-center"
                onClick={() => {
                  setSelectedNode(node);
                  focusOnNode(node);
                  setShowSearchResults(false);
                }}
              >
                <div 
                  className="w-3 h-3 rounded-full mr-2"
                  style={{ backgroundColor: colorScale(node.type) }}
                ></div>
                <div>
                  <div className="text-sm font-medium">{node.name}</div>
                  <div className="text-xs text-gray-500">{node.type}</div>
                </div>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Add these buttons to your UI */}
      <div className="absolute bottom-4 right-4 flex gap-2">
        <button 
          className="w-10 h-10 bg-white rounded-full shadow-md flex items-center justify-center hover:bg-gray-100"
          onClick={zoomIn}
        >
          <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6v6m0 0v6m0-6h6m-6 0H6" />
          </svg>
        </button>
        <button 
          className="w-10 h-10 bg-white rounded-full shadow-md flex items-center justify-center hover:bg-gray-100"
          onClick={zoomOut}
        >
          <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M18 12H6" />
          </svg>
        </button>
        <button 
          className="w-10 h-10 bg-white rounded-full shadow-md flex items-center justify-center hover:bg-gray-100"
          onClick={centerView}
        >
          <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 12l2-2m0 0l7-7 7 7M5 10v10a1 1 0 001 1h3m10-11l2 2m-2-2v10a1 1 0 01-1 1h-3m-6 0a1 1 0 001-1v-4a1 1 0 011-1h2a1 1 0 011 1v4a1 1 0 001 1m-6 0h6" />
          </svg>
        </button>
      </div>

      {/* Add Feedback Modal */}
      {showFeedbackModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded-lg shadow-lg w-96">
            <h2 className="text-xl font-bold mb-4">Add New Feedback</h2>

            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-bold mb-2">
                Feedback Text
              </label>
              <textarea
                value={newFeedback}
                onChange={(e) => setNewFeedback(e.target.value)}
                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                rows="4"
                placeholder="Enter user feedback here..."
              />
            </div>

            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-bold mb-2">
                User Role
              </label>
              <input
                type="text"
                value={newFeedbackRole}
                onChange={(e) => setNewFeedbackRole(e.target.value)}
                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                placeholder="e.g., Product Manager, Developer, etc."
              />
            </div>

            <div className="flex justify-end gap-2">
              <button
                onClick={() => setShowFeedbackModal(false)}
                className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-bold py-2 px-4 rounded"
                disabled={isAIProcessing}
              >
                Cancel
              </button>
              <button
                onClick={handleFeedbackSubmit}
                className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded flex items-center"
                disabled={isAIProcessing}
              >
                {isAIProcessing ? (
                  <>
                    <svg className="animate-spin -ml-1 mr-2 h-4 w-4 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                      <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                      <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                    </svg>
                    Processing...
                  </>
                ) : (
                  'Add Feedback'
                )}
              </button>
            </div>

            {aiError && (
              <div className="mt-4 p-2 bg-red-100 text-red-700 rounded">
                {aiError}
              </div>
            )}
          </div>
        </div>
      )}

      {/* AI Insights Panel */}
      {showAIInsightsPanel && aiInsights && (
        <div className="fixed right-0 top-0 h-full w-80 bg-white shadow-lg p-4 overflow-y-auto z-40">
          <div className="flex justify-between items-center mb-4">
            <h2 className="text-lg font-bold">AI Insights</h2>
            <button 
              onClick={() => setShowAIInsightsPanel(false)}
              className="text-gray-500 hover:text-gray-700"
            >
              <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
              </svg>
            </button>
          </div>

          {aiInsights.newThemes && aiInsights.newThemes.length > 0 && (
            <div className="mb-4">
              <h3 className="font-semibold text-sm text-gray-600 mb-2">New Themes Identified</h3>
              {aiInsights.newThemes.map((theme, index) => (
                <div key={index} className="bg-blue-50 p-2 rounded mb-2">
                  <div className="font-medium">{theme.name}</div>
                  <div className="text-xs text-gray-600">{theme.description}</div>
                </div>
              ))}
            </div>
          )}

          <h3 className="font-semibold text-sm text-gray-600 mb-2">Feedback Classification</h3>
          {aiInsights.results.map((result, index) => (
            <div key={index} className="bg-gray-50 p-2 rounded mb-2">
              <div className="flex justify-between">
                <div className="font-medium">{result.theme}</div>
                <div className={`text-xs px-2 py-1 rounded ${
                  result.sentiment === 'positive' ? 'bg-green-100 text-green-800' : 
                  result.sentiment === 'negative' ? 'bg-red-100 text-red-800' : 
                  'bg-gray-100 text-gray-800'
                }`}>
                  {result.sentiment}
                </div>
              </div>
              <div className="text-xs text-gray-600 mt-1">
                Priority: <span className="font-medium">{result.priority}/10</span>
              </div>
              {result.keywords && (
                <div className="flex flex-wrap gap-1 mt-1">
                  {result.keywords.map((keyword, kidx) => (
                    <span key={kidx} className="text-xs bg-gray-200 px-1 rounded">
                      {keyword}
                    </span>
                  ))}
                </div>
              )}
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default UserResearchNetwork;
