  const createMiniMap = () => {
    if (!showMiniMap || !svgRef.current) return null;

    const svg = d3.select(svgRef.current);
    const width = dimensions.width;
    const height = dimensions.height;

    // Calculate mini-map size (25% of the main view, but not larger than 200px)
    const miniMapSize = Math.min(Math.min(width, height) * 0.25, 200);
    miniMapSizeRef.current = miniMapSize;

    // Calculate scale for mini-map (ratio of mini-map size to main view)
    const miniMapScale = miniMapSize / Math.max(width, height);
    miniMapScaleRef.current = miniMapScale;

    // Create mini-map container
    const miniMap = svg.append("g")
      .attr("class", "mini-map")
      .attr("transform", `translate(${width - miniMapSize - 10}, ${height - miniMapSize - 10})`);

    // Add background
    miniMap.append("rect")
      .attr("width", miniMapSize)
      .attr("height", miniMapSize)
      .attr("fill", "#f0f0f0")
      .attr("stroke", "#ccc")
      .attr("stroke-width", 1)
      .attr("rx", 5)
      .attr("ry", 5);

    // Create a group for the mini-map content
    const miniMapContent = miniMap.append("g");

    // Add mini-map nodes
    const miniNodes = miniMapContent.selectAll(".mini-node")
      .data(graphData.nodes)
      .enter()
      .append("circle")
      .attr("class", "mini-node")
      .attr("r", d => {
        if (d.type === "theme") return 3 + (d.feedbackCount / feedbackWeight) * 0.2;
        return 3;
      })
      .attr("fill", d => colorScale(d.type))
      .attr("opacity", 0.7);

    // Add mini-map links
    const miniLinks = miniMapContent.selectAll(".mini-link")
      .data(graphData.links)
      .enter()
      .append("line")
      .attr("class", "mini-link")
      .attr("stroke", "#999")
      .attr("stroke-width", 0.5)
      .attr("stroke-opacity", 0.5);

    // Create a viewport rectangle to show the current view
    const viewport = miniMap.append("rect")
      .attr("class", "mini-map-viewport")
      .attr("stroke", "#666")
      .attr("stroke-width", 1)
      .attr("fill", "none")
      .attr("stroke-dasharray", "3,3");

    // Create a force simulation for the mini-map
    const miniMapSimulation = d3.forceSimulation(graphData.nodes)
      .force("link", d3.forceLink(graphData.links).id(d => d.id).distance(10))
      .force("charge", d3.forceManyBody().strength(-30))
      .force("center", d3.forceCenter(miniMapSize / 2, miniMapSize / 2))
      .force("collide", d3.forceCollide(5));

    // Update positions on tick
    miniMapSimulation.on("tick", () => {
      miniLinks
        .attr("x1", d => d.source.x)
        .attr("y1", d => d.source.y)
        .attr("x2", d => d.target.x)
        .attr("y2", d => d.target.y);

      miniNodes
        .attr("cx", d => d.x)
        .attr("cy", d => d.y);
    });

    // Add click handler to mini-map for navigation
    miniMap.on("click", (event) => {
      if (!zoomRef.current) return;

      // Get click position relative to mini-map
      const [x, y] = d3.pointer(event);

      // Calculate the target center in the main view coordinates
      const targetX = (x / miniMapScale) - (width / 2);
      const targetY = (y / miniMapScale) - (height / 2);

      // Apply zoom transform to center on the clicked point
      svg.transition().duration(500).call(
        zoomRef.current.transform,
        d3.zoomIdentity.translate(-targetX, -targetY)
      );
    });

    return miniMapSimulation;
  };

  // Now, find the useEffect that calls createMiniMap and make sure it's properly implemented
  // It should look something like this:
  useEffect(() => {
    if (showMiniMap && svgRef.current && dimensions.width) {
      const miniMapSimulation = createMiniMap();

      // Clean up
      return () => {
        if (miniMapSimulation) miniMapSimulation.stop();
      };
    }
  }, [showMiniMap, graphData, dimensions, feedbackWeight]);

