                </select>
              </div>
            )}

            <div className="flex justify-end space-x-2">
              <button 
                className="px-4 py-2 bg-gray-300 text-gray-700 rounded hover:bg-gray-400"
                onClick={() => {
                  setShowModal(false);
                  setNewNodeName('');
                  setNewNodeFeedbackCount(10);
                  setNewNodePriority('Medium');
                }}
              >
                Cancel
              </button>
              <button 
                className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
                onClick={() => {
                  if (!newNodeName.trim()) {
                    alert("Please enter a name");
                    return;
                  }

                  const newNode = {
                    id: `${modalType}-${Date.now()}`,
                    name: newNodeName,
                    type: modalType,
                    ...(modalType === 'theme' ? { feedbackCount: newNodeFeedbackCount } : {}),
                    ...(modalType === 'feature' ? { priority: newNodePriority } : {})
                  };

                  setGraphData({
                    nodes: [...graphData.nodes, newNode],
                    links: [...graphData.links]
                  });

                  setShowModal(false);
                  setNewNodeName('');
                  setNewNodeFeedbackCount(10);
                  setNewNodePriority('Medium');
                }}
              >
                Add
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Connection Modal */}
      {showConnectionModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded-lg shadow-xl w-96">
            <h2 className="text-xl font-bold mb-4">
              Connect Nodes
            </h2>

            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-bold mb-2">
                From
              </label>
              <div className="px-3 py-2 border border-gray-300 rounded-md bg-gray-50">
                {connectionSource?.name} ({connectionSource?.type})
              </div>
            </div>

            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-bold mb-2">
                To
              </label>
              <select 
                value={connectionTarget}
                onChange={(e) => setConnectionTarget(e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              >
                <option value="">Select a target node</option>
                {availableTargets.map((target) => (
                  <option key={target.id} value={target.id}>
                    {target.name} ({target.type})
                  </option>
                ))}
              </select>
            </div>

            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-bold mb-2">
                Connection Strength: {connectionStrength.toFixed(1)}
              </label>
              <input 
                type="range" 
                min="0.1" 
                max="1" 
                step="0.1"
                value={connectionStrength}
                onChange={(e) => setConnectionStrength(parseFloat(e.target.value))}
                className="w-full"
              />
            </div>

            <div className="flex justify-end space-x-2">
              <button 
                className="px-4 py-2 bg-gray-300 text-gray-700 rounded hover:bg-gray-400"
                onClick={() => {
                  setShowConnectionModal(false);
                  setConnectionSource(null);
                  setConnectionTarget('');
                  setConnectionStrength(0.5);
                }}
              >
                Cancel
              </button>
              <button 
                className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
                onClick={() => {
                  if (!connectionTarget) {
                    alert("Please select a target node");
                    return;
                  }

                  // Check if connection already exists
                  const connectionExists = graphData.links.some(link => 
                    (link.source === connectionSource.id && link.target === connectionTarget) ||
                    (link.source === connectionTarget && link.target === connectionSource.id)
                  );

                  if (connectionExists) {
                    alert("This connection already exists");
                    return;
                  }

                  const newLink = {
                    source: connectionSource.id,
                    target: connectionTarget,
                    strength: connectionStrength
                  };

                  setGraphData({
                    nodes: [...graphData.nodes],
                    links: [...graphData.links, newLink]
                  });

                  setShowConnectionModal(false);
                  setConnectionSource(null);
                  setConnectionTarget('');
                  setConnectionStrength(0.5);
                }}
              >
                Connect
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Add Feedback Modal */}
      {showFeedbackModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded-lg shadow-xl w-96">
            <h2 className="text-xl font-bold mb-4">
              Add New Feedback
            </h2>

            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-bold mb-2">
                Feedback Text
              </label>
              <textarea 
                value={newFeedback}
                onChange={(e) => setNewFeedback(e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                placeholder="Enter user feedback"
                rows={4}
              />
            </div>

            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-bold mb-2">
                User Role
              </label>
              <input 
                type="text" 
                value={newFeedbackRole}
                onChange={(e) => setNewFeedbackRole(e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                placeholder="e.g. Mobile User, Admin, Power User"
              />
            </div>

            <div className="flex justify-end space-x-2">
              <button 
                className="px-4 py-2 bg-gray-300 text-gray-700 rounded hover:bg-gray-400"
                onClick={() => {
                  setShowFeedbackModal(false);
                  setNewFeedback('');
                  setNewFeedbackRole('');
                }}
              >
                Cancel
              </button>
              <button 
                className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
                onClick={handleAddFeedback}
                disabled={isAIProcessing}
              >
                {isAIProcessing ? (
                  <div className="flex items-center">
                    <div className="w-4 h-4 border-2 border-white border-t-transparent rounded-full animate-spin mr-2"></div>
                    Processing...
                  </div>
                ) : 'Submit'}
              </button>
            </div>

            {aiError && (
              <div className="mt-4 p-2 bg-red-100 text-red-700 rounded">
                {aiError}
              </div>
            )}
          </div>
        </div>
      )}

      {/* Import Data Modal */}
      {showModal && modalType === 'import' && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded-lg shadow-lg max-w-md w-full">
            <h2 className="text-xl font-bold mb-4">Import Data</h2>

            <div className="mb-4">
              <p className="text-gray-600 mb-2">
                You can import data in two formats:
              </p>
              <ol className="list-decimal pl-5 mb-3 text-sm">
                <li className="mb-1">
                  <strong>Simple feedback format:</strong> A JSON file with a "feedback" array containing user feedback items
                </li>
                <li className="mb-1">
                  <strong>Advanced graph format:</strong> A JSON file with "nodes" and "links" arrays for direct graph manipulation
                </li>
              </ol>

              <div className="flex justify-between mb-4">
                <button
                  onClick={downloadSampleTemplate}
                  className="text-blue-600 text-sm hover:underline flex items-center"
                >
                  <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4 mr-1" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M7 16a4 4 0 01-.88-7.903A5 5 0 1115.9 6L16 6a5 5 0 011 9.9M9 19l3 3m0 0l3-3m-3 3V10" />
                  </svg>
                  Download Sample Template
                </button>
              </div>
            </div>

            <div className="mb-4">
              <label className="block text-gray-700 mb-2">Select JSON File</label>
              <input 
                type="file" 
                accept=".json"
                onChange={importData}
                className="w-full p-2 border border-gray-300 rounded"
              />
            </div>

            {error && (
              <div className="mb-4 p-3 bg-red-100 text-red-800 rounded">
                <p className="font-semibold mb-1">Error:</p>
                <p className="whitespace-pre-line text-sm">{error}</p>
              </div>
            )}

            <div className="flex justify-end">
              <button 
                onClick={() => {
                  setShowModal(false);
                  setError(null);
                }}
                className="px-4 py-2 bg-gray-200 text-gray-800 rounded hover:bg-gray-300 mr-2"
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default UserResearchNetwork;
