        if (matchingItem && matchingItem.text) {
          // Use the full text from the matching item
          insights.feedbackText = matchingItem.text;

          // Also update user role and timestamp if available
          if (matchingItem.user_role) {
            insights.user_role = matchingItem.user_role;
          }
          if (matchingItem.timestamp) {
            insights.timestamp = matchingItem.timestamp;
          }
        }
      }

      // Always prefer the node's user_role if available
      insights.user_role = node.user_role || node.persona || node.role || insights.user_role || "Unknown User";
      insights.timestamp = node.timestamp || node.date || node.created_at || new Date().toISOString();

      // Get related themes if available
      if (node.themes) {
        insights.relatedThemes = node.themes;
      } else {
        // Try to find related themes from connections
        const relatedThemes = connectedNodes
          .filter(n => n.type === "theme")
          .map(n => n.name);

        if (relatedThemes.length > 0) {
          insights.relatedThemes = relatedThemes;
        }
      }

      // Get sentiment if available
      if (node.sentiment) {
        insights.sentiment = node.sentiment;
      }
    }

    // Update insights and show panel
    setAIInsights(insights);
    setShowAIInsightsPanel(true);

    // Ensure mini-map is recreated if it should be visible
    if (showMiniMap) {
      // Use setTimeout to ensure this happens after the current render cycle
      setTimeout(() => {
        // Call the function directly instead of using a reference
        if (svgRef.current) {
          d3.select(svgRef.current).selectAll(".mini-map-container").remove();
          createMiniMap();
        }
      }, 0);
    }
  }, [graphData, feedbackItems, showMiniMap]);

  // Zoom control functions
  const zoomIn = useCallback(() => {
    if (!svgRef.current || !zoomRef.current) return;

    const svg = d3.select(svgRef.current);
    const currentTransform = d3.zoomTransform(svg.node());

    svg.transition()
      .duration(300)
      .call(
        zoomRef.current.transform,
        d3.zoomIdentity
          .translate(currentTransform.x, currentTransform.y)
          .scale(currentTransform.k * 1.3)
      );
  }, []);

  const zoomOut = useCallback(() => {
    if (!svgRef.current || !zoomRef.current) return;

    const svg = d3.select(svgRef.current);
    const currentTransform = d3.zoomTransform(svg.node());

    svg.transition()
      .duration(300)
      .call(
        zoomRef.current.transform,
        d3.zoomIdentity
          .translate(currentTransform.x, currentTransform.y)
          .scale(currentTransform.k / 1.3)
      );
  }, []);

  const centerView = useCallback(() => {
    if (!svgRef.current || !zoomRef.current) return;

    const svg = d3.select(svgRef.current);

    // Calculate the center point
    const centerX = dimensions.width / 2;
    const centerY = dimensions.height / 2;

    // Create a transform that centers the view
    const transform = d3.zoomIdentity
      .translate(centerX, centerY)
      .scale(1)
      .translate(-centerX, -centerY);

    // Apply the transform with a smooth transition
    svg.transition()
      .duration(750)
      .call(zoomRef.current.transform, transform);
  }, [dimensions]);

  const fitToScreen = useCallback(() => {
    if (!svgRef.current || !zoomRef.current || !graphData.nodes.length) return;

    const svg = d3.select(svgRef.current);

    // Find the bounds of all nodes
    let minX = Infinity, minY = Infinity, maxX = -Infinity, maxY = -Infinity;

    graphData.nodes.forEach(node => {
      if (!node.x || !node.y) return;

      if (node.x < minX) minX = node.x;
      if (node.y < minY) minY = node.y;
      if (node.x > maxX) maxX = node.x;
      if (node.y > maxY) maxY = node.y;
    });

    // If we don't have valid bounds, center view instead
    if (!isFinite(minX) || !isFinite(minY) || !isFinite(maxX) || !isFinite(maxY)) {
      centerView();
      return;
    }

    // Add padding
    const padding = 50;
    minX -= padding;
    minY -= padding;
    maxX += padding;
    maxY += padding;

    // Calculate width and height of the content
    const contentWidth = maxX - minX;
    const contentHeight = maxY - minY;

    // Calculate scale to fit the content
    const scale = Math.min(
      dimensions.width / contentWidth,
      dimensions.height / contentHeight,
      2 // Maximum scale factor
    ) * 0.9; // 90% to add some margin

    // Calculate the center of the content
    const centerX = (minX + maxX) / 2;
    const centerY = (minY + maxY) / 2;

    // Create a transform that centers and scales the content
    const transform = d3.zoomIdentity
      .translate(dimensions.width / 2, dimensions.height / 2)
      .scale(scale)
      .translate(-centerX, -centerY);

    // Apply the transform with a smooth transition
    svg.transition()
      .duration(750)
      .call(zoomRef.current.transform, transform);
  }, [dimensions, graphData.nodes, centerView]);

  // Toggle mini-map visibility
  const toggleMiniMap = useCallback(() => {
    setShowMiniMap(!showMiniMap);
  }, [showMiniMap]);

  const handleDataImported = (importedData) => {
    if (importedData) {
      setGraphData({ 
        nodes: importedData.nodes || [], 
        links: importedData.links || [] 
      });
      setInsights(importedData.insights || []);
    }
  };

  // Add a helper function to connect feedback to theme by ID
  const connectFeedbackToThemeById = useCallback((feedbackId, themeId) => {
    // Find the theme node
    const themeNode = graphData.nodes.find(node => node.id === themeId);
    if (!themeNode) return;

    // Update theme's feedback count
    setGraphData(prevData => ({
      ...prevData,
      nodes: prevData.nodes.map(node => 
        node.id === themeId 
          ? { ...node, feedbackCount: (node.feedbackCount || 0) + 1 }
          : node
      )
    }));

    // Add connection between feedback and theme
    setGraphData(prevData => {
      // Create the new link
      const newLink = {
        source: feedbackId,
        target: themeId,
        strength: 0.7
      };

      // Add link only if it doesn't already exist
      const linkExists = prevData.links.some(link => {
        const sourceId = typeof link.source === 'object' ? link.source.id : link.source;
        const targetId = typeof link.target === 'object' ? link.target.id : link.target;
        return (sourceId === feedbackId && targetId === themeId) || 
               (sourceId === themeId && targetId === feedbackId);
      });

      return {
        nodes: prevData.nodes,
        links: linkExists ? prevData.links : [...prevData.links, newLink]
      };
    });

    // If the user role is provided, try to connect to a persona
    const feedbackNode = graphData.nodes.find(n => n.id === feedbackId);
    if (feedbackNode && feedbackNode.user_role) {
      connectFeedbackToPersona(feedbackId, feedbackNode.user_role);
    }

    // Restart the simulation to reposition nodes
    if (simulationRef.current) {
      simulationRef.current.alpha(0.3).restart();
    }

    // Highlight the new feedback momentarily
    setTimeout(() => {
      const svgNode = d3.select(svgRef.current);
      const feedbackCircle = svgNode.selectAll(".nodes circle").filter(d => d.id === feedbackId);

      if (!feedbackCircle.empty()) {
        feedbackCircle
          .transition()
          .duration(500)
          .attr("r", 15)
          .transition()
          .duration(500)
          .attr("r", 10);
      }
    }, 500);
  }, [graphData, connectFeedbackToPersona]);

  // Loading screen
  if (loading) {
    return (
      <div className="flex flex-col h-full items-center justify-center bg-gray-50">
        <div className="text-2xl text-blue-800 mb-4">Loading Insights Network...</div>
        <div className="w-16 h-16 border-4 border-blue-600 border-t-transparent rounded-full animate-spin"></div>
      </div>
    );
  }

  // Error screen
  if (error) {
    return (
      <div className="flex flex-col h-full items-center justify-center bg-gray-50">
        <div className="text-2xl text-red-800 mb-4">Error Loading Data</div>
        <div className="text-gray-600">{error}</div>
        <button 
          className="mt-4 px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
          onClick={() => window.location.reload()}
        >
          Retry
        </button>
      </div>
    );
  }

  // Export the current graph data
  const exportData = useCallback(() => {
    const dataStr = JSON.stringify(graphData, null, 2);
    const dataUri = 'data:application/json;charset=utf-8,'+ encodeURIComponent(dataStr);

    const exportFileDefaultName = 'feedback-insights-data.json';

    const linkElement = document.createElement('a');
    linkElement.setAttribute('href', dataUri);
    linkElement.setAttribute('download', exportFileDefaultName);
    linkElement.click();
  }, [graphData]);

  // Reset to initial data
  const resetVisualization = useCallback(() => {
    if (window.confirm("Are you sure you want to reset? This will remove all custom data.")) {
      setGraphData(initialData);
      setSelectedNode(null);
      setHighlightMode('none');
    }
  }, []);

  // Helper function to connect feedback to a persona
  const connectFeedbackToPersona = useCallback((feedbackId, roleName) => {
    // Skip if no role name
    if (!roleName) return;

    // Check if persona exists
    let personaNode = graphData.nodes.find(node => 
      node.type === "persona" && node.name.toLowerCase() === roleName.toLowerCase()
    );

    // If persona doesn't exist, create it
    if (!personaNode) {
      personaNode = {
        id: `persona-${roleName.replace(/\s+/g, '-').toLowerCase()}`,
        name: roleName,
        type: "persona"
      };

      // Add the new persona node
      setGraphData(prevData => ({
        nodes: [...prevData.nodes, personaNode],
        links: [...prevData.links]
      }));
    }

    // Add connection between feedback and persona
    setGraphData(prevData => {
      // Create the new link
      const newLink = {
        source: feedbackId,
        target: personaNode.id,
        strength: 0.5
      };

      // Add link only if it doesn't already exist
      const linkExists = prevData.links.some(link => {
        const sourceId = typeof link.source === 'object' ? link.source.id : link.source;
        const targetId = typeof link.target === 'object' ? link.target.id : link.target;
        return (sourceId === feedbackId && targetId === personaNode.id) || 
               (sourceId === personaNode.id && targetId === feedbackId);
      });

      return {
        nodes: prevData.nodes,
        links: linkExists ? prevData.links : [...prevData.links, newLink]
      };
    });
  }, [graphData]);

  // Helper function to connect feedback to a theme
  const connectFeedbackToTheme = useCallback((feedbackId, themeName) => {
    // Check if theme exists
    let themeNode = graphData.nodes.find(node => 
      node.type === "theme" && node.name.toLowerCase() === themeName.toLowerCase()
    );

    // If theme doesn't exist, create it
    if (!themeNode) {
      themeNode = {
        id: `theme-${themeName.replace(/\s+/g, '-').toLowerCase()}`,
        name: themeName,
        type: "theme",
        feedbackCount: 1
      };

      // Add the new theme node
      setGraphData(prevData => ({
        nodes: [...prevData.nodes, themeNode],
        links: [...prevData.links]
      }));
    } else {
      // Update theme's feedback count
      setGraphData(prevData => ({
        ...prevData,
        nodes: prevData.nodes.map(node => 
          node.id === themeNode.id 
            ? { ...node, feedbackCount: (node.feedbackCount || 0) + 1 }
            : node
        )
      }));
    }

    // Add connection between feedback and theme
    setGraphData(prevData => {
      // Create the new link
      const newLink = {
        source: feedbackId,
        target: themeNode.id,
        strength: 0.7
      };

      // Add link only if it doesn't already exist
      const linkExists = prevData.links.some(link => {
        const sourceId = typeof link.source === 'object' ? link.source.id : link.source;
        const targetId = typeof link.target === 'object' ? link.target.id : link.target;
        return (sourceId === feedbackId && targetId === themeNode.id) || 
               (sourceId === themeNode.id && targetId === feedbackId);
      });

      return {
        nodes: prevData.nodes,
        links: linkExists ? prevData.links : [...prevData.links, newLink]
      };
    });

    // If the user role is provided, try to connect to a persona
    const feedbackNode = graphData.nodes.find(n => n.id === feedbackId);
    if (feedbackNode && feedbackNode.user_role) {
      connectFeedbackToPersona(feedbackId, feedbackNode.user_role);
    }

    // Restart the simulation to reposition nodes
    if (simulationRef.current) {
      simulationRef.current.alpha(0.3).restart();
    }

    // Highlight the new feedback momentarily
    setTimeout(() => {
      const svgNode = d3.select(svgRef.current);
      const feedbackCircle = svgNode.selectAll(".nodes circle").filter(d => d.id === feedbackId);

      if (!feedbackCircle.empty()) {
        feedbackCircle
          .transition()
          .duration(500)
          .attr("r", 15)
          .transition()
          .duration(500)
          .attr("r", 10);
      }
    }, 500);
  }, [graphData, connectFeedbackToPersona]);

  // Handle adding new feedback with better visualization
  const handleAddFeedback = useCallback(() => {
    if (!newFeedback) {
      alert("Please enter feedback text");
      return;
    }

    setIsAIProcessing(true);
    setAIError(null);

    // Generate a unique ID for the new feedback
    const feedbackId = `feedback-${Date.now()}`;

    // Create a new feedback object
    const feedbackItem = {
      id: feedbackId,
      text: newFeedback,
      user_role: newFeedbackRole || "User",
      timestamp: new Date().toISOString(),
      type: "feedback"
    };

    // Add to feedbackItems array
    setFeedbackItems(prev => [...prev, feedbackItem]);

    // First pass: Add feedback node to visualization
    const newNode = {
      id: feedbackId,
      name: newFeedback.length > 30 ? newFeedback.substring(0, 30) + "..." : newFeedback,
      text: newFeedback, // Store full text
      fullText: newFeedback, // Store full text for panel view
      type: "feedback",
      user_role: newFeedbackRole,
      timestamp: new Date().toISOString()
    };

    // Update graph with new node
    setGraphData(prevData => ({
      nodes: [...prevData.nodes, newNode],
      links: [...prevData.links]
    }));

    // If the user selected a specific theme, connect to it directly
    if (selectedThemeForFeedback) {
      const selectedTheme = graphData.nodes.find(node => node.id === selectedThemeForFeedback);
      if (selectedTheme) {
        // Connect to the selected theme
        connectFeedbackToThemeById(feedbackId, selectedThemeForFeedback);
        setIsAIProcessing(false);
      }
    } else {
      // No theme selected - use AI or fallback as before
      try {
        // First try to use the AI service if available
        classifyFeedback(newFeedback)
          .then(classification => {
            // Successfully classified with AI
            connectFeedbackToTheme(feedbackId, classification.theme);
            setIsAIProcessing(false);
          })
          .catch(error => {
            console.warn("AI classification failed, using fallback:", error);
            // Use fallback classification
            const fallbackClassification = mockClassifyFeedback(newFeedback);
            connectFeedbackToTheme(feedbackId, fallbackClassification.theme);
            setIsAIProcessing(false);
          });
      } catch (error) {
        console.error("Error in feedback processing:", error);
        // Still use fallback even if there's an error
        const fallbackClassification = mockClassifyFeedback(newFeedback);
        connectFeedbackToTheme(feedbackId, fallbackClassification.theme);
        setIsAIProcessing(false);
        setAIError("Error processing feedback, but added with basic classification");
      }
    }

    // Reset form fields
    setShowFeedbackModal(false);
    setNewFeedback('');
    setNewFeedbackRole('');
    setSelectedThemeForFeedback('');
  }, [newFeedback, newFeedbackRole, graphData.nodes, selectedThemeForFeedback, connectFeedbackToThemeById, connectFeedbackToTheme]);

  return (
    <div className="flex flex-col h-full">
      {/* Header and controls */}
      <div className="bg-white p-4 shadow-md">
        <div className="flex justify-between items-center mb-4">
          <div>
            <h1 className="text-2xl font-bold text-blue-800">User Research Insights Network</h1>
            <p className="text-gray-600">Visualizing connections between user feedback, features, and personas</p>
          </div>

          {/* Search bar */}
          <div className="relative w-64">
            <input
              type="text"
              placeholder="Search nodes..."
              value={searchTerm}
              onChange={(e) => {
                setSearchTerm(e.target.value);
                if (e.target.value.length >= 2) {
                  const results = graphData.nodes.filter(node => 
                    node.name.toLowerCase().includes(e.target.value.toLowerCase())
                  );
                  setSearchResults(results);
                  setShowSearchResults(true);
                } else {
                  setShowSearchResults(false);
                  setSearchResults([]);
                }
              }}
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
            {showSearchResults && searchResults.length > 0 && (
              <div className="absolute top-full left-0 right-0 mt-1 bg-white border border-gray-300 rounded-md shadow-lg z-20 max-h-60 overflow-y-auto">
                {searchResults.map((result, index) => (
                  <div 
                    key={index}
                    className="px-3 py-2 hover:bg-gray-100 cursor-pointer"
                    onClick={() => {
                      setSelectedNode(result);
                      handleNodeClick(result);
                      setShowSearchResults(false);
                    }}
                  >
                    <div className="flex items-center">
                      <div 
                        className="w-3 h-3 rounded-full mr-2"
                        style={{ backgroundColor: colorScale(result.type) }}
                      ></div>
                      <span>{result.name}</span>
                      <span className="ml-2 text-xs text-gray-500">({result.type})</span>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>

        {/* Controls */}
        <div className="flex flex-wrap gap-2 mb-4">
          <div className="flex items-center space-x-2">
            <span className="text-sm text-gray-600">Data:</span>
            <button 
              className="px-3 py-1 bg-blue-600 text-white rounded hover:bg-blue-700 text-sm"
              onClick={exportData}
            >
              Export
            </button>
            <label className="px-3 py-1 bg-blue-600 text-white rounded hover:bg-blue-700 text-sm cursor-pointer">
              Import
              <input 
                type="file" 
                accept=".json" 
                className="hidden" 
                onChange={importData}
              />
            </label>
            <button 
              className="px-3 py-1 bg-red-600 text-white rounded hover:bg-red-700 text-sm"
              onClick={resetVisualization}
            >
              Reset
            </button>
          </div>

          <div className="flex items-center space-x-2">
            <span className="text-sm text-gray-600">View:</span>
            <button 
              className="px-3 py-1 bg-purple-600 text-white rounded hover:bg-purple-700 text-sm"
              onClick={centerView}
            >
              Center View
            </button>
            <button 
              className="px-3 py-1 bg-purple-600 text-white rounded hover:bg-purple-700 text-sm"
              onClick={toggleMiniMap}
            >
              {showMiniMap ? 'Hide Mini-Map' : 'Show Mini-Map'}
            </button>
            <button 
              className="px-3 py-1 bg-blue-600 text-white rounded hover:bg-blue-700 text-sm"
              onClick={fitToScreen}
              title="Fit all nodes to screen"
            >
              Fit to Screen
            </button>
          </div>

          {/* Remove the "Add" section with buttons */}
        </div>

        <div className="flex flex-wrap gap-4">
          <div className="flex items-center space-x-2">
            <span className="text-sm text-gray-600">Feedback Weight:</span>
            <input 
              type="range" 
              min="1" 
              max="10" 
              value={feedbackWeight}
              onChange={(e) => setFeedbackWeight(parseInt(e.target.value))}
              className="w-32"
            />
            <span className="text-sm text-gray-600">{feedbackWeight}</span>
          </div>

          <div className="flex items-center space-x-2">
            <span className="text-sm text-gray-600">Highlight:</span>
            <button 
              className={`px-2 py-1 rounded text-xs ${highlightMode === 'none' ? 'bg-blue-600 text-white' : 'bg-gray-200 text-gray-700'}`}
              onClick={() => setHighlightMode('none')}
            >
              None
            </button>
            <button 
              className={`px-2 py-1 rounded text-xs ${highlightMode === 'themes' ? 'bg-blue-600 text-white' : 'bg-gray-200 text-gray-700'}`}
              onClick={() => setHighlightMode('themes')}
            >
              Themes
            </button>
            <button 
              className={`px-2 py-1 rounded text-xs ${highlightMode === 'feedback' ? 'bg-blue-600 text-white' : 'bg-gray-200 text-gray-700'}`}
              onClick={() => setHighlightMode('feedback')}
            >
              Feedback
            </button>
            <button 
              className={`px-2 py-1 rounded text-xs ${highlightMode === 'personas' ? 'bg-blue-600 text-white' : 'bg-gray-200 text-gray-700'}`}
              onClick={() => setHighlightMode('personas')}
            >
              Personas
            </button>
          </div>
        </div>
      </div>

      {/* Main visualization container */}
      <div className="flex flex-1 relative">
        <div 
          ref={containerRef} 
          className="flex-1 bg-gray-50 p-4 transition-all duration-300"
          style={{
            marginRight: showAIInsightsPanel && !isPanelCollapsed ? `${panelWidth}px` : '0',
            minHeight: '500px'
          }}
        >
          <svg 
            ref={svgRef} 
            width="100%" 
            height="100%"
            className="border border-gray-200 rounded-lg bg-white shadow-inner w-full h-full"
            style={{ minHeight: '500px' }}
            onClick={() => setSelectedNode(null)}
          />

          {/* Add Legend */}
          <Legend colorScale={colorScale} />

          {/* Zoom controls */}
          <div className="absolute bottom-8 right-8 flex flex-col bg-white rounded-lg shadow p-2 space-y-2 z-10">
            <button 
              className="w-8 h-8 flex items-center justify-center bg-gray-100 hover:bg-gray-200 rounded-full"
              onClick={zoomIn}
            >
              <span className="text-xl">+</span>
            </button>
            <button 
              className="w-8 h-8 flex items-center justify-center bg-gray-100 hover:bg-gray-200 rounded-full"
              onClick={zoomOut}
            >
              <span className="text-xl">-</span>
            </button>
            <button 
              className="w-8 h-8 flex items-center justify-center bg-gray-100 hover:bg-gray-200 rounded-full"
              onClick={fitToScreen}
            >
              <span className="text-xl">⊡</span>
            </button>
          </div>
        </div>

        {/* AI Insights Panel */}
        {showAIInsightsPanel && aiInsights && (
          <div 
            className={`fixed right-0 top-0 bottom-0 bg-white shadow-lg z-40 transition-all duration-300 flex`}
            style={{ 
              width: isPanelCollapsed ? '50px' : `${panelWidth}px`,
              maxWidth: '40%'
            }}
          >
            {/* Collapse/Expand Button */}
            <div className="w-10 flex flex-col items-center pt-4 border-r border-gray-200 bg-gray-50 shrink-0">
              <button 
                onClick={() => setIsPanelCollapsed(!isPanelCollapsed)}
                className="w-8 h-8 flex items-center justify-center rounded-full bg-white hover:bg-gray-100 text-gray-600 shadow-sm"
                title={isPanelCollapsed ? "Expand panel" : "Collapse panel"}
              >
                <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d={
                    isPanelCollapsed 
                      ? "M13 5l7 7-7 7M5 5l7 7-7 7" // Expand icon
                      : "M11 19l-7-7 7-7M19 19l-7-7 7-7" // Collapse icon
                  } />
                </svg>
              </button>

              {isPanelCollapsed && (
                <div className="mt-4 transform -rotate-90 whitespace-nowrap text-sm font-medium text-gray-600 origin-center">
                  {aiInsights.isFeatureView ? 'Feature Details' : 
                   aiInsights.persona ? `${aiInsights.persona}` : 
                   aiInsights.theme ? `${aiInsights.theme}` : 'AI Insights'}
                </div>
              )}

              <button 
                onClick={() => setShowAIInsightsPanel(false)}
                className="w-8 h-8 flex items-center justify-center rounded-full bg-white hover:bg-gray-100 text-gray-600 mt-4 shadow-sm"
                title="Close panel"
              >
                <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                </svg>
              </button>
            </div>

            {/* Panel Content - Only shown when expanded */}
            {!isPanelCollapsed && (
              <div className="flex-1 overflow-y-auto p-6 w-full" style={{ maxHeight: '100vh' }}>
                <h2 className="text-2xl font-bold text-gray-800 mb-6 sticky top-0 bg-white pt-2 pb-3 z-10">
                  {aiInsights.isFeatureView ? 'Feature Details' : 
                   aiInsights.persona ? `${aiInsights.persona} Details` : 
                   aiInsights.theme ? `${aiInsights.theme} Details` : 
                   aiInsights.isFeedbackView ? 'Feedback Details' : 'AI Feedback Insights'}
                </h2>

                {/* Node Details Section - New section for showing node-specific info */}
                <div className="mb-8 bg-white p-4 rounded-lg border border-gray-200 shadow-sm overflow-hidden">
                  {/* Persona Node Details */}
                  {aiInsights.persona && (
                    <div>
                      <div className="flex items-center mb-4">
                        <div className="w-4 h-4 rounded-full mr-2" style={{ backgroundColor: colorScale('persona') }}></div>
                        <h3 className="text-lg font-semibold text-gray-700">Persona: {aiInsights.persona}</h3>
                      </div>

                      <div className="mb-4">
                        <div className="text-sm text-gray-500 mb-1">Feedback Count</div>
                        <div className="text-2xl font-semibold">{aiInsights.feedbackCount || 0}</div>
                      </div>

                      {aiInsights.connectedThemes && aiInsights.connectedThemes.length > 0 && (
                        <div className="mb-4">
                          <div className="text-sm text-gray-500 mb-2">Common Themes</div>
                          <div className="space-y-1">
                            {aiInsights.connectedThemes.map((theme, idx) => (
                              <div key={idx} className="flex items-center bg-blue-50 px-3 py-2 rounded">
                                <div className="w-3 h-3 rounded-full mr-2" style={{ backgroundColor: colorScale('theme') }}></div>
                                <span className="text-sm">{theme.name}</span>
                              </div>
                            ))}
                          </div>
                        </div>
                      )}

                      {aiInsights.results && aiInsights.results.length > 0 && (
                        <div>
                          <div className="text-sm text-gray-500 mb-2">Feedback from this Persona</div>
                          <div className="space-y-3 max-h-60 overflow-y-auto">
                            {aiInsights.results.map((item, idx) => (
                              <div key={idx} className="bg-gray-50 p-3 rounded border border-gray-200 text-sm">
                                <p className="mb-2">"{item.text}"</p>
                                <div className="text-xs text-gray-500 italic">
                                  {item.timestamp ? new Date(item.timestamp).toLocaleString() : 'No timestamp'}
                                </div>
                              </div>
                            ))}
                          </div>
                        </div>
                      )}
                    </div>
                  )}

                  {/* Theme Node Details */}
                  {aiInsights.theme && !aiInsights.isFeatureView && !aiInsights.persona && (
                    <div>
                      <div className="flex items-center mb-4">
                        <div className="w-4 h-4 rounded-full mr-2" style={{ backgroundColor: colorScale('theme') }}></div>
                        <h3 className="text-lg font-semibold text-gray-700">Theme: {aiInsights.theme}</h3>
                      </div>

                      <div className="mb-4">
                        <div className="text-sm text-gray-500 mb-1">Related Feedback Count</div>
                        <div className="text-2xl font-semibold">{aiInsights.feedbackCount || 0}</div>
                      </div>

                      {aiInsights.connectedPersonas && aiInsights.connectedPersonas.length > 0 && (
                        <div className="mb-4">
                          <div className="text-sm text-gray-500 mb-2">Connected Personas</div>
                          <div className="space-y-1">
                            {aiInsights.connectedPersonas.map((persona, idx) => (
                              <div key={idx} className="flex items-center bg-orange-50 px-3 py-2 rounded">
                                <div className="w-3 h-3 rounded-full mr-2" style={{ backgroundColor: colorScale('persona') }}></div>
                                <span className="text-sm">{persona.name}</span>
                              </div>
                            ))}
                          </div>
                        </div>
                      )}

                      {aiInsights.connectedFeatures && aiInsights.connectedFeatures.length > 0 && (
                        <div className="mb-4">
                          <div className="text-sm text-gray-500 mb-2">Related Features</div>
                          <div className="space-y-2">
                            {aiInsights.connectedFeatures.map((feature, idx) => (
                              <div key={idx} className="bg-green-50 px-3 py-2 rounded border-l-4 border-green-400">
                                <div className="font-medium">{feature.name}</div>
                                <div className="text-xs mt-1">
                                  Priority: <span className={`font-medium ${
                                    feature.priority === 'High' ? 'text-red-600' : 
                                    feature.priority === 'Medium' ? 'text-yellow-600' : 'text-blue-600'
                                  }`}>{feature.priority || 'Medium'}</span>
                                </div>
                              </div>
                            ))}
                          </div>
                        </div>
                      )}
                    </div>
                  )}

                  {/* Feature Node Details */}
                  {aiInsights.isFeatureView && (
                    <div>
                      <div className="flex items-center mb-4">
                        <div className="w-4 h-4 rounded-full mr-2" style={{ backgroundColor: colorScale('feature') }}></div>
                        <h3 className="text-lg font-semibold text-gray-700">Feature: {aiInsights.name}</h3>
                      </div>

                      <div className="mb-4">
                        <div className="inline-block bg-gray-100 px-3 py-1 rounded-full text-sm font-medium">
                          Priority: <span className={`${
                            aiInsights.priority === 'High' ? 'text-red-600' : 
                            aiInsights.priority === 'Medium' ? 'text-yellow-600' : 'text-blue-600'
                          }`}>{aiInsights.priority}</span>
                        </div>
                      </div>

                      {aiInsights.connectedThemes && aiInsights.connectedThemes.length > 0 && (
                        <div className="mb-4">
                          <div className="text-sm text-gray-500 mb-2">Related Themes</div>
                          <div className="space-y-1">
                            {aiInsights.connectedThemes.map((theme, idx) => (
                              <div key={idx} className="flex items-center bg-blue-50 px-3 py-2 rounded">
                                <div className="w-3 h-3 rounded-full mr-2" style={{ backgroundColor: colorScale('theme') }}></div>
                                <span className="text-sm">{theme.name}</span>
                                {theme.feedbackCount && (
                                  <span className="ml-2 text-xs text-gray-500">({theme.feedbackCount} feedback)</span>
                                )}
                              </div>
                            ))}
                          </div>
                        </div>
                      )}

                      {aiInsights.connectedPersonas && aiInsights.connectedPersonas.length > 0 && (
                        <div>
                          <div className="text-sm text-gray-500 mb-2">Related Personas</div>
                          <div className="space-y-1">
                            {aiInsights.connectedPersonas.map((persona, idx) => (
                              <div key={idx} className="flex items-center bg-orange-50 px-3 py-2 rounded">
                                <div className="w-3 h-3 rounded-full mr-2" style={{ backgroundColor: colorScale('persona') }}></div>
                                <span className="text-sm">{persona.name}</span>
                              </div>
                            ))}
                          </div>
                        </div>
                      )}
                    </div>
                  )}

                  {/* Feedback Node Details - Enhanced for complete text */}
                  {aiInsights.isFeedbackView && (
                    <div>
                      <div className="flex items-center mb-4">
                        <div className="w-4 h-4 rounded-full mr-2" style={{ backgroundColor: colorScale('feedback') }}></div>
                        <h3 className="text-lg font-semibold text-gray-700">Feedback Details</h3>
                      </div>

                      <div className="bg-gray-50 p-4 rounded-lg border border-gray-200 mb-4">
                        {/* Complete feedback text with better formatting */}
                        <div className="text-gray-800 mb-4 bg-white p-3 rounded border border-gray-200 max-h-80 overflow-y-auto">
                          <p className="whitespace-pre-wrap text-base leading-relaxed">"{aiInsights.feedbackText}"</p>

                          {/* Debug info for development - comment out in production */}
                          {/* <div className="mt-3 pt-3 border-t border-gray-200 text-xs text-gray-500">
                            <div>Content length: {aiInsights.feedbackText?.length || 0} characters</div>
                          </div> */}
                        </div>

                        <div className="flex justify-between items-center">
                          <div className="flex items-center">
                            <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4 text-gray-500 mr-1" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z" />
                            </svg>
                            <span className="text-sm text-gray-600 font-medium">{aiInsights.user_role || 'Unknown User'}</span>
                          </div>

                          <div className="text-xs text-gray-500">
                            {aiInsights.timestamp ? new Date(aiInsights.timestamp).toLocaleString() : 'No timestamp available'}
                          </div>
                        </div>
                      </div>

                      {/* Sentiment Analysis - If available */}
                      {aiInsights.sentiment && (
                        <div className="mt-4">
                          <div className="text-sm text-gray-500 mb-2">Sentiment Analysis</div>
                          <div className={`px-3 py-2 rounded-full inline-block font-medium text-sm ${
                            aiInsights.sentiment === 'Positive' ? 'bg-green-100 text-green-800' : 
                            aiInsights.sentiment === 'Negative' ? 'bg-red-100 text-red-800' : 
                            'bg-gray-100 text-gray-800'
                          }`}>
                            {aiInsights.sentiment}
                          </div>
                        </div>
                      )}

                      {/* Related Themes - If any */}
                      {aiInsights.relatedThemes && aiInsights.relatedThemes.length > 0 && (
                        <div className="mt-4">
                          <div className="text-sm text-gray-500 mb-2">Related Themes</div>
                          <div className="flex flex-wrap gap-2">
                            {aiInsights.relatedThemes.map((theme, idx) => (
                              <div key={idx} className="bg-blue-50 px-3 py-1 rounded-full text-sm">
                                {theme}
                              </div>
                            ))}
                          </div>
                        </div>
                      )}
                    </div>
                  )}
                </div>

                {/* Existing analytics sections */}
                {analyticsData && (
                  <div className="space-y-6 mb-8">
                    {/* Sentiment Overview Section */}
                    <div>
                      <h3 className="text-lg font-semibold text-gray-700 mb-3 sticky top-14 bg-white z-10 pb-2">Sentiment Overview</h3>
                      <div className="bg-gray-50 p-4 rounded-lg shadow-sm">
                        <div className="flex items-center justify-around mb-2">
                          <div className="text-center">
                            <div className="text-green-500 text-2xl font-bold">
                              {analyticsData.sentimentDistribution?.Positive || 0}
                            </div>
                            <div className="text-xs text-gray-500">Positive</div>
                          </div>
                          <div className="text-center">
                            <div className="text-red-500 text-2xl font-bold">
                              {analyticsData.sentimentDistribution?.Negative || 0}
                            </div>
                            <div className="text-xs text-gray-500">Negative</div>
                          </div>
                          <div className="text-center">
                            <div className="text-gray-500 text-2xl font-bold">
                              {analyticsData.sentimentDistribution?.Neutral || 0}
                            </div>
                            <div className="text-xs text-gray-500">Neutral</div>
                          </div>
                        </div>
                      </div>
                    </div>

                    {/* Theme Analysis Section can go here */}
                  </div>
                )}

                {/* Rest of the panel content */}
                {aiInsights.insights && aiInsights.insights.length > 0 && (
                  <div className="mb-8">
                    <h3 className="text-lg font-semibold text-gray-700 mb-3">Key Insights</h3>
                    <div className="space-y-4">
                      {aiInsights.insights.map(insight => (
                        <div key={insight.id || Math.random()} className="bg-blue-50 p-4 rounded-lg shadow-sm border-l-4 border-blue-500">
                          <h4 className="font-medium text-gray-800 mb-1">{insight.title}</h4>
                          <p className="text-gray-700 text-sm">{insight.description}</p>
                        </div>
                      ))}
                    </div>
                  </div>
                )}
              </div>
            )}
          </div>
        )}
      </div>

      {/* Add Feedback Modal */}
      {showFeedbackModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded-lg shadow-xl w-96">
            <h2 className="text-xl font-bold mb-4">
              Add New Feedback
            </h2>

            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-bold mb-2">
                Feedback Text
              </label>
              <textarea 
                value={newFeedback}
                onChange={(e) => setNewFeedback(e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                placeholder="Enter user feedback"
                rows={4}
              />
            </div>

            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-bold mb-2">
                User Role
              </label>
              <input 
                type="text" 
                value={newFeedbackRole}
                onChange={(e) => setNewFeedbackRole(e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                placeholder="e.g. Mobile User, Admin, Power User"
              />
            </div>

            {/* New Theme Selection Dropdown */}
            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-bold mb-2">
                Connect to Theme (Optional)
              </label>
              <select
                value={selectedThemeForFeedback || ""}
                onChange={(e) => setSelectedThemeForFeedback(e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              >
                <option value="">Auto-detect theme (using AI)</option>
                {graphData.nodes
                  .filter(node => node.type === "theme")
                  .map(theme => (
                    <option key={theme.id} value={theme.id}>
                      {theme.name}
                    </option>
                  ))
                }
              </select>
              <p className="text-xs text-gray-500 mt-1">
                Leave empty to use AI for theme detection or select an existing theme
              </p>
            </div>

            <div className="flex justify-end space-x-2">
              <button 
                className="px-4 py-2 bg-gray-300 text-gray-700 rounded hover:bg-gray-400"
                onClick={() => {
                  setShowFeedbackModal(false);
                  setNewFeedback('');
                  setNewFeedbackRole('');
                  setSelectedThemeForFeedback('');
                }}
              >
                Cancel
              </button>
              <button 
                className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
                onClick={handleAddFeedback}
                disabled={isAIProcessing}
              >
                {isAIProcessing ? (
                  <div className="flex items-center">
                    <div className="w-4 h-4 border-2 border-white border-t-transparent rounded-full animate-spin mr-2"></div>
                    Processing...
                  </div>
                ) : 'Submit'}
              </button>
            </div>

            {aiError && (
              <div className="mt-4 p-2 bg-red-100 text-red-700 rounded">
                {aiError}
              </div>
            )}
          </div>
        </div>
      )}

      {/* Display AI Insights in sidebar when available */}
      {insights.length > 0 && !showAIInsightsPanel && (
        <button
          onClick={() => {
            setAIInsights({ results: [], insights: insights });
            setShowAIInsightsPanel(true);
          }}
          className="fixed bottom-8 left-8 bg-blue-600 text-white px-4 py-2 rounded-full shadow-lg hover:bg-blue-700"
        >
          View AI Insights ({insights.length})
        </button>
      )}
    </div>
  );
};

export default UserResearchNetwork;
