// UserResearchNetwork.js
import React, { useState, useEffect, useRef, useCallback } from 'react';
import * as d3 from 'd3';
import { ForceGraph2D } from 'react-force-graph';
import './UserResearchNetwork.css';

const UserResearchNetwork = () => {
  const [graphData, setGraphData] = useState({ nodes: [], links: [] });
  const [selectedNode, setSelectedNode] = useState(null);
  const [aiInsights, setAiInsights] = useState("");
  const [loading, setLoading] = useState(true);
  const [themes, setThemes] = useState([]);
  const fileInputRef = useRef(null);

  // Function to handle file import
  const handleFileImport = (event) => {
    const file = event.target.files[0];
    if (!file) return;

    setLoading(true);

    const reader = new FileReader();
    reader.onload = (e) => {
      try {
        const jsonData = JSON.parse(e.target.result);
        // Check if data has the feedback array
        if (jsonData.feedback && Array.isArray(jsonData.feedback)) {
          processData(jsonData.feedback); // Pass the feedback array
        } else {
          throw new Error("Invalid format: Expected a 'feedback' array");
        }
      } catch (error) {
        console.error("Error parsing JSON:", error);
        alert("Error: Please ensure the file contains a valid feedback array");
      setLoading(false);
      }
    };
    reader.readAsText(file);
  };

  // Process the imported JSON data
  const processData = (feedbackData) => {
    // Generate themes based on feedback content
    const extractedThemes = extractThemes(feedbackData);
    setThemes(extractedThemes);

    // Create nodes from feedback items
    const nodes = feedbackData.map((item) => ({
      id: item.id,
      name: item.id,
      val: 1,
      text: item.text,
      user_role: item.user_role,
      timestamp: item.timestamp,
      theme: 'Unclassified', // Will be updated below
      allThemes: [],        // Will be updated below
      color: '#c8d6e5'     // Default color
    }));

    // Assign themes to each node
    nodes.forEach(node => {
      const itemThemes = assignThemesToFeedback(node.text, extractedThemes);
      node.allThemes = itemThemes;
      node.theme = itemThemes[0] || 'Miscellaneous';
      node.color = getColorForTheme(node.theme);
    });

    // Create theme nodes
    const themeNodes = extractedThemes.map(theme => ({
      id: `theme-${theme}`,
      name: theme,
      val: 2,
      type: 'theme',
      theme: theme,
      color: getColorForTheme(theme)
    }));

    // Create persona nodes
    const uniqueRoles = [...new Set(feedbackData.map(item => item.user_role))];
    const personaNodes = uniqueRoles.map(role => ({
      id: `persona-${role}`,
      name: role,
      val: 2,
      type: 'persona',
      color: '#ff9f43'
    }));

    // Combine all nodes
    const allNodes = [...nodes, ...themeNodes, ...personaNodes];

    // Create links
    const links = [];

    // Link feedback to themes
    nodes.forEach(node => {
      node.allThemes.forEach(theme => {
        links.push({
          source: node.id,
          target: `theme-${theme}`,
          value: 1,
          theme: theme
        });
      });

      // Link feedback to personas
      links.push({
        source: node.id,
        target: `persona-${node.user_role}`,
        value: 1,
        type: 'feedback-persona'
      });
    });

    // Generate AI insights
    const insights = generateAiInsights(feedbackData, extractedThemes);

    setGraphData({ nodes: allNodes, links: links });
    setAiInsights(insights);
    setLoading(false);
  };

  // Extract potential themes from feedback data
  const extractThemes = (feedbackData) => {
    // Simple keyword-based theme extraction
    const keywords = {
      'Performance': ['slow', 'fast', 'speed', 'load', 'time', 'performance', 'lag'],
      'UI/UX': ['layout', 'design', 'interface', 'look', 'ui', 'ux', 'user interface'],
      'Features': ['feature', 'functionality', 'tool', 'option', 'capability'],
      'Usability': ['easy', 'difficult', 'intuitive', 'confusing', 'simple', 'complex'],
      'Data': ['data', 'information', 'stats', 'metrics', 'numbers', 'charts']
    };

    // Count occurrences of each theme
    const themeCounts = {};

    feedbackData.forEach(item => {
      const lowerText = item.text.toLowerCase();

      Object.entries(keywords).forEach(([theme, words]) => {
        if (words.some(word => lowerText.includes(word))) {
          themeCounts[theme] = (themeCounts[theme] || 0) + 1;
        }
      });
    });

    // Return themes that appear in the data
    return Object.keys(themeCounts).filter(theme => themeCounts[theme] > 0);
  };

  // Assign themes to a specific feedback item
  const assignThemesToFeedback = (text, availableThemes) => {
    const keywords = {
      'Performance': ['slow', 'fast', 'speed', 'load', 'time', 'performance', 'lag'],
      'UI/UX': ['layout', 'design', 'interface', 'look', 'ui', 'ux', 'user interface'],
      'Features': ['feature', 'functionality', 'tool', 'option', 'capability'],
      'Usability': ['easy', 'difficult', 'intuitive', 'confusing', 'simple', 'complex'],
      'Data': ['data', 'information', 'stats', 'metrics', 'numbers', 'charts']
    };

    const matchedThemes = [];
    const lowerText = text.toLowerCase();

    availableThemes.forEach(theme => {
      if (keywords[theme] && keywords[theme].some(word => lowerText.includes(word))) {
        matchedThemes.push(theme);
      }
    });

    return matchedThemes.length > 0 ? matchedThemes : ['Miscellaneous'];
  };

  // Create links between related nodes
  const createLinks = (nodes) => {
    const links = [];

    // Connect nodes that share themes
    for (let i = 0; i < nodes.length; i++) {
      for (let j = i + 1; j < nodes.length; j++) {
        const node1 = nodes[i];
        const node2 = nodes[j];

        // Check for theme overlap
        const sharedThemes = node1.allThemes.filter(theme => 
          node2.allThemes.includes(theme)
        );

        if (sharedThemes.length > 0) {
          links.push({
            source: node1.id,
            target: node2.id,
            value: sharedThemes.length,
            theme: sharedThemes[0]
          });
        }
      }
    }

    return links;
  };

  // Generate insights from the feedback data
  const generateAiInsights = (feedbackData, themes) => {
    // Simple insights generation
    const totalFeedback = feedbackData.length;

    let insightText = `### AI Analysis of ${totalFeedback} Feedback Items\n\n`;

    // Add theme distribution
    insightText += "#### Key Themes Identified:\n";
    themes.forEach(theme => {
      const count = feedbackData.filter(item => 
        assignThemesToFeedback(item.text, [theme]).includes(theme)
      ).length;

      const percentage = Math.round((count / totalFeedback) * 100);
      insightText += `- **${theme}**: ${count} items (${percentage}%)\n`;
    });

    // Add role distribution
    const roles = {};
    feedbackData.forEach(item => {
      if (item.user_role) {
        roles[item.user_role] = (roles[item.user_role] || 0) + 1;
      }
    });

    insightText += "\n#### Feedback by User Role:\n";
    Object.entries(roles).forEach(([role, count]) => {
      const percentage = Math.round((count / totalFeedback) * 100);
      insightText += `- **${role}**: ${count} items (${percentage}%)\n`;
    });

    return insightText;
  };

  // Get color for a theme
  const getColorForTheme = (theme) => {
    const themeColors = {
      'Performance': '#ff6b6b',
      'UI/UX': '#48dbfb',
      'Features': '#1dd1a1',
      'Usability': '#feca57',
      'Data': '#5f27cd',
      'Miscellaneous': '#c8d6e5'
    };

    return themeColors[theme] || '#c8d6e5';
  };

  // Handle file drop
  const handleFileDrop = (event) => {
    event.preventDefault();

    if (event.dataTransfer.items && event.dataTransfer.items.length > 0) {
      const file = event.dataTransfer.items[0].getAsFile();
      if (file.type === "application/json") {
        const reader = new FileReader();
        reader.onload = (e) => {
          try {
            const jsonData = JSON.parse(e.target.result);
            processData(jsonData.feedback);
          } catch (error) {
            console.error("Error parsing JSON:", error);
          }
        };
        reader.readAsText(file);
      }
    }
  };

  const preventDefaults = (event) => {
    event.preventDefault();
    event.stopPropagation();
  };

  useEffect(() => {
    // For drag and drop functionality
    const dropArea = document.getElementById('drop-area');
    if (dropArea) {
      ['dragenter', 'dragover', 'dragleave', 'drop'].forEach(eventName => {
        dropArea.addEventListener(eventName, preventDefaults, false);
      });

      dropArea.addEventListener('drop', handleFileDrop, false);

      return () => {
        ['dragenter', 'dragover', 'dragleave', 'drop'].forEach(eventName => {
          dropArea.removeEventListener(eventName, preventDefaults, false);
        });

        dropArea.removeEventListener('drop', handleFileDrop, false);
      };
    }
  }, []);

  const handleNodeClick = (node) => {
    setSelectedNode(node);
  };

    return (
    <div className="user-research-network">
      <div className="controls-panel">
        <h2>User Feedback Network</h2>
        <div id="drop-area" className="drop-area">
          <p>Drag & drop your feedback JSON file here</p>
          <p>or</p>
              <input 
                type="file" 
                accept=".json" 
            onChange={handleFileImport}
            ref={fileInputRef}
            style={{ display: 'none' }}
          />
          <button onClick={() => fileInputRef.current.click()}>
            Select File
            </button>
          </div>

        {themes.length > 0 && (
          <div className="themes-legend">
            <h3>Themes</h3>
            <div className="theme-items">
              {themes.map(theme => (
                <div className="theme-item" key={theme}>
                  <span 
                    className="theme-color" 
                    style={{ backgroundColor: getColorForTheme(theme) }}
                  ></span>
                  <span className="theme-name">{theme}</span>
          </div>
              ))}
          </div>
        </div>
        )}
          </div>

      <div className="graph-container">
        {loading ? (
          <div className="loading-indicator">Loading...</div>
        ) : (
          <ForceGraph2D
            graphData={graphData}
            nodeLabel="text"
            nodeColor={node => node.color}
            nodeRelSize={6}
            linkWidth={link => 1 + link.value * 0.5}
            linkColor={link => getColorForTheme(link.theme)}
            onNodeClick={handleNodeClick}
          />
        )}
            </div>

      <div className="details-panel">
        {selectedNode ? (
          <div className="node-details">
            <h3>{selectedNode.name}</h3>
            <p className="feedback-text">{selectedNode.text}</p>
            {selectedNode.user_role && (
              <p className="feedback-meta">
                <strong>Role:</strong> {selectedNode.user_role}
              </p>
            )}
            {selectedNode.timestamp && (
              <p className="feedback-meta">
                <strong>Date:</strong> {new Date(selectedNode.timestamp).toLocaleString()}
              </p>
            )}
            <div className="feedback-themes">
              <strong>Themes:</strong>
              {selectedNode.allThemes.map(theme => (
                <span 
                  key={theme} 
                  className="theme-tag"
                  style={{ backgroundColor: getColorForTheme(theme) }}
                >
                  {theme}
                              </span>
                          ))}
                        </div>
                      </div>
        ) : (
          <div className="ai-insights">
            <h3>AI Insights</h3>
            <div dangerouslySetInnerHTML={{ __html: aiInsights.replace(/\n/g, "<br />") }} />
              </div>
            )}
          </div>
    </div>
  );
};

export default UserResearchNetwork;
