  }, []);

  // Handle new feedback submission
  const handleAddFeedback = useCallback(async () => {
    if (!newFeedback || !newFeedbackRole) {
      setError("Please provide both feedback text and user role");
      return;
    }

    // Create a new feedback item
    const newFeedbackItem = {
      id: `feedback-${Date.now()}`,
      text: newFeedback,
      user_role: newFeedbackRole,
      timestamp: new Date().toISOString()
    };

    // Add to feedback items
    setFeedbackItems(prev => [...prev, newFeedbackItem]);

    // Process with AI
    setIsAIProcessing(true);
    try {
      const result = await classifyFeedback(newFeedback);

      if (result && result.theme) {
        // Process the feedback and update the graph

        // Check if the persona exists
        let personaNode = graphData.nodes.find(node => 
          node.type === "persona" && node.name === newFeedbackRole
        );

        if (!personaNode) {
          // Create a new persona node
          const personaId = `persona-${Date.now()}`;
          personaNode = {
            id: personaId,
            name: newFeedbackRole,
            type: "persona",
            feedbackCount: 1
          };

          // Add persona to graph
          setGraphData(prev => ({
            nodes: [...prev.nodes, personaNode],
            links: [...prev.links]
          }));
        } else {
          // Update persona feedback count
          setGraphData(prev => ({
            nodes: prev.nodes.map(node => 
              node.id === personaNode.id 
                ? {...node, feedbackCount: (node.feedbackCount || 0) + 1}
                : node
            ),
            links: [...prev.links]
          }));
        }

        // Check if theme exists
        let themeNode = graphData.nodes.find(node => 
          node.type === "theme" && node.name === result.theme
        );

        if (!themeNode) {
          // Create a new theme node
          const themeId = `theme-${Date.now()}`;
          themeNode = {
            id: themeId,
            name: result.theme,
            type: "theme",
            feedbackCount: 1
          };

          // Add theme to graph
          setGraphData(prev => ({
            nodes: [...prev.nodes, themeNode],
            links: [
              ...prev.links,
              // Add link between persona and theme
              {
                source: personaNode.id,
                target: themeId,
                strength: 0.7
              }
            ]
          }));
        } else {
          // Update theme and check for link
          const link = graphData.links.find(link => {
            const sourceId = typeof link.source === 'object' ? link.source.id : link.source;
            const targetId = typeof link.target === 'object' ? link.target.id : link.target;

            return (sourceId === personaNode.id && targetId === themeNode.id) ||
                   (sourceId === themeNode.id && targetId === personaNode.id);
          });

          if (!link) {
            // Create link if it doesn't exist
            setGraphData(prev => ({
              nodes: prev.nodes.map(node => 
                node.id === themeNode.id 
                  ? {...node, feedbackCount: (node.feedbackCount || 0) + 1}
                  : node
              ),
              links: [
                ...prev.links,
                {
                  source: personaNode.id,
                  target: themeNode.id,
                  strength: 0.7
                }
              ]
            }));
          } else {
            // Just update the theme
            setGraphData(prev => ({
              nodes: prev.nodes.map(node => 
                node.id === themeNode.id 
                  ? {...node, feedbackCount: (node.feedbackCount || 0) + 1}
                  : node
              ),
              links: [...prev.links]
            }));
          }
        }
      }

      // Reset form
      setNewFeedback('');
      setNewFeedbackRole('');
      setShowFeedbackModal(false);

    } catch (error) {
      console.error("Error processing feedback:", error);
      setAIError("Failed to process feedback. Please try again.");
    } finally {
      setIsAIProcessing(false);
    }
  }, [newFeedback, newFeedbackRole, graphData]);

  // Zoom control functions
  const zoomIn = useCallback(() => {
    if (!svgRef.current || !zoomRef.current) return;

    const svg = d3.select(svgRef.current);
    const currentTransform = d3.zoomTransform(svg.node());

    svg.transition()
      .duration(300)
      .call(
        zoomRef.current.transform,
        d3.zoomIdentity
          .translate(currentTransform.x, currentTransform.y)
          .scale(currentTransform.k * 1.3)
      );
  }, []);

  const zoomOut = useCallback(() => {
    if (!svgRef.current || !zoomRef.current) return;

    const svg = d3.select(svgRef.current);
    const currentTransform = d3.zoomTransform(svg.node());

    svg.transition()
      .duration(300)
      .call(
        zoomRef.current.transform,
        d3.zoomIdentity
          .translate(currentTransform.x, currentTransform.y)
          .scale(currentTransform.k / 1.3)
      );
  }, []);

  const centerView = useCallback(() => {
    if (!svgRef.current || !zoomRef.current) return;

    const svg = d3.select(svgRef.current);

    // Calculate the center point
    const centerX = dimensions.width / 2;
    const centerY = dimensions.height / 2;

    // Create a transform that centers the view
    const transform = d3.zoomIdentity
      .translate(centerX, centerY)
      .scale(1)
      .translate(-centerX, -centerY);

    // Apply the transform with a smooth transition
    svg.transition()
      .duration(750)
      .call(zoomRef.current.transform, transform);
  }, [dimensions]);

  const fitToScreen = useCallback(() => {
    if (!svgRef.current || !zoomRef.current || !graphData.nodes.length) return;

    const svg = d3.select(svgRef.current);

    // Find the bounds of all nodes
    let minX = Infinity, minY = Infinity, maxX = -Infinity, maxY = -Infinity;

    graphData.nodes.forEach(node => {
      if (!node.x || !node.y) return;

      if (node.x < minX) minX = node.x;
      if (node.y < minY) minY = node.y;
      if (node.x > maxX) maxX = node.x;
      if (node.y > maxY) maxY = node.y;
    });

    // If we don't have valid bounds, center view instead
    if (!isFinite(minX) || !isFinite(minY) || !isFinite(maxX) || !isFinite(maxY)) {
      centerView();
      return;
    }

    // Add padding
    const padding = 50;
    minX -= padding;
    minY -= padding;
    maxX += padding;
    maxY += padding;

    // Calculate width and height of the content
    const contentWidth = maxX - minX;
    const contentHeight = maxY - minY;

    // Calculate scale to fit the content
    const scale = Math.min(
      dimensions.width / contentWidth,
      dimensions.height / contentHeight,
      2 // Maximum scale factor
    ) * 0.9; // 90% to add some margin

    // Calculate the center of the content
    const centerX = (minX + maxX) / 2;
    const centerY = (minY + maxY) / 2;

    // Create a transform that centers and scales the content
    const transform = d3.zoomIdentity
      .translate(dimensions.width / 2, dimensions.height / 2)
      .scale(scale)
      .translate(-centerX, -centerY);

    // Apply the transform with a smooth transition
    svg.transition()
      .duration(750)
      .call(zoomRef.current.transform, transform);
  }, [dimensions, graphData.nodes, centerView]);

  // Toggle mini-map visibility
  const toggleMiniMap = useCallback(() => {
    setShowMiniMap(!showMiniMap);
  }, [showMiniMap]);

  const handleDataImported = (importedData) => {
    if (importedData) {
      setGraphData({ 
        nodes: importedData.nodes || [], 
        links: importedData.links || [] 
      });
      setInsights(importedData.insights || []);
    }
  };

  // Loading screen
  if (loading) {
    return (
      <div className="flex flex-col h-full items-center justify-center bg-gray-50">
        <div className="text-2xl text-blue-800 mb-4">Loading Insights Network...</div>
        <div className="w-16 h-16 border-4 border-blue-600 border-t-transparent rounded-full animate-spin"></div>
      </div>
    );
  }

  // Error screen
  if (error) {
    return (
      <div className="flex flex-col h-full items-center justify-center bg-gray-50">
        <div className="text-2xl text-red-800 mb-4">Error Loading Data</div>
        <div className="text-gray-600">{error}</div>
        <button 
          className="mt-4 px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
          onClick={() => window.location.reload()}
        >
          Retry
        </button>
      </div>
    );
  }

  return (
    <div className="flex flex-col h-full">
      {/* Header and controls */}
      <div className="bg-white p-4 shadow-md">
        <div className="flex justify-between items-center mb-4">
          <div>
            <h1 className="text-2xl font-bold text-blue-800">User Research Insights Network</h1>
            <p className="text-gray-600">Visualizing connections between user feedback, features, and personas</p>
          </div>

          {/* Search bar */}
          <div className="relative w-64">
            <input
              type="text"
              placeholder="Search nodes..."
              value={searchTerm}
              onChange={(e) => {
                setSearchTerm(e.target.value);
                if (e.target.value.length >= 2) {
                  const results = graphData.nodes.filter(node => 
                    node.name.toLowerCase().includes(e.target.value.toLowerCase())
                  );
                  setSearchResults(results);
                  setShowSearchResults(true);
                } else {
                  setShowSearchResults(false);
                  setSearchResults([]);
                }
              }}
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
            {showSearchResults && searchResults.length > 0 && (
              <div className="absolute top-full left-0 right-0 mt-1 bg-white border border-gray-300 rounded-md shadow-lg z-20 max-h-60 overflow-y-auto">
                {searchResults.map((result, index) => (
                  <div 
                    key={index}
                    className="px-3 py-2 hover:bg-gray-100 cursor-pointer"
                    onClick={() => {
                      setSelectedNode(result);
                      handleNodeClick(result);
                      setShowSearchResults(false);
                    }}
                  >
                    <div className="flex items-center">
                      <div 
                        className="w-3 h-3 rounded-full mr-2"
                        style={{ backgroundColor: colorScale(result.type) }}
                      ></div>
                      <span>{result.name}</span>
                      <span className="ml-2 text-xs text-gray-500">({result.type})</span>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>

        {/* Controls */}
        <div className="flex flex-wrap gap-2 mb-4">
          <div className="flex items-center space-x-2">
            <span className="text-sm text-gray-600">Data:</span>
            <button 
              className="px-3 py-1 bg-blue-600 text-white rounded hover:bg-blue-700 text-sm"
              onClick={exportData}
            >
              Export
            </button>
            <label className="px-3 py-1 bg-blue-600 text-white rounded hover:bg-blue-700 text-sm cursor-pointer">
              Import
              <input 
                type="file" 
                accept=".json" 
                className="hidden" 
                onChange={importData}
              />
            </label>
            <button 
              className="px-3 py-1 bg-red-600 text-white rounded hover:bg-red-700 text-sm"
              onClick={resetVisualization}
            >
              Reset
            </button>
          </div>

          <div className="flex items-center space-x-2">
            <span className="text-sm text-gray-600">View:</span>
            <button 
              className="px-3 py-1 bg-purple-600 text-white rounded hover:bg-purple-700 text-sm"
              onClick={() => setIsSimulationRunning(!isSimulationRunning)}
            >
              {isSimulationRunning ? 'Pause Simulation' : 'Resume Simulation'}
            </button>
            <button 
              className="px-3 py-1 bg-purple-600 text-white rounded hover:bg-purple-700 text-sm"
              onClick={centerView}
            >
              Center View
            </button>
            <button 
              className="px-3 py-1 bg-purple-600 text-white rounded hover:bg-purple-700 text-sm"
              onClick={toggleMiniMap}
            >
              {showMiniMap ? 'Hide Mini-Map' : 'Show Mini-Map'}
            </button>
            <button 
              className="px-3 py-1 bg-blue-600 text-white rounded hover:bg-blue-700 text-sm"
              onClick={fitToScreen}
              title="Fit all nodes to screen"
            >
              Fit to Screen
            </button>
          </div>

          <div className="flex items-center space-x-2">
            <span className="text-sm text-gray-600">Add:</span>
            <button 
              className="px-3 py-1 bg-green-600 text-white rounded hover:bg-green-700 text-sm"
              onClick={() => {
                setModalType('theme');
                setNewNodeName(`Theme ${graphData.nodes.filter(n => n.type === 'theme').length + 1}`);
                setShowModal(true);
              }}
            >
              Add Theme
            </button>
            <button 
              className="px-3 py-1 bg-green-600 text-white rounded hover:bg-green-700 text-sm"
              onClick={() => {
                setModalType('feature');
                setNewNodeName(`Feature ${graphData.nodes.filter(n => n.type === 'feature').length + 1}`);
                setShowModal(true);
              }}
            >
              Add Feature
            </button>
            <button 
              className="px-3 py-1 bg-green-600 text-white rounded hover:bg-green-700 text-sm"
              onClick={() => setShowFeedbackModal(true)}
            >
              Add Feedback
            </button>
          </div>
        </div>

        <div className="flex flex-wrap gap-4">
          <div className="flex items-center space-x-2">
            <span className="text-sm text-gray-600">Connect:</span>
            <button 
              className={`px-3 py-1 rounded text-sm ${
                selectedNode ? 'bg-orange-600 text-white hover:bg-orange-700' : 'bg-gray-300 text-gray-500 cursor-not-allowed'
              }`}
              onClick={connectNodes}
              disabled={!selectedNode}
            >
              Connect Nodes
            </button>
          </div>

          <div className="flex items-center space-x-2">
            <span className="text-sm text-gray-600">Feedback Weight:</span>
            <input 
              type="range" 
              min="1" 
              max="10" 
              value={feedbackWeight}
              onChange={(e) => setFeedbackWeight(parseInt(e.target.value))}
              className="w-32"
            />
            <span className="text-sm text-gray-600">{feedbackWeight}</span>
          </div>

          <div className="flex items-center space-x-2">
            <span className="text-sm text-gray-600">Highlight:</span>
            <button 
              className={`px-2 py-1 rounded text-xs ${highlightMode === 'none' ? 'bg-blue-600 text-white' : 'bg-gray-200 text-gray-700'}`}
              onClick={() => setHighlightMode('none')}
            >
              None
            </button>
            <button 
              className={`px-2 py-1 rounded text-xs ${highlightMode === 'themes' ? 'bg-blue-600 text-white' : 'bg-gray-200 text-gray-700'}`}
              onClick={() => setHighlightMode('themes')}
            >
              Themes
            </button>
            <button 
              className={`px-2 py-1 rounded text-xs ${highlightMode === 'feedback' ? 'bg-blue-600 text-white' : 'bg-gray-200 text-gray-700'}`}
              onClick={() => setHighlightMode('feedback')}
            >
              Feedback
            </button>
            <button 
              className={`px-2 py-1 rounded text-xs ${highlightMode === 'personas' ? 'bg-blue-600 text-white' : 'bg-gray-200 text-gray-700'}`}
              onClick={() => setHighlightMode('personas')}
            >
              Personas
            </button>
          </div>
        </div>
      </div>

      {/* Main visualization container */}
      <div className="flex flex-1 relative">
        <div 
          ref={containerRef} 
          className="flex-1 bg-gray-50 p-4 transition-all duration-300"
          style={{
            marginRight: showAIInsightsPanel && !isPanelCollapsed ? `${panelWidth}px` : '0',
            minHeight: '500px'
          }}
        >
          <svg 
            ref={svgRef} 
            width="100%" 
            height="100%"
            className="border border-gray-200 rounded-lg bg-white shadow-inner w-full h-full"
            style={{ minHeight: '500px' }}
            onClick={() => setSelectedNode(null)}
          />

          {/* Add Legend */}
          <Legend colorScale={colorScale} />

          {/* Zoom controls */}
          <div className="absolute bottom-8 right-8 flex flex-col bg-white rounded-lg shadow p-2 space-y-2 z-10">
            <button 
              className="w-8 h-8 flex items-center justify-center bg-gray-100 hover:bg-gray-200 rounded-full"
              onClick={zoomIn}
            >
              <span className="text-xl">+</span>
            </button>
            <button 
              className="w-8 h-8 flex items-center justify-center bg-gray-100 hover:bg-gray-200 rounded-full"
              onClick={zoomOut}
            >
              <span className="text-xl">-</span>
            </button>
            <button 
              className="w-8 h-8 flex items-center justify-center bg-gray-100 hover:bg-gray-200 rounded-full"
              onClick={fitToScreen}
            >
              <span className="text-xl">⊡</span>
            </button>
          </div>
        </div>

        {/* AI Insights Panel */}
        {showAIInsightsPanel && aiInsights && (
          <div 
            className={`fixed right-0 top-0 bottom-0 bg-white shadow-lg z-40 transition-all duration-300`}
            style={{ 
              width: isPanelCollapsed ? '50px' : `${panelWidth}px`,
              maxWidth: '40%'
            }}
          >
            {/* Collapse/Expand Button */}
            <div className="w-10 flex flex-col items-center pt-4 border-r border-gray-200 bg-gray-50">
              <button 
                onClick={() => setIsPanelCollapsed(!isPanelCollapsed)}
                className="w-8 h-8 flex items-center justify-center rounded-full bg-white hover:bg-gray-100 text-gray-600 shadow-sm"
                title={isPanelCollapsed ? "Expand panel" : "Collapse panel"}
              >
                <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d={
                    isPanelCollapsed 
                      ? "M13 5l7 7-7 7M5 5l7 7-7 7" // Expand icon
                      : "M11 19l-7-7 7-7M19 19l-7-7 7-7" // Collapse icon
                  } />
                </svg>
              </button>

              {isPanelCollapsed && (
                <div className="mt-4 transform -rotate-90 whitespace-nowrap text-sm font-medium text-gray-600 origin-center">
                  {aiInsights.isFeatureView ? 'Feature Details' : 
                   aiInsights.persona ? `${aiInsights.persona}` : 
                   aiInsights.theme ? `${aiInsights.theme}` : 'AI Insights'}
                </div>
              )}

              <button 
                onClick={() => setShowAIInsightsPanel(false)}
                className="w-8 h-8 flex items-center justify-center rounded-full bg-white hover:bg-gray-100 text-gray-600 mt-4 shadow-sm"
                title="Close panel"
              >
                <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                </svg>
              </button>
            </div>

            {/* Panel Content - Only shown when expanded */}
            {!isPanelCollapsed && (
              <div className="flex-1 overflow-y-auto p-6">
                {/* Panel Header */}
                <div className="mb-6 border-b pb-3">
                  {selectedNode ? (
                    <h2 className="text-2xl font-bold text-gray-800">
                      {selectedNode.type === 'persona' ? 'User Role Analysis' : 
                       selectedNode.type === 'theme' ? 'Theme Analysis' : 
                       selectedNode.type === 'feedback' ? 'Feedback Details' : 'AI Feedback Insights'}
                      {selectedNode.name && <span className="ml-2 text-blue-600">{selectedNode.name}</span>}
                    </h2>
                  ) : (
                    <h2 className="text-2xl font-bold text-gray-800 mb-1">AI Feedback Insights</h2>
                  )}

                  {!selectedNode && analyticsData && (
                    <p className="text-sm text-gray-500">
                      Analysis of {analyticsData.totalFeedback} feedback items across {Object.keys(analyticsData.themeDistribution).length} themes
                    </p>
                  )}
                </div>

                {/* Selected Node Content */}
                {selectedNode && (
                  <div className="mb-6">
                    {/* Persona Node Selected */}
                    {selectedNode.type === 'persona' && (
                      <div className="space-y-4">
                        <div className="bg-orange-50 p-4 rounded-lg shadow-sm">
                          <h3 className="font-medium text-gray-800 mb-2">Feedback Overview</h3>
                          <p className="text-sm text-gray-600 mb-3">
                            {selectedNode.feedbackCount} feedback items from {selectedNode.name} users
                          </p>

                          {/* Sentiment stats for this persona */}
                          {selectedNode.sentimentStats && (
                            <>
                              <div className="flex items-center justify-around mb-2">
                                <div className="text-center">
                                  <div className="text-green-500 text-xl font-bold">
                                    {selectedNode.sentimentStats.positive}
                                  </div>
                                  <div className="text-xs text-gray-500">Positive</div>
                                </div>
                                <div className="text-center">
                                  <div className="text-red-500 text-xl font-bold">
                                    {selectedNode.sentimentStats.negative}
                                  </div>
                                  <div className="text-xs text-gray-500">Negative</div>
                                </div>
                                <div className="text-center">
                                  <div className="text-gray-500 text-xl font-bold">
                                    {selectedNode.sentimentStats.neutral}
                                  </div>
                                  <div className="text-xs text-gray-500">Neutral</div>
                                </div>
                              </div>

                              {/* Sentiment Bar */}
                              <div className="w-full h-3 bg-gray-200 rounded-full overflow-hidden flex">
                                <div 
                                  className="bg-green-500 h-full" 
                                  style={{ 
                                    width: `${(selectedNode.sentimentStats.positive / selectedNode.feedbackCount) * 100}%` 
                                  }}
                                ></div>
                                <div 
                                  className="bg-red-500 h-full" 
                                  style={{ 
                                    width: `${(selectedNode.sentimentStats.negative / selectedNode.feedbackCount) * 100}%` 
                                  }}
                                ></div>
                                <div 
                                  className="bg-gray-400 h-full" 
                                  style={{ 
                                    width: `${(selectedNode.sentimentStats.neutral / selectedNode.feedbackCount) * 100}%` 
                                  }}
                                ></div>
                              </div>
                            </>
                          )}
                        </div>

                        {/* Related Themes */}
                        {analyticsData && (
                          <div>
                            <h3 className="font-medium text-gray-800 mb-2">Key Themes</h3>
                            <div className="space-y-3">
                              {graphData.links
                                .filter(link => 
                                  link.source.id === selectedNode.id || 
                                  link.target.id === selectedNode.id
                                )
                                .map(link => {
                                  // Find the theme node that this persona is connected to
                                  const connectedNode = link.source.id === selectedNode.id 
                                    ? link.target 
                                    : link.source;

                                  if (connectedNode.type !== 'theme') return null;

                                  return (
                                    <div 
                                      key={connectedNode.id} 
                                      className="bg-blue-50 p-3 rounded cursor-pointer hover:bg-blue-100"
                                      onClick={() => handleNodeClick(connectedNode)}
                                    >
                                      <p className="font-medium">{connectedNode.name}</p>
                                      <p className="text-xs text-gray-500">Click to explore this theme</p>
                                    </div>
                                  );
                                })}
                            </div>
                          </div>
                        )}
                      </div>
                    )}

                    {/* Theme Node Selected */}
                    {selectedNode.type === 'theme' && analyticsData && (
                      <div className="space-y-4">
                        <div className="bg-blue-50 p-4 rounded-lg shadow-sm">
                          <h3 className="font-medium text-gray-800 mb-2">Theme Overview</h3>
                          <p className="text-sm text-gray-600 mb-3">
                            {selectedNode.feedbackCount} feedback items related to {selectedNode.name}
                          </p>

                          {/* Sentiment for this theme */}
                          {analyticsData.sentimentByTheme[selectedNode.name] && (
                            <>
                              <div className="flex items-center justify-around mb-2">
                                <div className="text-center">
                                  <div className="text-green-500 text-xl font-bold">
                                    {analyticsData.sentimentByTheme[selectedNode.name].Positive}
                                  </div>
                                  <div className="text-xs text-gray-500">Positive</div>
                                </div>
                                <div className="text-center">
                                  <div className="text-red-500 text-xl font-bold">
                                    {analyticsData.sentimentByTheme[selectedNode.name].Negative}
                                  </div>
                                  <div className="text-xs text-gray-500">Negative</div>
                                </div>
                                <div className="text-center">
                                  <div className="text-gray-500 text-xl font-bold">
                                    {analyticsData.sentimentByTheme[selectedNode.name].Neutral}
                                  </div>
                                  <div className="text-xs text-gray-500">Neutral</div>
                                </div>
                              </div>

                              {/* Sentiment Bar */}
                              <div className="w-full h-3 bg-gray-200 rounded-full overflow-hidden flex mb-3">
                                <div 
                                  className="bg-green-500 h-full" 
                                  style={{ 
                                    width: `${(analyticsData.sentimentByTheme[selectedNode.name].Positive / selectedNode.feedbackCount) * 100}%` 
                                  }}
                                ></div>
                                <div 
                                  className="bg-red-500 h-full" 
                                  style={{ 
                                    width: `${(analyticsData.sentimentByTheme[selectedNode.name].Negative / selectedNode.feedbackCount) * 100}%` 
                                  }}
                                ></div>
                                <div 
                                  className="bg-gray-400 h-full" 
                                  style={{ 
                                    width: `${(analyticsData.sentimentByTheme[selectedNode.name].Neutral / selectedNode.feedbackCount) * 100}%` 
                                  }}
                                ></div>
                              </div>
                            </>
                          )}
                        </div>

                        {/* Related User Roles */}
                        <div>
                          <h3 className="font-medium text-gray-800 mb-2">User Roles</h3>
                          <div className="space-y-3">
                            {graphData.links
                              .filter(link => 
                                (link.source.id === selectedNode.id && link.target.type === 'persona') || 
                                (link.target.id === selectedNode.id && link.source.type === 'persona')
                              )
                              .map(link => {
                                // Find the persona node that this theme is connected to
                                const connectedNode = link.source.id === selectedNode.id 
                                  ? link.target 
                                  : link.source;

                                if (connectedNode.type !== 'persona') return null;

                                return (
                                  <div 
                                    key={connectedNode.id} 
                                    className="bg-orange-50 p-3 rounded cursor-pointer hover:bg-orange-100"
                                    onClick={() => handleNodeClick(connectedNode)}
                                  >
                                    <p className="font-medium">{connectedNode.name}</p>
                                    <p className="text-xs text-gray-500">Click to explore this user role</p>
                                  </div>
                                );
                              })}
                          </div>
                        </div>

                        {/* Sample Feedback for this theme */}
                        <div>
                          <h3 className="font-medium text-gray-800 mb-2">Sample Feedback</h3>
                          <div className="space-y-3 max-h-60 overflow-y-auto pr-2">
                            {graphData.links
                              .filter(link => 
                                (link.source.id === selectedNode.id && link.target.type === 'feedback') || 
                                (link.target.id === selectedNode.id && link.source.type === 'feedback')
                              )
                              .slice(0, 5)
                              .map(link => {
                                // Find the feedback node connected to this theme
                                const feedbackNode = link.source.id === selectedNode.id 
                                  ? link.target 
                                  : link.source;

                                if (feedbackNode.type !== 'feedback') return null;

                                const sentimentColor = 
                                  feedbackNode.sentiment === 'Positive' ? 'text-green-600' :
                                  feedbackNode.sentiment === 'Negative' ? 'text-red-600' :
                                  'text-gray-600';

                                return (
                                  <div 
                                    key={feedbackNode.id} 
                                    className="bg-gray-50 p-3 rounded border-l-4"
                                    style={{ 
                                      borderColor: feedbackNode.sentiment === 'Positive' ? '#10B981' : 
                                                   feedbackNode.sentiment === 'Negative' ? '#EF4444' : 
                                                   '#9CA3AF'
                                    }}
                                  >
                                    <p className="text-sm mb-1">{feedbackNode.title}</p>
                                    <div className="flex justify-between items-center">
                                      <span className={`text-xs ${sentimentColor}`}>
                                        {feedbackNode.sentiment}
                                      </span>
                                      {feedbackNode.user_role && (
                                        <span className="text-xs text-gray-500">
                                          {feedbackNode.user_role}
                                        </span>
                                      )}
                                    </div>
                                  </div>
                                );
                              })}
                          </div>
                        </div>
                      </div>
                    )}

                    {/* Feedback Node Selected */}
                    {selectedNode.type === 'feedback' && (
                      <div className="space-y-4">
                        <div className={`bg-gray-50 p-4 rounded-lg shadow-sm border-l-4 ${
                          selectedNode.sentiment === 'Positive' ? 'border-green-500' :
                          selectedNode.sentiment === 'Negative' ? 'border-red-500' :
                          'border-gray-400'
                        }`}>
                          <h3 className="font-medium text-gray-800 mb-2">Feedback</h3>
                          <p className="text-gray-700 mb-3">{selectedNode.title}</p>

                          <div className="flex justify-between items-center text-sm">
                            <span className={`px-2 py-1 rounded-full ${
                              selectedNode.sentiment === 'Positive' ? 'bg-green-100 text-green-800' :
                              selectedNode.sentiment === 'Negative' ? 'bg-red-100 text-red-800' :
                              'bg-gray-100 text-gray-800'
                            }`}>
                              {selectedNode.sentiment}
                            </span>

                            {selectedNode.user_role && (
                              <span className="bg-orange-100 text-orange-800 px-2 py-1 rounded-full">
                                {selectedNode.user_role}
                              </span>
                            )}

                            {selectedNode.theme && (
                              <span className="bg-blue-100 text-blue-800 px-2 py-1 rounded-full">
                                {selectedNode.theme}
                              </span>
                            )}
                          </div>
                        </div>

                        {/* Related Information */}
                        <div className="flex space-x-4">
                          {/* Theme Connection */}
                          {selectedNode.theme && (
                            <div 
                              className="flex-1 bg-blue-50 p-3 rounded cursor-pointer hover:bg-blue-100"
                              onClick={() => {
                                // Find and select the connected theme node
                                const themeNode = graphData.nodes.find(
                                  node => node.type === 'theme' && node.name === selectedNode.theme
                                );
                                if (themeNode) handleNodeClick(themeNode);
                              }}
                            >
                              <p className="font-medium">Theme: {selectedNode.theme}</p>
                              <p className="text-xs text-gray-500">Click to explore this theme</p>
                            </div>
                          )}

                          {/* User Role Connection */}
                          {selectedNode.user_role && (
                            <div 
                              className="flex-1 bg-orange-50 p-3 rounded cursor-pointer hover:bg-orange-100"
                              onClick={() => {
                                // Find and select the connected persona node
                                const personaId = `persona-${selectedNode.user_role.replace(/\s+/g, '-').toLowerCase()}`;
                                const personaNode = graphData.nodes.find(node => node.id === personaId);
                                if (personaNode) handleNodeClick(personaNode);
                              }}
                            >
                              <p className="font-medium">User: {selectedNode.user_role}</p>
                              <p className="text-xs text-gray-500">Click to explore this user role</p>
                            </div>
                          )}
                        </div>
                      </div>
                    )}

                    {/* Back to Overview Button */}
                    <button
                      onClick={() => setSelectedNode(null)}
                      className="mt-4 px-3 py-1 bg-gray-100 text-gray-700 rounded-md text-sm hover:bg-gray-200"
                    >
                      ← Back to Overview
                    </button>
                  </div>
                )}

                {/* Default Overview Content (shown when no node is selected) */}
                {!selectedNode && (
                  <>
                    {/* Keep your existing default panel content here */}
                    {/* Sentiment Overview Section */}
                    {analyticsData && (
                      <div className="mb-8">
                        <h3 className="text-lg font-semibold text-gray-700 mb-3">Sentiment Overview</h3>
                        {/* ... existing sentiment overview ... */}
                      </div>
                    )}

                    {/* Theme Analysis Section */}
                    {analyticsData && (
                      <div className="mb-8">
                        <h3 className="text-lg font-semibold text-gray-700 mb-3">Theme Analysis</h3>
                        {/* ... existing theme analysis ... */}
                      </div>
                    )}

                    {/* User Role Section */}
                    {analyticsData && (
                      <div className="mb-8">
                        <h3 className="text-lg font-semibold text-gray-700 mb-3">User Roles</h3>
                        {/* ... existing user role analysis ... */}
                      </div>
                    )}

                    {/* AI Generated Insights */}
                    <div className="mb-8">
                      <h3 className="text-lg font-semibold text-gray-700 mb-3">Key Insights</h3>
                      {/* ... existing insights ... */}
                    </div>

                    {/* Recommended Actions Section */}
                    <div>
                      <h3 className="text-lg font-semibold text-gray-700 mb-3">Recommended Actions</h3>
                      {/* ... existing recommended actions ... */}
                    </div>
                  </>
                )}
              </div>
            )}
          </div>
        )}
      </div>

      {/* Add Node Modal */}
      {showModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded-lg shadow-xl w-96">
            <h2 className="text-xl font-bold mb-4">
              Add New {modalType === 'theme' ? 'Theme' : 'Feature'}
            </h2>

            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-bold mb-2">
                Name
              </label>
              <input 
                type="text" 
                value={newNodeName}
                onChange={(e) => setNewNodeName(e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                placeholder={`Enter ${modalType} name`}
              />
            </div>

            {modalType === 'theme' && (
              <div className="mb-4">
                <label className="block text-gray-700 text-sm font-bold mb-2">
                  Feedback Count
                </label>
                <input 
                  type="number" 
                  value={newNodeFeedbackCount}
                  onChange={(e) => setNewNodeFeedbackCount(parseInt(e.target.value))}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                  min="0"
                />
              </div>
            )}

            {modalType === 'feature' && (
              <div className="mb-4">
                <label className="block text-gray-700 text-sm font-bold mb-2">
                  Priority
                </label>
                <select 
                  value={newNodePriority}
                  onChange={(e) => setNewNodePriority(e.target.value)}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                >
                  <option value="Low">Low</option>
                  <option value="Medium">Medium</option>
                  <option value="High">High</option>
                </select>
              </div>
            )}

            <div className="flex justify-end space-x-2">
              <button 
                className="px-4 py-2 bg-gray-300 text-gray-700 rounded hover:bg-gray-400"
                onClick={() => {
                  setShowModal(false);
                  setNewNodeName('');
                  setNewNodeFeedbackCount(10);
                  setNewNodePriority('Medium');
                }}
              >
                Cancel
              </button>
              <button 
                className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
                onClick={handleAddNode}
              >
                Add
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Connection Modal */}
      {showConnectionModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded-lg shadow-xl w-96">
            <h2 className="text-xl font-bold mb-4">
              Connect Nodes
            </h2>

            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-bold mb-2">
                From
              </label>
              <div className="px-3 py-2 border border-gray-300 rounded-md bg-gray-50">
                {connectionSource?.name} ({connectionSource?.type})
              </div>
            </div>

            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-bold mb-2">
                To
              </label>
              <select 
                value={connectionTarget}
                onChange={(e) => setConnectionTarget(e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              >
                <option value="">Select a target node</option>
                {availableTargets.map((target) => (
                  <option key={target.id} value={target.id}>
                    {target.name} ({target.type})
                  </option>
                ))}
              </select>
            </div>

            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-bold mb-2">
                Connection Strength: {connectionStrength.toFixed(1)}
              </label>
              <input 
                type="range" 
                min="0.1" 
                max="1" 
                step="0.1"
                value={connectionStrength}
                onChange={(e) => setConnectionStrength(parseFloat(e.target.value))}
                className="w-full"
              />
            </div>

            <div className="flex justify-end space-x-2">
              <button 
                className="px-4 py-2 bg-gray-300 text-gray-700 rounded hover:bg-gray-400"
                onClick={() => {
                  setShowConnectionModal(false);
                  setConnectionSource(null);
                  setConnectionTarget('');
                  setConnectionStrength(0.5);
                }}
              >
                Cancel
              </button>
              <button 
                className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
                onClick={handleConnectionSubmit}
                disabled={!connectionTarget}
              >
                Connect
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Add Feedback Modal */}
      {showFeedbackModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded-lg shadow-xl w-96">
            <h2 className="text-xl font-bold mb-4">
              Add New Feedback
            </h2>

            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-bold mb-2">
                Feedback Text
              </label>
              <textarea 
                value={newFeedback}
                onChange={(e) => setNewFeedback(e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                placeholder="Enter user feedback"
                rows={4}
              />
            </div>

            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-bold mb-2">
                User Role
              </label>
              <input 
                type="text" 
                value={newFeedbackRole}
                onChange={(e) => setNewFeedbackRole(e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                placeholder="e.g. Mobile User, Admin, Power User"
              />
            </div>

            <div className="flex justify-end space-x-2">
              <button 
                className="px-4 py-2 bg-gray-300 text-gray-700 rounded hover:bg-gray-400"
                onClick={() => {
                  setShowFeedbackModal(false);
                  setNewFeedback('');
                  setNewFeedbackRole('');
                }}
              >
                Cancel
              </button>
              <button 
                className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
                onClick={handleAddFeedback}
                disabled={isAIProcessing}
              >
                {isAIProcessing ? (
                  <div className="flex items-center">
                    <div className="w-4 h-4 border-2 border-white border-t-transparent rounded-full animate-spin mr-2"></div>
                    Processing...
                  </div>
                ) : 'Submit'}
              </button>
            </div>

            {aiError && (
              <div className="mt-4 p-2 bg-red-100 text-red-700 rounded">
                {aiError}
              </div>
            )}
          </div>
        </div>
      )}

      {/* Display AI Insights in sidebar when available */}
      {insights.length > 0 && !showAIInsightsPanel && (
        <button
          onClick={() => {
            setAIInsights({ results: [], insights: insights });
            setShowAIInsightsPanel(true);
          }}
          className="fixed bottom-8 left-8 bg-blue-600 text-white px-4 py-2 rounded-full shadow-lg hover:bg-blue-700"
        >
          View AI Insights ({insights.length})
        </button>
      )}
    </div>
  );
};

export default UserResearchNetwork;
