    maxY += padding;

    // Calculate width and height of the content
    const contentWidth = maxX - minX;
    const contentHeight = maxY - minY;

    // Calculate scale to fit the content
    const scale = Math.min(
      dimensions.width / contentWidth,
      dimensions.height / contentHeight,
      2 // Maximum scale factor
    ) * 0.9; // 90% to add some margin

    // Calculate the center of the content
    const centerX = (minX + maxX) / 2;
    const centerY = (minY + maxY) / 2;

    // Create a transform that centers and scales the content
    const transform = d3.zoomIdentity
      .translate(dimensions.width / 2, dimensions.height / 2)
      .scale(scale)
      .translate(-centerX, -centerY);

    // Apply the transform with a smooth transition
    svg.transition()
      .duration(750)
      .call(zoomRef.current.transform, transform);
  }, [dimensions, graphData.nodes, centerView]);

  // Toggle mini-map visibility
  const toggleMiniMap = useCallback(() => {
    setShowMiniMap(!showMiniMap);
  }, [showMiniMap]);

  const handleDataImported = (importedData) => {
    if (importedData) {
      setGraphData({ 
        nodes: importedData.nodes || [], 
        links: importedData.links || [] 
      });
      setInsights(importedData.insights || []);
    }
  };

  // Define the Legend component inside UserResearchNetwork.js
  const LegendComponent = () => {
    return (
      <div className="absolute left-4 bottom-20 bg-white p-3 rounded-lg shadow-md border border-gray-200 z-10">
        <h3 className="text-sm font-semibold mb-2">Legend</h3>

        <div className="space-y-2">
          <div className="space-y-1">
            <h4 className="text-xs font-medium text-gray-700">Node Types</h4>
            <div className="flex items-center">
              <div className="w-4 h-4 rounded-full mr-2" style={{ backgroundColor: "#3182CE" }}></div>
              <span className="text-xs">Theme</span>
            </div>
            <div className="flex items-center">
              <div className="w-4 h-4 rounded-full mr-2" style={{ backgroundColor: "#38A169" }}></div>
              <span className="text-xs">Feedback</span>
            </div>
            <div className="flex items-center">
              <div className="w-4 h-4 rounded-full mr-2" style={{ backgroundColor: "#DD6B20" }}></div>
              <span className="text-xs">User Role</span>
            </div>
          </div>

          <div className="space-y-1 pt-2 border-t border-gray-200">
            <h4 className="text-xs font-medium text-gray-700">Feedback Sentiment</h4>
            <div className="flex items-center">
              <div className="w-4 h-4 rounded-full mr-2" style={{ backgroundColor: sentimentColorScale["Positive"] }}></div>
              <span className="text-xs">Positive</span>
            </div>
            <div className="flex items-center">
              <div className="w-4 h-4 rounded-full mr-2" style={{ backgroundColor: sentimentColorScale["Negative"] }}></div>
              <span className="text-xs">Negative</span>
            </div>
            <div className="flex items-center">
              <div className="w-4 h-4 rounded-full mr-2" style={{ backgroundColor: sentimentColorScale["Neutral"] }}></div>
              <span className="text-xs">Neutral</span>
            </div>
          </div>
        </div>
      </div>
    );
  };

  // Load initial data
  useEffect(() => {
    if (!loading && graphData.nodes.length === 0) {
      setGraphData(initialData);
    }
  }, [loading, graphData.nodes.length]);

  // Loading screen
  if (loading) {
    return (
      <div className="flex flex-col h-full items-center justify-center bg-gray-50">
        <div className="text-2xl text-blue-800 mb-4">Loading Insights Network...</div>
        <div className="w-16 h-16 border-4 border-blue-600 border-t-transparent rounded-full animate-spin"></div>
      </div>
    );
  }

  // Error screen
  if (error) {
    return (
      <div className="flex flex-col h-full items-center justify-center bg-gray-50">
        <div className="text-2xl text-red-800 mb-4">Error Loading Data</div>
        <div className="text-gray-600">{error}</div>
        <button 
          className="mt-4 px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
          onClick={() => window.location.reload()}
        >
          Retry
        </button>
      </div>
    );
  }

  return (
    <div className="relative w-full h-full" ref={containerRef}>
      <svg ref={svgRef} className="w-full h-full"></svg>

      {/* Use the inline Legend component */}
      <LegendComponent />

      {/* Zoom controls */}
      <div className="absolute bottom-4 right-4 flex space-x-2">
        <button 
          className="w-8 h-8 flex items-center justify-center bg-gray-100 hover:bg-gray-200 rounded-full"
          onClick={zoomIn}
        >
          <span className="text-xl">+</span>
        </button>
        <button 
          className="w-8 h-8 flex items-center justify-center bg-gray-100 hover:bg-gray-200 rounded-full"
          onClick={zoomOut}
        >
          <span className="text-xl">-</span>
        </button>
        <button 
          className="w-8 h-8 flex items-center justify-center bg-gray-100 hover:bg-gray-200 rounded-full"
          onClick={fitToScreen}
        >
          <span className="text-xl"></span>
        </button>
      </div>

      {/* Rest of your UI components */}
    </div>
  );
};

export default UserResearchNetwork;
