  const suggestConnections = () => {
    if (!selectedNode) return;

    // Find nodes of the same type that have similar connections
    const sameTypeNodes = graphData.nodes.filter(n => 
      n.id !== selectedNode.id && n.type === selectedNode.type
    );

    // Get current connections of the selected node
    const currentConnections = graphData.links
      .filter(l => l.source.id === selectedNode.id || l.source === selectedNode.id 
              || l.target.id === selectedNode.id || l.target === selectedNode.id)
      .map(l => {
        const targetId = l.source.id === selectedNode.id || l.source === selectedNode.id 
          ? (l.target.id || l.target) 
          : (l.source.id || l.source);
        return targetId;
      });

    // Find potential new connections based on what similar nodes are connected to
    const potentialConnections = [];

    sameTypeNodes.forEach(similarNode => {
      // Find connections of this similar node
      graphData.links
        .filter(l => l.source.id === similarNode.id || l.source === similarNode.id 
                || l.target.id === similarNode.id || l.target === similarNode.id)
        .forEach(l => {
          const targetId = l.source.id === similarNode.id || l.source === similarNode.id 
            ? (l.target.id || l.target) 
            : (l.source.id || l.source);

          // If this is a new potential connection
          if (!currentConnections.includes(targetId)) {
            const targetNode = graphData.nodes.find(n => n.id === targetId);
            if (targetNode && targetNode.type !== selectedNode.type) {
              potentialConnections.push(targetNode);
            }
          }
        });
    });

    // Remove duplicates
    const uniquePotentials = Array.from(new Set(potentialConnections.map(n => n.id)))
      .map(id => potentialConnections.find(n => n.id === id));

