  // Update the analyzeMultipleFeedback function to use a mock implementation for testing
  const analyzeMultipleFeedback = async (feedbackItems) => {
    try {
      // For testing purposes, use a mock implementation instead of calling the API
      // This will allow you to test the functionality without API keys
      console.log("Analyzing multiple feedback items:", feedbackItems.length);

      // Create mock themes based on keywords in the feedback
      const themeKeywords = {
        "Performance": ["slow", "speed", "fast", "performance", "loading", "lag"],
        "User Interface": ["ui", "interface", "design", "layout", "look", "feel"],
        "Mobile Experience": ["mobile", "phone", "app", "tablet", "responsive"],
        "Stability": ["crash", "bug", "error", "stable", "reliability"],
        "Features": ["feature", "functionality", "capability", "ability"],
        "Data Management": ["data", "export", "import", "save", "load"]
      };

      // Generate themes based on the feedback content
      const themes = [];
      const themeFrequency = {};

      // First pass - identify themes
      feedbackItems.forEach(item => {
        const text = item.text.toLowerCase();

        for (const [theme, keywords] of Object.entries(themeKeywords)) {
          if (keywords.some(keyword => text.includes(keyword))) {
            if (!themeFrequency[theme]) {
              themeFrequency[theme] = 0;
              themes.push({
                name: theme,
                description: `Feedback related to ${theme.toLowerCase()}`
              });
            }
            themeFrequency[theme]++;
          }
        }
      });

      // If no themes were found, add a default one
      if (themes.length === 0) {
        themes.push({
          name: "General Feedback",
          description: "Miscellaneous user feedback"
        });
      }

      // Second pass - classify feedback
      const classifications = feedbackItems.map((item, index) => {
        const text = item.text.toLowerCase();
        const matchedThemes = [];

        // Find primary and secondary themes
        for (const [theme, keywords] of Object.entries(themeKeywords)) {
          if (keywords.some(keyword => text.includes(keyword))) {
            matchedThemes.push(theme);
          }
        }

        // If no themes matched, use "General Feedback"
        if (matchedThemes.length === 0) {
          matchedThemes.push("General Feedback");
        }

        // Determine sentiment (simple keyword-based approach)
        let sentiment = "neutral";
        const positiveWords = ["good", "great", "excellent", "love", "like", "helpful", "easy"];
        const negativeWords = ["bad", "poor", "terrible", "hate", "difficult", "confusing", "slow"];

        if (positiveWords.some(word => text.includes(word))) {
          sentiment = "positive";
        } else if (negativeWords.some(word => text.includes(word))) {
          sentiment = "negative";
        }

        // Determine priority (1-10)
        let priority = 5; // Default medium priority

        // Increase priority for negative feedback
        if (sentiment === "negative") {
          priority += 2;
        }

        // Increase priority for feedback mentioning critical issues
        const criticalWords = ["crash", "bug", "error", "broken", "unusable", "critical"];
        if (criticalWords.some(word => text.includes(word))) {
          priority += 2;
        }

        // Cap priority between 1-10
        priority = Math.max(1, Math.min(10, priority));

        return {
          feedbackIndex: index,
          themes: matchedThemes,
          sentiment,
          priority
        };
      });

      // Generate theme connections
      const themeConnections = [];
      const processedPairs = new Set();

      themes.forEach(theme1 => {
        themes.forEach(theme2 => {
          if (theme1.name !== theme2.name) {
            const pairKey = [theme1.name, theme2.name].sort().join('-');

            if (!processedPairs.has(pairKey)) {
              processedPairs.add(pairKey);

              // Check if these themes appear together in any feedback
              let connectionStrength = 0;

              classifications.forEach(classification => {
                if (classification.themes.includes(theme1.name) && 
                    classification.themes.includes(theme2.name)) {
                  connectionStrength += 0.2;
                }
              });

              // Only add connections with some strength
              if (connectionStrength > 0) {
                themeConnections.push({
                  source: theme1.name,
                  target: theme2.name,
                  strength: Math.min(1.0, connectionStrength),
                  reason: `These themes often appear together in user feedback`
                });
              }
            }
          }
        });
      });

      return {
        themes,
        classifications,
        themeConnections
      };
    } catch (error) {
      console.error("Error in mock analyzeMultipleFeedback:", error);
      throw error;
    }
  };

