  // Import data from file with more robust processing
  const importData = useCallback(event => {
    const file = event.target.files[0];
    if (!file) return;
    setLoading(true);
    setError(null);
    console.log("Starting import from file:", file.name);
    const reader = new FileReader();
    reader.onload = e => {
      try {
        const fileContent = e.target.result;
        console.log("File content loaded, parsing JSON...");
        const importedData = JSON.parse(fileContent);
        console.log("Successfully parsed JSON:", importedData);

        // Check if it's the simple feedback format (array of feedback items)
        if (importedData.feedback && Array.isArray(importedData.feedback)) {
          console.log(`Found feedback array with ${importedData.feedback.length} items`);

          // Store the feedback items
          setFeedbackItems(importedData.feedback);

          // Process the feedback to create a graph
          console.log("Transforming feedback to graph...");
          processImportedFeedback(importedData.feedback);
        }
        // Check if it's the direct graph format (nodes and links)
        else if (importedData.nodes && importedData.links) {
          console.log(`Found direct graph data: ${importedData.nodes.length} nodes, ${importedData.links.length} links`);

          // Normalize the links and nodes
          const validNodes = importedData.nodes.map(node => ({
            id: node.id || `node-${Date.now()}-${Math.random().toString(36).substring(2, 9)}`,
            name: node.name || 'Unnamed Node',
            type: node.type || 'theme',
            feedbackCount: node.feedbackCount || 0,
            priority: node.priority || 'Medium',
            ...node // preserve other properties
          }));

          // Create a map of node IDs for quick lookups
          const nodeMap = {};
          validNodes.forEach(node => {
            nodeMap[node.id] = true;
          });

          // Normalize and validate links
          const validLinks = importedData.links.map(link => {
            // Extract source and target consistently
            const sourceId = typeof link.source === 'object' ? link.source.id : link.source;
            const targetId = typeof link.target === 'object' ? link.target.id : link.target;

            // Validate source and target
            if (!sourceId || !targetId) {
              console.warn('Link missing source or target:', link);
              return null;
            }
            return {
              source: sourceId,
              target: targetId,
              strength: link.strength || 0.5
            };
          }).filter(link => link !== null && nodeMap[link.source] && nodeMap[link.target]);
          console.log(`Validated ${validLinks.length} links out of ${importedData.links.length}`);

          // Set the graph data
          setGraphData({
            nodes: validNodes,
            links: validLinks
          });
          setLoading(false);
        }
        // Invalid format
        else {
          console.error("Unrecognized data format:", Object.keys(importedData));
          setError("Invalid data format. The file should contain either:" + "\n1. A 'feedback' array with user feedback items, or" + "\n2. 'nodes' and 'links' arrays for direct graph data");
          setLoading(false);
        }
      } catch (err) {
        console.error("Error parsing or processing the file:", err);
        setError(`Error importing file: ${err.message || "Unknown error"}`);
        setLoading(false);
      }
    };
    reader.onerror = err => {
      console.error("FileReader error:", err);
      setError("Error reading the file. Please try again.");
      setLoading(false);
    };
    console.log("Starting file read...");
    reader.readAsText(file);
  }, []);

  // Process imported feedback with enhanced reliability
  const processImportedFeedback = useCallback(feedbackItems => {
    if (!feedbackItems || feedbackItems.length === 0) {
      setLoading(false);
      return;
    }
    console.log(`Processing ${feedbackItems.length} feedback items...`);
    setIsAIProcessing(true);

    // Create a new graph structure
    const newGraph = {
      nodes: [],
      links: []
    };

    // Track created nodes to avoid duplicates
    const nodeMap = {
      personas: {},
      themes: {},
      features: {}
    };

    // Step 1: First create all persona nodes from feedback
    console.log("Creating persona nodes...");
    feedbackItems.forEach(item => {
      if (!item.user_role) return;
      if (!nodeMap.personas[item.user_role]) {
        const personaId = `persona-${Date.now()}-${Object.keys(nodeMap.personas).length}`;
        const personaNode = {
          id: personaId,
          name: item.user_role,
          type: "persona",
          feedbackCount: 1
        };
        newGraph.nodes.push(personaNode);
        nodeMap.personas[item.user_role] = personaNode;
        console.log(`Created persona node: ${item.user_role}`);
      } else {
        // Increment feedback count for existing persona
        nodeMap.personas[item.user_role].feedbackCount += 1;
      }
    });

    // Step 2: Process each feedback to identify themes
    // Use chunks to avoid overwhelming the system
    console.log("Processing feedback to identify themes...");
    const processChunks = async () => {
      const chunkSize = 5; // Process 5 at a time
      const chunks = [];

      // Split feedback into chunks
      for (let i = 0; i < feedbackItems.length; i += chunkSize) {
        chunks.push(feedbackItems.slice(i, i + chunkSize));
      }

      // Process each chunk
      for (let i = 0; i < chunks.length; i++) {
        const chunk = chunks[i];
        console.log(`Processing chunk ${i + 1} of ${chunks.length}...`);

        // Process items in this chunk
        for (const item of chunk) {
          if (!item.text || !item.user_role) continue;
          try {
            // Attempt to use the AI classification service
            console.log(`Classifying: "${item.text.substring(0, 30)}..."`);
            let result;
            try {
              // Try AI service first
              result = await classifyFeedback(item.text);
              console.log("AI classification result:", result);
            } catch (aiError) {
              // If AI fails, use fallback classification
              console.warn("AI classification failed, using fallback:", aiError);
              result = mockClassifyFeedback(item.text);
              console.log("Fallback classification result:", result);
            }
            if (result && result.theme) {
              // Create or update theme node
              if (!nodeMap.themes[result.theme]) {
                const themeId = `theme-${Date.now()}-${Object.keys(nodeMap.themes).length}`;
                const themeNode = {
                  id: themeId,
                  name: result.theme,
                  type: "theme",
                  feedbackCount: 1
                };
                newGraph.nodes.push(themeNode);
                nodeMap.themes[result.theme] = themeNode;
                console.log(`Created theme node: ${result.theme}`);
              } else {
                // Increment existing theme's feedback count
                nodeMap.themes[result.theme].feedbackCount += 1;
              }

              // Connect persona to theme
              const personaNode = nodeMap.personas[item.user_role];
              const themeNode = nodeMap.themes[result.theme];
              if (personaNode && themeNode) {
                // Check if link already exists
                const linkExists = newGraph.links.some(link => link.source === personaNode.id && link.target === themeNode.id || link.source === themeNode.id && link.target === personaNode.id);
                if (!linkExists) {
                  newGraph.links.push({
                    source: personaNode.id,
                    target: themeNode.id,
                    strength: 0.7
                  });
                  console.log(`Connected persona "${personaNode.name}" to theme "${themeNode.name}"`);
                }
              }
            }
          } catch (err) {
            console.error("Error processing feedback item:", err);
          }
        }

        // Update graph data every chunk to show progress
        if (i < chunks.length - 1) {
          setGraphData({
            ...newGraph
          });
          // Small delay to allow UI to update
          await new Promise(resolve => setTimeout(resolve, 100));
        }
      }

      // Step 3: Generate feature suggestions for top themes
      console.log("Generating feature suggestions...");
      const significantThemes = Object.values(nodeMap.themes).filter(theme => theme.feedbackCount >= 2).sort((a, b) => b.feedbackCount - a.feedbackCount).slice(0, 5); // Top 5 themes

      console.log(`Found ${significantThemes.length} significant themes for feature generation`);
      for (const theme of significantThemes) {
        try {
          // Get feedback related to this theme
          const relatedFeedback = feedbackItems.filter(item => {
            const text = item.text.toLowerCase();
            return text.includes(theme.name.toLowerCase());
          }).map(item => item.text);

          // Generate feature ideas
          console.log(`Generating features for theme "${theme.name}" with ${relatedFeedback.length} feedback items`);
          let featureIdeas;
          try {
            // Try AI service first
            featureIdeas = await generateFeatureIdeas(theme.name, relatedFeedback);
            console.log("AI generated feature ideas:", featureIdeas);
          } catch (aiError) {
            // If AI fails, use mock generation
            console.warn("AI feature generation failed, using fallback:", aiError);
            featureIdeas = mockGenerateFeatureIdeas(theme.name, relatedFeedback);
            console.log("Fallback feature ideas:", featureIdeas);
          }
          if (featureIdeas && featureIdeas.length > 0) {
            // Create feature nodes
            for (const idea of featureIdeas) {
              const featureId = `feature-${Date.now()}-${Object.keys(nodeMap.features).length}`;
              const featureNode = {
                id: featureId,
                name: idea.name,
                description: idea.description || "",
                type: "feature",
                priority: idea.priority || "Medium"
              };
              newGraph.nodes.push(featureNode);
              nodeMap.features[featureNode.name] = featureNode;
              console.log(`Created feature: ${featureNode.name}`);

              // Link feature to theme
              newGraph.links.push({
                source: theme.id,
                target: featureId,
                strength: 0.8
              });

              // Find personas connected to this theme
              newGraph.links.forEach(link => {
                const sourceId = typeof link.source === 'object' ? link.source.id : link.source;
                const targetId = typeof link.target === 'object' ? link.target.id : link.target;
                if (sourceId === theme.id || targetId === theme.id) {
                  const personaId = sourceId === theme.id ? targetId : sourceId;
                  const personaNode = newGraph.nodes.find(n => n.id === personaId && n.type === 'persona');
                  if (personaNode) {
                    // Link persona to feature
                    const existingLink = newGraph.links.some(l => l.source === personaId && l.target === featureId || l.source === featureId && l.target === personaId);
                    if (!existingLink) {
                      newGraph.links.push({
                        source: personaId,
                        target: featureId,
                        strength: 0.6
                      });
                      console.log(`Connected persona "${personaNode.name}" to feature "${featureNode.name}"`);
                    }
                  }
                }
              });
            }
          }
        } catch (err) {
          console.error(`Error generating features for theme "${theme.name}":`, err);
        }
      }

      // Ensure we have at least some features if none were generated
      if (newGraph.nodes.filter(n => n.type === 'feature').length === 0) {
        console.log("No features were generated - creating fallback features");

        // Create a fallback feature for each major theme
        for (const theme of significantThemes.slice(0, 3)) {
          const featureId = `feature-${Date.now()}-${Math.random().toString(36).substring(2, 9)}`;
          const featureNode = {
            id: featureId,
            name: `${theme.name} Improvement`,
            type: "feature",
            priority: theme.feedbackCount >= 5 ? "High" : "Medium",
            fallback: true
          };
          newGraph.nodes.push(featureNode);
          console.log(`Created fallback feature: ${featureNode.name}`);

          // Link to theme
          newGraph.links.push({
            source: theme.id,
            target: featureId,
            strength: 0.8
          });
        }
      }

      // Final graph update
      console.log("Finalizing graph...");
      console.log("Final graph has:", newGraph.nodes.length, "nodes and", newGraph.links.length, "links");
      console.log("Nodes by type:", {
        personas: newGraph.nodes.filter(n => n.type === 'persona').length,
        themes: newGraph.nodes.filter(n => n.type === 'theme').length,
        features: newGraph.nodes.filter(n => n.type === 'feature').length
      });
      setGraphData(newGraph);

      // Pre-compute positions for better visualization
      setTimeout(() => {
        if (simulationRef.current) {
          simulationRef.current.stop();

          // Create a temporary simulation to calculate positions
          const tempSimulation = d3.forceSimulation(newGraph.nodes).force("link", d3.forceLink(newGraph.links).id(d => d.id).distance(100)).force("charge", d3.forceManyBody().strength(-300)).force("center", d3.forceCenter(dimensions.width / 2, dimensions.height / 2)).force("collide", d3.forceCollide(40));

          // Run several ticks to get a good initial layout
          for (let i = 0; i < 100; i++) {
            tempSimulation.tick();
          }
          tempSimulation.stop();
        }
        setIsAIProcessing(false);
        setLoading(false);
        console.log("Import processing complete");
      }, 100);
    };

    // Start the processing
    processChunks().catch(err => {
      console.error("Error in processChunks:", err);
      setError("Error processing feedback. Please try again.");
      setIsAIProcessing(false);
      setLoading(false);
    });
  }, [dimensions]);

  // Add a new node (theme or feature)
  const handleAddNode = useCallback(() => {
    if (!newNodeName.trim()) {
      alert("Please enter a node name");
      return;
    }
    let newNode;
    if (modalType === 'theme') {
      newNode = {
        id: `theme-${Date.now()}`,
        name: newNodeName,
        type: "theme",
        feedbackCount: newNodeFeedbackCount
      };
    } else if (modalType === 'feature') {
      newNode = {
        id: `feature-${Date.now()}`,
        name: newNodeName,
        type: "feature",
        priority: newNodePriority
      };
    }
    if (newNode) {
      setGraphData(prevData => ({
        nodes: [...prevData.nodes, newNode],
        links: [...prevData.links]
      }));
      setShowModal(false);
      setNewNodeName('');
      setNewNodeFeedbackCount(10);
      setNewNodePriority('Medium');
    }
  }, [modalType, newNodeName, newNodeFeedbackCount, newNodePriority]);

  // Handle node selection and display node details
  const handleNodeClick = useCallback(node => {
    if (!node) return;

    // Find connected nodes
    const connectedLinks = graphData.links.filter(link => {
      const sourceId = typeof link.source === 'object' ? link.source.id : link.source;
      const targetId = typeof link.target === 'object' ? link.target.id : link.target;
      return sourceId === node.id || targetId === node.id;
    });
    const connectedNodeIds = connectedLinks.map(link => {
      const sourceId = typeof link.source === 'object' ? link.source.id : link.source;
      const targetId = typeof link.target === 'object' ? link.target.id : link.target;
      return sourceId === node.id ? targetId : sourceId;
    });
    const connectedNodes = graphData.nodes.filter(n => connectedNodeIds.includes(n.id));

    // Prepare insights
    let insights = {
      results: []
    };
    if (node.type === "theme") {
      // Theme view
      insights.theme = node.name;
      insights.feedbackCount = node.feedbackCount || 0;

      // Connected features
      const connectedFeatures = connectedNodes.filter(n => n.type === "feature");
      if (connectedFeatures.length > 0) {
        insights.connectedFeatures = connectedFeatures;
      }

      // Connected personas
      const connectedPersonas = connectedNodes.filter(n => n.type === "persona");
      if (connectedPersonas.length > 0) {
        insights.connectedPersonas = connectedPersonas;
      }
    } else if (node.type === "feature") {
      // Feature view
      insights.isFeatureView = true;
      insights.name = node.name;
      insights.priority = node.priority || "Medium";

      // Connected themes
      const connectedThemes = connectedNodes.filter(n => n.type === "theme");
      if (connectedThemes.length > 0) {
        insights.connectedThemes = connectedThemes;
      }

      // Connected personas
      const connectedPersonas = connectedNodes.filter(n => n.type === "persona");
      if (connectedPersonas.length > 0) {
        insights.connectedPersonas = connectedPersonas;
      }
    } else if (node.type === "persona") {
      // Persona view
      insights.persona = node.name;
      insights.feedbackCount = node.feedbackCount || 0;

      // Connected themes
      const connectedThemes = connectedNodes.filter(n => n.type === "theme");
      if (connectedThemes.length > 0) {
        insights.connectedThemes = connectedThemes;
      }

      // Connected features
      const connectedFeatures = connectedNodes.filter(n => n.type === "feature");
      if (connectedFeatures.length > 0) {
        insights.connectedFeatures = connectedFeatures;
      }

      // Find feedback from this persona
      if (feedbackItems.length > 0) {
        const personaFeedback = feedbackItems.filter(item => item.user_role === node.name);
        if (personaFeedback.length > 0) {
          insights.results = personaFeedback.map((item, index) => ({
            feedbackIndex: index,
            text: item.text,
            user_role: item.user_role
          }));
        }
      }
    }

    // Update insights and show panel
    setAIInsights(insights);
    setShowAIInsightsPanel(true);
  }, [graphData, feedbackItems]);

  // Create a connection between nodes
  const handleConnectionSubmit = useCallback(() => {
    if (!connectionSource || !connectionTarget) return;

    // Check if connection already exists
    const existingConnection = graphData.links.some(link => {
      const sourceId = typeof link.source === 'object' ? link.source.id : link.source;
      const targetId = typeof link.target === 'object' ? link.target.id : link.target;
      return sourceId === connectionSource.id && targetId === connectionTarget || sourceId === connectionTarget && targetId === connectionSource.id;
    });
    if (!existingConnection) {
      const newLink = {
        source: connectionSource.id,
        target: connectionTarget,
        strength: connectionStrength
      };
      setGraphData(prevData => ({
        ...prevData,
        links: [...prevData.links, newLink]
      }));
    }
    setShowConnectionModal(false);
    setConnectionSource(null);
    setConnectionTarget('');
    setConnectionStrength(0.5);
  }, [connectionSource, connectionTarget, connectionStrength, graphData]);

  // Initialize connection creation
  const connectNodes = useCallback(() => {
    if (!selectedNode) return;

    // Find suitable targets (different type than selected node)
    const targets = graphData.nodes.filter(n => n.id !== selectedNode.id && n.type !== selectedNode.type);
    if (targets.length === 0) {
      alert("No compatible nodes found to connect with.");
      return;
    }
    setConnectionSource(selectedNode);
    setAvailableTargets(targets);
    setConnectionTarget(targets[0].id);
