        const fallbackClassification = mockClassifyFeedback(newFeedback);
        connectFeedbackToTheme(feedbackId, fallbackClassification.theme);
        setIsAIProcessing(false);
        setAIError("Error processing feedback, but added with basic classification");
      }
    }

    // Reset form fields
    setShowFeedbackModal(false);
    setNewFeedback('');
    setNewFeedbackRole('');
    setSelectedThemeForFeedback('');
  }, [newFeedback, newFeedbackRole, graphData.nodes, selectedThemeForFeedback]);

  // Helper function to connect feedback to a theme
  const connectFeedbackToTheme = useCallback((feedbackId, themeName) => {
    // Check if theme exists
    let themeNode = graphData.nodes.find(node => 
      node.type === "theme" && node.name.toLowerCase() === themeName.toLowerCase()
    );

    // If theme doesn't exist, create it
    if (!themeNode) {
      themeNode = {
        id: `theme-${themeName.replace(/\s+/g, '-').toLowerCase()}`,
        name: themeName,
        type: "theme",
        feedbackCount: 1
      };

      // Add the new theme node
      setGraphData(prevData => ({
        nodes: [...prevData.nodes, themeNode],
        links: [...prevData.links]
      }));
    } else {
      // Update theme's feedback count
      setGraphData(prevData => ({
        ...prevData,
        nodes: prevData.nodes.map(node => 
          node.id === themeNode.id 
            ? { ...node, feedbackCount: (node.feedbackCount || 0) + 1 }
            : node
        )
      }));
    }

    // Add connection between feedback and theme
    setGraphData(prevData => {
      // Create the new link
      const newLink = {
        source: feedbackId,
        target: themeNode.id,
        strength: 0.7
      };

      // Add link only if it doesn't already exist
      const linkExists = prevData.links.some(link => {
        const sourceId = typeof link.source === 'object' ? link.source.id : link.source;
        const targetId = typeof link.target === 'object' ? link.target.id : link.target;
        return (sourceId === feedbackId && targetId === themeNode.id) || 
               (sourceId === themeNode.id && targetId === feedbackId);
      });

      return {
        nodes: prevData.nodes,
        links: linkExists ? prevData.links : [...prevData.links, newLink]
      };
    });

    // If the user role is provided, try to connect to a persona
    const feedbackNode = graphData.nodes.find(n => n.id === feedbackId);
    if (feedbackNode && feedbackNode.user_role) {
      connectFeedbackToPersona(feedbackId, feedbackNode.user_role);
    }

    // Restart the simulation to reposition nodes
    if (simulationRef.current) {
      simulationRef.current.alpha(0.3).restart();
    }

    // Highlight the new feedback momentarily
    setTimeout(() => {
      const svgNode = d3.select(svgRef.current);
      const feedbackCircle = svgNode.selectAll(".nodes circle").filter(d => d.id === feedbackId);

      if (!feedbackCircle.empty()) {
        feedbackCircle
          .transition()
          .duration(500)
          .attr("r", 15)
          .transition()
          .duration(500)
          .attr("r", 10);
      }
    }, 500);
  }, [graphData]);

  // Helper function to connect feedback to a persona
  const connectFeedbackToPersona = useCallback((feedbackId, roleName) => {
    // Skip if no role name
    if (!roleName) return;

    // Check if persona exists
    let personaNode = graphData.nodes.find(node => 
      node.type === "persona" && node.name.toLowerCase() === roleName.toLowerCase()
    );

    // If persona doesn't exist, create it
    if (!personaNode) {
      personaNode = {
        id: `persona-${roleName.replace(/\s+/g, '-').toLowerCase()}`,
        name: roleName,
        type: "persona"
      };

      // Add the new persona node
      setGraphData(prevData => ({
        nodes: [...prevData.nodes, personaNode],
        links: [...prevData.links]
      }));
    }

    // Add connection between feedback and persona
    setGraphData(prevData => {
      // Create the new link
      const newLink = {
        source: feedbackId,
        target: personaNode.id,
        strength: 0.5
      };

      // Add link only if it doesn't already exist
      const linkExists = prevData.links.some(link => {
        const sourceId = typeof link.source === 'object' ? link.source.id : link.source;
        const targetId = typeof link.target === 'object' ? link.target.id : link.target;
        return (sourceId === feedbackId && targetId === personaNode.id) || 
               (sourceId === personaNode.id && targetId === feedbackId);
      });

      return {
        nodes: prevData.nodes,
        links: linkExists ? prevData.links : [...prevData.links, newLink]
      };
    });
  }, [graphData]);

  // Zoom control functions
  const zoomIn = useCallback(() => {
    if (!svgRef.current || !zoomRef.current) return;

    const svg = d3.select(svgRef.current);
    const currentTransform = d3.zoomTransform(svg.node());

    svg.transition()
      .duration(300)
      .call(
        zoomRef.current.transform,
        d3.zoomIdentity
          .translate(currentTransform.x, currentTransform.y)
          .scale(currentTransform.k * 1.3)
      );
  }, []);

  const zoomOut = useCallback(() => {
    if (!svgRef.current || !zoomRef.current) return;

    const svg = d3.select(svgRef.current);
    const currentTransform = d3.zoomTransform(svg.node());

    svg.transition()
      .duration(300)
      .call(
        zoomRef.current.transform,
        d3.zoomIdentity
          .translate(currentTransform.x, currentTransform.y)
          .scale(currentTransform.k / 1.3)
      );
  }, []);

  const centerView = useCallback(() => {
    if (!svgRef.current || !zoomRef.current) return;

    const svg = d3.select(svgRef.current);

    // Calculate the center point
    const centerX = dimensions.width / 2;
    const centerY = dimensions.height / 2;

    // Create a transform that centers the view
    const transform = d3.zoomIdentity
      .translate(centerX, centerY)
      .scale(1)
      .translate(-centerX, -centerY);

    // Apply the transform with a smooth transition
    svg.transition()
      .duration(750)
      .call(zoomRef.current.transform, transform);
  }, [dimensions]);

  const fitToScreen = useCallback(() => {
    if (!svgRef.current || !zoomRef.current || !graphData.nodes.length) return;

    const svg = d3.select(svgRef.current);

    // Find the bounds of all nodes
    let minX = Infinity, minY = Infinity, maxX = -Infinity, maxY = -Infinity;

    graphData.nodes.forEach(node => {
      if (!node.x || !node.y) return;

      if (node.x < minX) minX = node.x;
      if (node.y < minY) minY = node.y;
      if (node.x > maxX) maxX = node.x;
      if (node.y > maxY) maxY = node.y;
    });

    // If we don't have valid bounds, center view instead
    if (!isFinite(minX) || !isFinite(minY) || !isFinite(maxX) || !isFinite(maxY)) {
      centerView();
      return;
    }

    // Add padding
    const padding = 50;
    minX -= padding;
    minY -= padding;
    maxX += padding;
    maxY += padding;

    // Calculate width and height of the content
    const contentWidth = maxX - minX;
    const contentHeight = maxY - minY;

    // Calculate scale to fit the content
    const scale = Math.min(
      dimensions.width / contentWidth,
      dimensions.height / contentHeight,
      2 // Maximum scale factor
    ) * 0.9; // 90% to add some margin

    // Calculate the center of the content
    const centerX = (minX + maxX) / 2;
    const centerY = (minY + maxY) / 2;

    // Create a transform that centers and scales the content
    const transform = d3.zoomIdentity
      .translate(dimensions.width / 2, dimensions.height / 2)
      .scale(scale)
      .translate(-centerX, -centerY);

    // Apply the transform with a smooth transition
    svg.transition()
      .duration(750)
      .call(zoomRef.current.transform, transform);
  }, [dimensions, graphData.nodes, centerView]);

  // Add a toggle function for mini-map
  const toggleMiniMap = useCallback(() => {
    setShowMiniMap(!showMiniMap);
  }, [showMiniMap]);

  const handleDataImported = (importedData) => {
    if (importedData) {
      setGraphData({ 
        nodes: importedData.nodes || [], 
        links: importedData.links || [] 
      });
      setInsights(importedData.insights || []);
    }
  };

  // Add a helper function to connect feedback to theme by ID
  const connectFeedbackToThemeById = useCallback((feedbackId, themeId) => {
    // Find the theme node
    const themeNode = graphData.nodes.find(node => node.id === themeId);
    if (!themeNode) return;

    // Update theme's feedback count
    setGraphData(prevData => ({
      ...prevData,
      nodes: prevData.nodes.map(node => 
        node.id === themeId 
          ? { ...node, feedbackCount: (node.feedbackCount || 0) + 1 }
          : node
      )
    }));

    // Add connection between feedback and theme
    setGraphData(prevData => {
      // Create the new link
      const newLink = {
        source: feedbackId,
        target: themeId,
        strength: 0.7
      };

      // Add link only if it doesn't already exist
      const linkExists = prevData.links.some(link => {
        const sourceId = typeof link.source === 'object' ? link.source.id : link.source;
        const targetId = typeof link.target === 'object' ? link.target.id : link.target;
        return (sourceId === feedbackId && targetId === themeId) || 
               (sourceId === themeId && targetId === feedbackId);
      });

      return {
        nodes: prevData.nodes,
        links: linkExists ? prevData.links : [...prevData.links, newLink]
      };
    });

    // If the user role is provided, try to connect to a persona
    const feedbackNode = graphData.nodes.find(n => n.id === feedbackId);
    if (feedbackNode && feedbackNode.user_role) {
      connectFeedbackToPersona(feedbackId, feedbackNode.user_role);
    }

    // Restart the simulation to reposition nodes
    if (simulationRef.current) {
      simulationRef.current.alpha(0.3).restart();
    }

    // Highlight the new feedback momentarily
    setTimeout(() => {
      const svgNode = d3.select(svgRef.current);
      const feedbackCircle = svgNode.selectAll(".nodes circle").filter(d => d.id === feedbackId);

      if (!feedbackCircle.empty()) {
        feedbackCircle
          .transition()
          .duration(500)
          .attr("r", 15)
          .transition()
          .duration(500)
          .attr("r", 10);
      }
    }, 500);
  }, [graphData, connectFeedbackToPersona]);

  // Loading screen
  if (loading) {
    return (
      <div className="flex flex-col h-full items-center justify-center bg-gray-50">
        <div className="text-2xl text-blue-800 mb-4">Loading Insights Network...</div>
        <div className="w-16 h-16 border-4 border-blue-600 border-t-transparent rounded-full animate-spin"></div>
      </div>
    );
  }

  // Error screen
  if (error) {
    return (
      <div className="flex flex-col h-full items-center justify-center bg-gray-50">
        <div className="text-2xl text-red-800 mb-4">Error Loading Data</div>
        <div className="text-gray-600">{error}</div>
        <button 
          className="mt-4 px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
          onClick={() => window.location.reload()}
        >
          Retry
        </button>
      </div>
    );
  }

  return (
    <div className="flex flex-col h-full">
      {/* Header and controls */}
      <div className="bg-white p-4 shadow-md">
        <div className="flex justify-between items-center mb-4">
          <div>
            <h1 className="text-2xl font-bold text-blue-800">User Research Insights Network</h1>
            <p className="text-gray-600">Visualizing connections between user feedback, features, and personas</p>
          </div>

          {/* Search bar */}
          <div className="relative w-64">
            <input
              type="text"
              placeholder="Search nodes..."
              value={searchTerm}
              onChange={(e) => {
                setSearchTerm(e.target.value);
                if (e.target.value.length >= 2) {
                  const results = graphData.nodes.filter(node => 
                    node.name.toLowerCase().includes(e.target.value.toLowerCase())
                  );
                  setSearchResults(results);
                  setShowSearchResults(true);
                } else {
                  setShowSearchResults(false);
                  setSearchResults([]);
                }
              }}
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
            {showSearchResults && searchResults.length > 0 && (
              <div className="absolute top-full left-0 right-0 mt-1 bg-white border border-gray-300 rounded-md shadow-lg z-20 max-h-60 overflow-y-auto">
                {searchResults.map((result, index) => (
                  <div 
                    key={index}
                    className="px-3 py-2 hover:bg-gray-100 cursor-pointer"
                    onClick={() => {
                      setSelectedNode(result);
                      handleNodeClick(result);
                      setShowSearchResults(false);
                    }}
                  >
                    <div className="flex items-center">
                      <div 
                        className="w-3 h-3 rounded-full mr-2"
                        style={{ backgroundColor: colorScale(result.type) }}
                      ></div>
                      <span>{result.name}</span>
                      <span className="ml-2 text-xs text-gray-500">({result.type})</span>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>

        {/* Controls */}
        <div className="flex flex-wrap gap-2 mb-4">
          <div className="flex items-center space-x-2">
            <span className="text-sm text-gray-600">Data:</span>
            <button 
              className="px-3 py-1 bg-blue-600 text-white rounded hover:bg-blue-700 text-sm"
              onClick={exportData}
            >
              Export
            </button>
            <label className="px-3 py-1 bg-blue-600 text-white rounded hover:bg-blue-700 text-sm cursor-pointer">
              Import
              <input 
                type="file" 
                accept=".json" 
                className="hidden" 
                onChange={importData}
              />
            </label>
            <button 
              className="px-3 py-1 bg-red-600 text-white rounded hover:bg-red-700 text-sm"
              onClick={resetVisualization}
            >
              Reset
            </button>
          </div>

          <div className="flex items-center space-x-2">
            <span className="text-sm text-gray-600">View:</span>
            <button 
              className="px-3 py-1 bg-purple-600 text-white rounded hover:bg-purple-700 text-sm"
              onClick={() => setIsSimulationRunning(!isSimulationRunning)}
            >
              {isSimulationRunning ? 'Pause Simulation' : 'Resume Simulation'}
            </button>
            <button 
              className="px-3 py-1 bg-purple-600 text-white rounded hover:bg-purple-700 text-sm"
              onClick={centerView}
            >
              Center View
            </button>
            <button 
              className="px-4 py-2 bg-purple-600 text-white rounded hover:bg-purple-700"
              onClick={toggleMiniMap}
            >
              {showMiniMap ? "Hide Mini-Map" : "Show Mini-Map"}
            </button>
            <button 
              className="px-3 py-1 bg-blue-600 text-white rounded hover:bg-blue-700 text-sm"
              onClick={fitToScreen}
              title="Fit all nodes to screen"
            >
              Fit to Screen
            </button>
          </div>

          <div className="flex items-center space-x-2">
            <span className="text-sm text-gray-600">Add:</span>
            <button 
              className="px-3 py-1 bg-green-600 text-white rounded hover:bg-green-700 text-sm"
              onClick={() => {
                setModalType('theme');
                setNewNodeName(`Theme ${graphData.nodes.filter(n => n.type === 'theme').length + 1}`);
                setShowModal(true);
              }}
            >
              Add Theme
            </button>
            <button 
              className="px-3 py-1 bg-green-600 text-white rounded hover:bg-green-700 text-sm"
              onClick={() => {
                setModalType('feature');
                setNewNodeName(`Feature ${graphData.nodes.filter(n => n.type === 'feature').length + 1}`);
                setShowModal(true);
              }}
            >
              Add Feature
            </button>
            <button 
              className="px-3 py-1 bg-green-600 text-white rounded hover:bg-green-700 text-sm"
              onClick={() => setShowFeedbackModal(true)}
            >
              Add Feedback
            </button>
          </div>
        </div>

        <div className="flex flex-wrap gap-4">
          <div className="flex items-center space-x-2">
            <span className="text-sm text-gray-600">Connect:</span>
            <button 
              className={`px-3 py-1 rounded text-sm ${
                selectedNode ? 'bg-orange-600 text-white hover:bg-orange-700' : 'bg-gray-300 text-gray-500 cursor-not-allowed'
              }`}
              onClick={connectNodes}
              disabled={!selectedNode}
            >
              Connect Nodes
            </button>
          </div>

          <div className="flex items-center space-x-2">
            <span className="text-sm text-gray-600">Feedback Weight:</span>
            <input 
              type="range" 
              min="1" 
              max="10" 
              value={feedbackWeight}
              onChange={(e) => setFeedbackWeight(parseInt(e.target.value))}
