                        <h3 className="text-lg font-semibold text-gray-700">Theme: {aiInsights.theme}</h3>
                      </div>

                      <div className="mb-4">
                        <div className="text-sm text-gray-500 mb-1">Related Feedback Count</div>
                        <div className="text-2xl font-semibold">{aiInsights.feedbackCount || 0}</div>
                      </div>

                      {aiInsights.connectedPersonas && aiInsights.connectedPersonas.length > 0 && (
                        <div className="mb-4">
                          <div className="text-sm text-gray-500 mb-2">Connected Personas</div>
                          <div className="space-y-1">
                            {aiInsights.connectedPersonas.map((persona, idx) => (
                              <div key={idx} className="flex items-center bg-orange-50 px-3 py-2 rounded">
                                <div className="w-3 h-3 rounded-full mr-2" style={{ backgroundColor: colorScale('persona') }}></div>
                                <span className="text-sm">{persona.name}</span>
                              </div>
                            ))}
                          </div>
                        </div>
                      )}

                      {aiInsights.connectedFeatures && aiInsights.connectedFeatures.length > 0 && (
                        <div className="mb-4">
                          <div className="text-sm text-gray-500 mb-2">Related Features</div>
                          <div className="space-y-2">
                            {aiInsights.connectedFeatures.map((feature, idx) => (
                              <div key={idx} className="bg-green-50 px-3 py-2 rounded border-l-4 border-green-400">
                                <div className="font-medium">{feature.name}</div>
                                <div className="text-xs mt-1">
                                  Priority: <span className={`font-medium ${
                                    feature.priority === 'High' ? 'text-red-600' : 
                                    feature.priority === 'Medium' ? 'text-yellow-600' : 'text-blue-600'
                                  }`}>{feature.priority || 'Medium'}</span>
                                </div>
                              </div>
                            ))}
                          </div>
                        </div>
                      )}
                    </div>
                  )}

                  {/* Feature Node Details */}
                  {aiInsights.isFeatureView && (
                    <div>
                      <div className="flex items-center mb-4">
                        <div className="w-4 h-4 rounded-full mr-2" style={{ backgroundColor: colorScale('feature') }}></div>
                        <h3 className="text-lg font-semibold text-gray-700">Feature: {aiInsights.name}</h3>
                      </div>

                      <div className="mb-4">
                        <div className="inline-block bg-gray-100 px-3 py-1 rounded-full text-sm font-medium">
                          Priority: <span className={`${
                            aiInsights.priority === 'High' ? 'text-red-600' : 
                            aiInsights.priority === 'Medium' ? 'text-yellow-600' : 'text-blue-600'
                          }`}>{aiInsights.priority}</span>
                        </div>
                      </div>

                      {aiInsights.connectedThemes && aiInsights.connectedThemes.length > 0 && (
                        <div className="mb-4">
                          <div className="text-sm text-gray-500 mb-2">Related Themes</div>
                          <div className="space-y-1">
                            {aiInsights.connectedThemes.map((theme, idx) => (
                              <div key={idx} className="flex items-center bg-blue-50 px-3 py-2 rounded">
                                <div className="w-3 h-3 rounded-full mr-2" style={{ backgroundColor: colorScale('theme') }}></div>
                                <span className="text-sm">{theme.name}</span>
                                {theme.feedbackCount && (
                                  <span className="ml-2 text-xs text-gray-500">({theme.feedbackCount} feedback)</span>
                                )}
                              </div>
                            ))}
                          </div>
                        </div>
                      )}

                      {aiInsights.connectedPersonas && aiInsights.connectedPersonas.length > 0 && (
                        <div>
                          <div className="text-sm text-gray-500 mb-2">Related Personas</div>
                          <div className="space-y-1">
                            {aiInsights.connectedPersonas.map((persona, idx) => (
                              <div key={idx} className="flex items-center bg-orange-50 px-3 py-2 rounded">
                                <div className="w-3 h-3 rounded-full mr-2" style={{ backgroundColor: colorScale('persona') }}></div>
                                <span className="text-sm">{persona.name}</span>
                              </div>
                            ))}
                          </div>
                        </div>
                      )}
                    </div>
                  )}

                  {/* Feedback Node Details - Enhanced for complete text */}
                  {aiInsights.isFeedbackView && (
                    <div>
                      <div className="flex items-center mb-4">
                        <div className="w-4 h-4 rounded-full mr-2" style={{ backgroundColor: colorScale('feedback') }}></div>
                        <h3 className="text-lg font-semibold text-gray-700">Feedback Details</h3>
                      </div>

                      <div className="bg-gray-50 p-4 rounded-lg border border-gray-200 mb-4">
                        {/* Complete feedback text with better formatting */}
                        <div className="text-gray-800 mb-4 bg-white p-3 rounded border border-gray-200 max-h-80 overflow-y-auto">
                          <p className="whitespace-pre-wrap text-base leading-relaxed">"{aiInsights.feedbackText}"</p>

                          {/* Debug info for development - comment out in production */}
                          {/* <div className="mt-3 pt-3 border-t border-gray-200 text-xs text-gray-500">
                            <div>Content length: {aiInsights.feedbackText?.length || 0} characters</div>
                          </div> */}
                        </div>

                        <div className="flex justify-between items-center">
                          <div className="flex items-center">
                            <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4 text-gray-500 mr-1" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z" />
                            </svg>
                            <span className="text-sm text-gray-600 font-medium">{aiInsights.user_role || 'Unknown User'}</span>
                          </div>

                          <div className="text-xs text-gray-500">
                            {aiInsights.timestamp ? new Date(aiInsights.timestamp).toLocaleString() : 'No timestamp available'}
                          </div>
                        </div>
                      </div>

                      {/* Sentiment Analysis - If available */}
                      {aiInsights.sentiment && (
                        <div className="mt-4">
                          <div className="text-sm text-gray-500 mb-2">Sentiment Analysis</div>
                          <div className={`px-3 py-2 rounded-full inline-block font-medium text-sm ${
                            aiInsights.sentiment === 'Positive' ? 'bg-green-100 text-green-800' : 
                            aiInsights.sentiment === 'Negative' ? 'bg-red-100 text-red-800' : 
                            'bg-gray-100 text-gray-800'
                          }`}>
                            {aiInsights.sentiment}
                          </div>
                        </div>
                      )}

                      {/* Related Themes - If any */}
                      {aiInsights.relatedThemes && aiInsights.relatedThemes.length > 0 && (
                        <div className="mt-4">
                          <div className="text-sm text-gray-500 mb-2">Related Themes</div>
                          <div className="flex flex-wrap gap-2">
                            {aiInsights.relatedThemes.map((theme, idx) => (
                              <div key={idx} className="bg-blue-50 px-3 py-1 rounded-full text-sm">
                                {theme}
                              </div>
                            ))}
                          </div>
                        </div>
                      )}
                    </div>
                  )}
                </div>

                {/* Existing analytics sections */}
                {analyticsData && (
                  <div className="space-y-6 mb-8">
                    {/* Sentiment Overview Section */}
                    <div>
                      <h3 className="text-lg font-semibold text-gray-700 mb-3 sticky top-14 bg-white z-10 pb-2">Sentiment Overview</h3>
                      <div className="bg-gray-50 p-4 rounded-lg shadow-sm">
                        <div className="flex items-center justify-around mb-2">
                          <div className="text-center">
                            <div className="text-green-500 text-2xl font-bold">
                              {analyticsData.sentimentDistribution?.Positive || 0}
                            </div>
                            <div className="text-xs text-gray-500">Positive</div>
                          </div>
                          <div className="text-center">
                            <div className="text-red-500 text-2xl font-bold">
                              {analyticsData.sentimentDistribution?.Negative || 0}
                            </div>
                            <div className="text-xs text-gray-500">Negative</div>
                          </div>
                          <div className="text-center">
                            <div className="text-gray-500 text-2xl font-bold">
                              {analyticsData.sentimentDistribution?.Neutral || 0}
                            </div>
                            <div className="text-xs text-gray-500">Neutral</div>
                          </div>
                        </div>
                      </div>
                    </div>

                    {/* Theme Analysis Section can go here */}
                  </div>
                )}

                {/* Rest of the panel content */}
                {aiInsights.insights && aiInsights.insights.length > 0 && (
                  <div className="mb-8">
                    <h3 className="text-lg font-semibold text-gray-700 mb-3">Key Insights</h3>
                    <div className="space-y-4">
                      {aiInsights.insights.map(insight => (
                        <div key={insight.id || Math.random()} className="bg-blue-50 p-4 rounded-lg shadow-sm border-l-4 border-blue-500">
                          <h4 className="font-medium text-gray-800 mb-1">{insight.title}</h4>
                          <p className="text-gray-700 text-sm">{insight.description}</p>
                        </div>
                      ))}
                    </div>
                  </div>
                )}
              </div>
            )}
          </div>
        )}
      </div>

      {/* Add Node Modal */}
      {showModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded-lg shadow-xl w-96">
            <h2 className="text-xl font-bold mb-4">
              Add New {modalType === 'theme' ? 'Theme' : 'Feature'}
            </h2>

            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-bold mb-2">
                Name
              </label>
              <input 
                type="text" 
                value={newNodeName}
                onChange={(e) => setNewNodeName(e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                placeholder={`Enter ${modalType} name`}
              />
            </div>

            {modalType === 'theme' && (
              <div className="mb-4">
                <label className="block text-gray-700 text-sm font-bold mb-2">
                  Feedback Count
                </label>
                <input 
                  type="number" 
                  value={newNodeFeedbackCount}
                  onChange={(e) => setNewNodeFeedbackCount(parseInt(e.target.value))}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                  min="0"
                />
              </div>
            )}

            {modalType === 'feature' && (
              <div className="mb-4">
                <label className="block text-gray-700 text-sm font-bold mb-2">
                  Priority
                </label>
                <select 
                  value={newNodePriority}
                  onChange={(e) => setNewNodePriority(e.target.value)}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                >
                  <option value="Low">Low</option>
                  <option value="Medium">Medium</option>
                  <option value="High">High</option>
                </select>
              </div>
            )}

            <div className="flex justify-end space-x-2">
              <button 
                className="px-4 py-2 bg-gray-300 text-gray-700 rounded hover:bg-gray-400"
                onClick={() => {
                  setShowModal(false);
                  setNewNodeName('');
                  setNewNodeFeedbackCount(10);
                  setNewNodePriority('Medium');
                }}
              >
                Cancel
              </button>
              <button 
                className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
                onClick={handleAddNode}
              >
                Add
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Connection Modal */}
      {showConnectionModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded-lg shadow-xl w-96">
            <h2 className="text-xl font-bold mb-4">
              Connect Nodes
            </h2>

            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-bold mb-2">
                From
              </label>
              <div className="px-3 py-2 border border-gray-300 rounded-md bg-gray-50">
                {connectionSource?.name} ({connectionSource?.type})
              </div>
            </div>

            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-bold mb-2">
                To
              </label>
              <select 
                value={connectionTarget}
                onChange={(e) => setConnectionTarget(e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              >
                <option value="">Select a target node</option>
                {availableTargets.map((target) => (
                  <option key={target.id} value={target.id}>
                    {target.name} ({target.type})
                  </option>
                ))}
              </select>
            </div>

            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-bold mb-2">
                Connection Strength: {connectionStrength.toFixed(1)}
              </label>
              <input 
                type="range" 
                min="0.1" 
                max="1" 
                step="0.1"
                value={connectionStrength}
                onChange={(e) => setConnectionStrength(parseFloat(e.target.value))}
                className="w-full"
              />
            </div>

            <div className="flex justify-end space-x-2">
              <button 
                className="px-4 py-2 bg-gray-300 text-gray-700 rounded hover:bg-gray-400"
                onClick={() => {
                  setShowConnectionModal(false);
                  setConnectionSource(null);
                  setConnectionTarget('');
                  setConnectionStrength(0.5);
                }}
              >
                Cancel
              </button>
              <button 
                className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
                onClick={handleConnectionSubmit}
                disabled={!connectionTarget}
              >
                Connect
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Add Feedback Modal */}
      {showFeedbackModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded-lg shadow-xl w-96">
            <h2 className="text-xl font-bold mb-4">
              Add New Feedback
            </h2>

            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-bold mb-2">
                Feedback Text
              </label>
              <textarea 
                value={newFeedback}
                onChange={(e) => setNewFeedback(e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                placeholder="Enter user feedback"
                rows={4}
              />
            </div>

            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-bold mb-2">
                User Role
              </label>
              <input 
                type="text" 
                value={newFeedbackRole}
                onChange={(e) => setNewFeedbackRole(e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                placeholder="e.g. Mobile User, Admin, Power User"
              />
            </div>

            {/* New Theme Selection Dropdown */}
            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-bold mb-2">
                Connect to Theme (Optional)
              </label>
              <select
                value={selectedThemeForFeedback || ""}
                onChange={(e) => setSelectedThemeForFeedback(e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              >
                <option value="">Auto-detect theme (using AI)</option>
                {graphData.nodes
                  .filter(node => node.type === "theme")
                  .map(theme => (
                    <option key={theme.id} value={theme.id}>
                      {theme.name}
                    </option>
                  ))
                }
              </select>
              <p className="text-xs text-gray-500 mt-1">
                Leave empty to use AI for theme detection or select an existing theme
              </p>
            </div>

            <div className="flex justify-end space-x-2">
              <button 
                className="px-4 py-2 bg-gray-300 text-gray-700 rounded hover:bg-gray-400"
                onClick={() => {
                  setShowFeedbackModal(false);
                  setNewFeedback('');
                  setNewFeedbackRole('');
                  setSelectedThemeForFeedback('');
                }}
              >
                Cancel
              </button>
              <button 
                className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
                onClick={handleAddFeedback}
                disabled={isAIProcessing}
              >
                {isAIProcessing ? (
                  <div className="flex items-center">
                    <div className="w-4 h-4 border-2 border-white border-t-transparent rounded-full animate-spin mr-2"></div>
                    Processing...
                  </div>
                ) : 'Submit'}
              </button>
            </div>

            {aiError && (
              <div className="mt-4 p-2 bg-red-100 text-red-700 rounded">
                {aiError}
              </div>
            )}
          </div>
        </div>
      )}

      {/* Display AI Insights in sidebar when available */}
      {insights.length > 0 && !showAIInsightsPanel && (
        <button
          onClick={() => {
            setAIInsights({ results: [], insights: insights });
            setShowAIInsightsPanel(true);
          }}
          className="fixed bottom-8 left-8 bg-blue-600 text-white px-4 py-2 rounded-full shadow-lg hover:bg-blue-700"
        >
          View AI Insights ({insights.length})
        </button>
      )}
    </div>
  );
};

export default UserResearchNetwork;
